SELECT l59.MATRL_DOC_NO AS docno, l60.MATRL_DOC_ITM AS docnoitm, l59.MATRL_DOC_YEAR AS docyear, tt.CODE_NAME AS transtype, l60.DELVRY_NO AS delvryno, l59.REF_DOC_NO AS refdocno, l60.STOCK_TRNSFR_REQST AS reqstno, mt.CODE_NAME AS movetype, DECODE (l60.DEBT_CRDIT_INDICT, 'H', 'Credit', 'S', 'Debit', l60.DEBT_CRDIT_INDICT) AS debitcrdt, l60.MATRL_NO AS itmcode, s26.STK_DESC AS itmdesc, DECODE(l60.DEBT_CRDIT_INDICT, 'H', (l60.QTY * -1), 'S', l60.QTY, l60.QTY) AS qty, ut.CODE_NAME AS uom, (f28.WH_LOC_CODE || ' - ' || f28.WH_LOC_DESC) AS reqststorg, (t28.WH_LOC_CODE || ' - ' || t28.WH_LOC_DESC) AS rcivstorg, TO_CHAR(TO_DATE(l59.USNAM , 'YYYYMMDD') , 'DD/MM/YYYY') AS postingdt, l59.CRT_DT AS crtdt
FROM LOG0059M l59 JOIN LOG0060D l60 ON l59.MATRL_DOC_NO = l60.MATRL_DOC_NO JOIN SYS0026M s26 ON l60.MATRL_NO = s26.STK_CODE LEFT JOIN SYS0028M f28 ON l60.STORG_LOC = f28.WH_LOC_ID LEFT JOIN SYS0028M t28 ON l60.RCIV_ISSU_STORG_LOC = t28.WH_LOC_ID JOIN SYS0013M tt ON l59.TRNSC_TYPE_CODE = tt.CODE AND tt.CODE_MASTER_ID = 306 JOIN SYS0013M mt ON l60.INVNTRY_MOV_TYPE = mt.CODE AND mt.CODE_MASTER_ID = 308 JOIN SYS0013M ut ON l60.MEASURE_BAS_UNIT = ut.CODE_ID AND ut.CODE_MASTER_ID = 42
WHERE l60.AUTO_CRT_ITM = 'M' AND f28.WH_LOC_CODE = #{loccode} AND l60.MATRL_NO = #{itmcode}
AND L60.DOC_POSTNG_DT BETWEEN TO_CHAR(TO_DATE(#{basedt}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{postingdt}, 'DD/MM/YYYY'),'YYYYMMDD')
AND l59.TRNSC_TYPE_CODE IN
#{item}
AND l60.INVNTRY_MOV_TYPE IN
#{item}
ORDER BY l60.DOC_POSTNG_DT ASC

