SELECT SRO_CRT_YYYY FORE_YYYY , SRO_CRT_MM FORE_MM , SRO_CRT_WW FORE_WW , C.WH_LOC_CODE RDC_CODE , C.WH_LOC_DESC RDC_DESC , C.WH_LOC_ID RDC_LOC_ID , D.WH_LOC_CODE CODY_CODE , D.WH_LOC_DESC CODY_DESC , D.WH_LOC_ID CODY_LOC_ID , E.STK_CODE , E.STK_DESC , B.SRO_FORE_QTY , B.SRO_REQ_DEVLI_QTY , B.SRO_CODY_AVA_QTY , B.SRO_CDC_AVA_QTY , A.SRO_REF_SMO_NO , DECODE (A.SRO_STATUS_CD, 'A', 'Active','C', 'Completed') AS SRO_STATUS_CD FROM LOG0117M A JOIN LOG0118D B ON A.SRO_NO = B.SRO_NO JOIN SYS0028M C ON B.SRO_RDC_CODE = C.WH_LOC_CODE JOIN SYS0028M D ON B.SRO_CODY_CODE = D.WH_LOC_CODE JOIN SYS0026M E ON B.SRO_STK_CODE = E.STK_CODE JOIN SYS0005M F ON D.WH_LOC_BRNCH_ID = F.BRNCH_ID WHERE 1=1
AND A.SRO_CRT_WW = #{searchWeek}
AND A.SRO_CDC_CODE = #{searchCDC}
AND #{searchBranch} IN (D.WH_LOC_BRNCH_ID , D.WH_LOC_BRNCH_ID2 , D.WH_LOC_BRNCH_ID3, D.WH_LOC_BRNCH_ID4, D.WH_LOC_BRNCH_ID5)
AND (E.STK_CODE = #{searchMatCode} OR E.STK_DESC LIKE '%' || #{searchMatCode} || '%' )
AND D.WH_LOC_CODE IN
#{item}
AND A.SRO_STATUS_CD IN
#{item}
AND E.STK_TYPE_ID IN
#{item}
AND E.STK_CTGRY_ID IN
#{item}
