#{item}
AND (UPPER(E.STK_DESC) LIKE '%' || UPPER(#{sstocknm}) || '%' OR UPPER(E.STK_CODE) LIKE UPPER(#{sstocknm}) || '%')

SELECT B.WH_LOC_CODE BRANCH_CODE , B.WH_LOC_DESC BRANCH_NAME , A.SAFETY_QTY , C.STK_CODE , C.STK_DESC , C.STK_CTGRY_ID , C.STK_TYPE_ID , D.CODE_NAME CATEGORY , E.CODE_NAME TYPE , A.UPD_DT FROM LOG0119M A LEFT JOIN SYS0028M B ON A.WH_LOC_CODE = B.WH_LOC_CODE LEFT JOIN SYS0026M C ON A.STK_CODE = C.STK_CODE LEFT JOIN SYS0013M D ON C.STK_CTGRY_ID = D.CODE_ID LEFT JOIN SYS0013M E ON C.STK_TYPE_ID = E.CODE_ID WHERE NVL(A.DISAB,0) = 0
AND B.CDC_CODE = #{searchCDC}
AND #{searchBranch} IN (B.WH_LOC_BRNCH_ID , B.WH_LOC_BRNCH_ID2 , B.WH_LOC_BRNCH_ID3, B.WH_LOC_BRNCH_ID4, B.WH_LOC_BRNCH_ID5)
