AND m0056m.ID = #{campaignIdSearch}
AND m0056m.TITLE LIKE '%'||#{titleSearch}||'%'
AND m0056m.MASTER_CODE LIKE '%'||#{masterCodeSearch}||'%'
AND m0056m.PLATFORM IN
#{item}
AND m0056m.MODULE_TYPE IN
#{item}
AND m0056m.STUS_CODE_ID IN
#{item}
AND TO_DATE(TO_CHAR(m0056m.START_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') > = TO_DATE(#{requestStartDate},'DD/MM/YYYY')
AND TO_DATE(TO_CHAR(m0056m.END_DATE, 'DD/MM/YYYY'),'DD/MM/YYYY') <= TO_DATE(#{requestEndDate},'DD/MM/YYYY')

