mapper.select:
SELECT m0057d.DETAIL_ID ,m0057d.VOUCHER_CODE ,m0057d.STUS_CODE_ID ,s0038m.NAME AS STUS_NAME ,m0057d.CUST_EMAIL ,m0057d.ORD_ID ,CASE WHEN (m0057d.USED = 1) THEN 'YES' ELSE 'NO' END AS USED ,CASE WHEN (m0059d.EMAIL_SENT_STUS = 4) THEN 'YES' ELSE 'NO' END AS EMAIL_SENT ,TO_CHAR(m0057d.CRT_DT,'DD/MM/YYYY') AS CRT_DT FROM MSC0057D m0057d JOIN SYS0038M s0038m ON m0057d.STUS_CODE_ID = s0038m.STUS_CODE_ID LEFT JOIN MSC0059D m0059d ON m0057d.MAIL_ID = m0059d.MAIL_ID WHERE 1=1 AND m0057d.ID = #{campaignId}
AND VOUCHER_CODE LIKE '%' || #{voucher} || '%'
ORDER BY DETAIL_ID

SELECT m0056m.ID ,m0056m.MASTER_CODE ,m0056m.TITLE ,m0057d.DETAIL_ID ,m0057d.VOUCHER_CODE ,m0057d.STUS_CODE_ID ,s0038m.NAME AS STUS_NAME ,m0057d.CUST_EMAIL ,m0057d.ORD_ID ,CASE WHEN (m0057d.USED = 1) THEN 'YES' ELSE 'NO' END AS USED ,CASE WHEN (m0059d.EMAIL_SENT_STUS = 4) THEN 'YES' ELSE 'NO' END AS EMAIL_SENT ,TO_CHAR(m0057d.CRT_DT,'DD/MM/YYYY') AS CRT_DT ,m0057d.CUST_NAME ,m0057d.PRODUCT ,m0057d.OBLIGATION ,m0057d.FREE_ITEM ,m0057d.CUST_CONTACT FROM MSC0057D m0057d JOIN MSC0056M m0056m ON m0056m.ID = m0057d.ID JOIN SYS0038M s0038m ON m0057d.STUS_CODE_ID = s0038m.STUS_CODE_ID LEFT JOIN MSC0059D m0059d ON m0057d.MAIL_ID = m0059d.MAIL_ID WHERE 1=1 AND m0057d.ID = #{campaignId}
AND VOUCHER_CODE LIKE '%' || #{voucher} || '%'
ORDER BY DETAIL_ID

{
      call SP_VOUCHER_GENERATE( #{campaignId},#{voucherGenAmount},#{userId},#{p1Stus , mode=OUT , jdbcType=INTEGER , javaType=String , resultMap=resultSp},#{p2Msg , mode=OUT , jdbcType=VARCHAR , javaType=String , resultMap=resultSp} )
}

