mapper.select:
SELECT Project1.CMPLNC_ID CMPLNC_ID , Project1.CMPLNC_NO CMPLNC_NO , Project1.MEM_ID MEM_ID , Project1.MEM_CODE MEM_CODE , Project1.MEM_TYPE MEM_TYPE , Project1.CODE_NAME CODE_NAME , Project1.CMPLNC_STUS_ID CMPLNC_STUS_ID , Project1.NAME NAME , Project1.C1 C1 , Project1.USER_ID USER_ID , Project1.USER_NAME USER_NAME FROM ( SELECT Project1.CMPLNC_ID CMPLNC_ID , Project1.CMPLNC_NO CMPLNC_NO , Project1.MEM_ID MEM_ID , Project1.CMPLNC_STUS_ID CMPLNC_STUS_ID , Project1.MEM_CODE MEM_CODE , Project1.MEM_TYPE MEM_TYPE , Project1.CODE_NAME CODE_NAME , Project1.NAME NAME , Project1.USER_ID USER_ID , Project1.USER_NAME USER_NAME , Project1.C1 C1 , ROW_NUMBER() OVER ( ORDER BY Project1.CMPLNC_ID ASC ) ROW_NUMBER FROM ( SELECT Extent1.CMPLNC_ID CMPLNC_ID , Extent1.CMPLNC_NO CMPLNC_NO , Extent1.MEM_ID MEM_ID , Extent1.CMPLNC_STUS_ID CMPLNC_STUS_ID , Extent3.MEM_CODE MEM_CODE , Extent3.MEM_TYPE MEM_TYPE , Extent4.CODE_NAME CODE_NAME , Extent5.NAME NAME , Extent6.USER_ID USER_ID , Extent6.USER_NAME USER_NAME , GroupBy1.A2 C1 FROM MSC0005D Extent1 JOIN ( SELECT Extent2.CMPLNC_ID K1 , MAX(Extent2.CMPLNC_PERSON_IN_CHRG) A1 , MIN(Extent2.CMPLNC_RCIV_DT) A2 FROM MSC0007D Extent2 GROUP BY Extent2.CMPLNC_ID ) GroupBy1 ON GroupBy1.K1 = Extent1.CMPLNC_ID LEFT JOIN ORG0001D Extent3 ON Extent3.MEM_ID = Extent1.MEM_ID LEFT JOIN SYS0013M Extent4 ON Extent4.CODE_ID = Extent3.MEM_TYPE LEFT JOIN SYS0038M Extent5 ON Extent5.STUS_CODE_ID = Extent1.CMPLNC_STUS_ID LEFT JOIN SYS0047M Extent6 ON Extent6.USER_ID = GroupBy1.A1 WHERE 1=1
AND UPPER(Extent1.CMPLNC_NO) = UPPER(#{caseNo})
AND Extent1.CMPLNC_STUS_ID IN
#{item}
AND UPPER(Extent3.MEM_CODE) = UPPER(#{memCode})
AND Extent3.MEM_TYPE = #{memType}
AND Extent2.CMPLNC_RCIV_DT = to_date(#{recevDt},'DD/MM/YYYY')
AND Extent6.User_ID = #{userId}
) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.CMPLNC_ID ASC

