SELECT T.MEM_ID , T.MEM_CODE , T.NAME , T.NRIC , T.MEM_TYPE , T.CODE_NAME , T.DEPT_CODE , T.GRP_CODE , T.ORG_CODE , T.MEM_LVL , T.LVL3_UP_ID , T.LVL2_UP_ID , T.LVL1_UP_ID FROM ( SELECT T1.MEM_ID , T1.MEM_CODE , T1.MEM_TYPE , T1.NAME , T1.NRIC , T2.CODE_NAME , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.DEPT_CODE ELSE '' END DEPT_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.GRP_CODE ELSE '' END GRP_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.ORG_CODE ELSE '' END ORG_CODE , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.MEM_LVL ELSE 0 END MEM_LVL , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL3_UP_ID ELSE 0 END LVL3_UP_ID , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL2_UP_ID ELSE 0 END LVL2_UP_ID , CASE WHEN (NOT((T3.MEM_ID IS NULL) AND (T3.MEM_CODE IS NULL) AND (T3.MEM_LVL IS NULL) AND (T3.DEPT_CODE IS NULL) AND (T3.GRP_CODE IS NULL) AND (T3.ORG_CODE IS NULL) AND (T3.TOP_ORG_CODE IS NULL) AND (T3.MEM_UP_ID IS NULL) AND (T3.LVL3_UP_ID IS NULL) AND (T3.LVL2_UP_ID IS NULL) AND (T3.LVL1_UP_ID IS NULL) AND (T3.LVL0_UP_ID IS NULL))) THEN T3.LVL1_UP_ID ELSE 0 END LVL1_UP_ID FROM ORG0001D T1 JOIN SYS0013M T2 ON T2.CODE_ID = T1.MEM_TYPE LEFT JOIN (SELECT V1.MEM_ID , V1.MEM_CODE , V1.MEM_LVL , V1.DEPT_CODE , V1.GRP_CODE , V1.ORG_CODE , V1.TOP_ORG_CODE , V1.MEM_UP_ID , V1.LVL3_UP_ID , V1.LVL2_UP_ID , V1.LVL1_UP_ID , V1.LVL0_UP_ID FROM ORG1001V V1 ) T3 ON T3.MEM_ID = T1.MEM_ID WHERE 1 = 1
