mapper.select:
SELECT sm.LOTY_UPLOAD_ID ,sm.LOTY_UPLOAD_STATUS_CODE ,(SELECT CODE_NAME FROM sys0013m WHERE sm.LOTY_UPLOAD_STATUS_CODE =CODE AND CODE_MASTER_ID ='486') AS LOTY_UPLOAD_STATUS_NAME ,(SELECT CODE_NAME FROM sys0013m WHERE sm.LOTY_APPROVAL_STATUS_CODE =CODE AND CODE_MASTER_ID ='488') AS LOTY_APPROVAL_STATUS_NAME ,(SELECT COUNT(*) FROM SAL0301D SS WHERE SS.LOTY_UPLOAD_ID= sm.LOTY_UPLOAD_ID ) AS tot_Cnt ,sm.LOTY_APPROVAL_STATUS_CODE ,sm.LOTY_APPROVER as LOTY_APPROVER_ID ,asy.USER_NAME as LOTY_APPROVER ,sm.LOTY_APPROVER_DATE ,sm.CRT_DT ,sm.CRT_USER_ID ,csy.USER_NAME AS creator FROM SAL0300M sm , SYS0047M csy , SYS0047M asy WHERE 1=1 AND sm.CRT_USER_ID = csy.USER_ID (+) AND sm.LOTY_APPROVER = asy.USER_ID (+)
AND LOTY_UPLOAD_ID = #{uploadId }
AND TO_CHAR(CRT_DT,'YYYYMMDD') BETWEEN #{createDateTo} AND #{createDateFr}
AND csy.USER_NAME LIKE '%' ||UPPER( #{creator} )|| '%'
AND sm.LOTY_UPLOAD_STATUS_CODE IN
#{item}

mapper.update:
UPDATE SAL0300M
SET LOTY_UPLOAD_STATUS_CODE = 10
    ,LOTY_APPROVAL_STATUS_CODE = 'C'
    ,LOTY_APPROVER_DATE =SYSDATE
    ,LOTY_APPROVER =#{userId}
    ,UPD_DT        =SYSDATE
    ,UPD_USER_ID   =#{userId}
WHERE  LOTY_UPLOAD_ID  = #{uploadId}

UPDATE SAL0300M
SET LOTY_UPLOAD_STATUS_CODE = 4
   , LOTY_APPROVAL_STATUS_CODE = 'C'
   , LOTY_APPROVER_DATE =SYSDATE
   , LOTY_APPROVER =#{userId}
   , UPD_DT        =SYSDATE
    ,UPD_USER_ID   =#{userId}
WHERE  LOTY_UPLOAD_ID  = #{uploadId}

DELETE SAL0301D
   WHERE LOTY_UPLOAD_ID = #{lotyUploadId}
   AND LOTY_YEAR =#{lotyYear}
   AND LOTY_PERIOD =#{lotyPeriod}
   AND LOTY_MEMBER_CODE =#{lotyMemberCode}

mapper.insert:
INSERT INTO SAL0301D (
     LOTY_UPLOAD_ID
    ,LOTY_YEAR
    ,LOTY_PERIOD
    ,LOTY_MEMBER_CODE
    ,LOTY_MEMBER_STATUS_CODE
    ,LOTY_VAILD_STATUS_CODE
    ,LOTY_START_DATE
    ,LOTY_END_DATE
    ,CRT_DT
    ,CRT_USER_ID
)VALUES(
        #{lotyUploadId}
        ,#{lotyYear}
        ,#{lotyPeriod}
        ,#{lotyMemberCode}
        ,#{lotyMemberStatusCode}
        , (SELECT count(1)  FROM ORG0001D  WHERE  MEM_CODE =#{lotyMemberCode})
        ,  to_date(#{lotyStartDate},'dd/mm/yyyy')
        ,to_date(#{lotyEndDate},'dd/mm/yyyy')
        ,SYSDATE
        ,#{userId}
)

INSERT INTO SAL0301D (
     LOTY_UPLOAD_ID
    ,LOTY_YEAR
    ,LOTY_PERIOD
    ,LOTY_MEMBER_CODE
    ,LOTY_MEMBER_STATUS_CODE
    ,LOTY_VAILD_STATUS_CODE
    ,LOTY_START_DATE
    ,LOTY_END_DATE
    ,CRT_DT
    ,CRT_USER_ID
)VALUES(
        #{uploadId}
        ,#{year}
        ,#{period}
        , #{itemMemCd}
        ,(SELECT CODE FROM SYS0013M WHERE CODE_MASTER_ID ='487' AND CODE_ID  =#{hpStatus}  )
        , (SELECT count(1)  FROM ORG0001D  WHERE  MEM_CODE =#{itemMemCd})
        ,  to_date(#{requestCreatStart},'dd/mm/yyyy')
        ,to_date(#{requestCreatEnd},'dd/mm/yyyy')
        ,SYSDATE
        ,#{userId}
)
