mapper.select:
SELECT MEM_ID, NAME, MEM_CODE, NRIC, STUS, STATUS_NAME, SAL_ORG_REJOIN_ID, SAL_ORG_REJOIN, APPR_STUS, APPR_STUS_NAME, APPROVED_BY, TRM_REJOIN FROM ( SELECT O01D.MEM_ID, O01D.NAME, O01D.MEM_CODE, O01D.NRIC, O01D.STUS, S38M.NAME AS STATUS_NAME, 0 AS SAL_ORG_REJOIN_ID, '-' AS SAL_ORG_REJOIN, 0 AS APPR_STUS, '-' AS APPR_STUS_NAME, '-' AS APPROVED_BY, O01D.TRM_REJOIN AS TRM_REJOIN FROM ORG0001D O01D LEFT JOIN SYS0038M S38M ON O01D.STUS = S38M.STUS_CODE_ID WHERE 1=1
AND O01D.NRIC LIKE '%'|| TRIM(#{icNum})
AND O01D.MEM_CODE LIKE TRIM(#{code}) ||'%'
AND O01D.NAME = #{name}
AND O01D.STUS = #{currentStatus}
AND O01D.TRM_REJOIN = 1 AND O01D.STUS = 3
UNION SELECT O46D.MEM_ID, O46D.NAME, O46D.MEM_CODE, O46D.NRIC, A.STUS AS STUS, A.STATUS_NAME AS STATUS_NAME, O46D.SAL_ORG_REJOIN AS SAL_ORG_REJOIN_ID, S13M.CODE AS SAL_ORG_REJOIN, O46D.APPR_STUS AS APPR_STUS, SY38M.NAME AS APPR_STUS_NAME, B.USER_FULL_NAME AS APPROVED_BY, A.TRM_REJOIN AS TRM_REJOIN FROM ORG0046D O46D LEFT JOIN SYS0038M SY38M ON O46D.APPR_STUS = SY38M.STUS_CODE_ID LEFT JOIN ( SELECT OR01D.MEM_ID, OR01D.STUS, SYS38M.NAME AS STATUS_NAME, OR01D.TRM_REJOIN AS TRM_REJOIN FROM ORG0001D OR01D LEFT JOIN SYS0038M SYS38M ON OR01D.STUS = SYS38M.STUS_CODE_ID WHERE 1=1
AND OR01D.NRIC LIKE '%'|| TRIM(#{icNum})
AND OR01D.MEM_CODE LIKE TRIM(#{code}) ||'%'
AND OR01D.NAME = #{name}
AND OR01D.STUS = #{currentStatus}
AND OR01D.TRM_REJOIN = 1 AND OR01D.STUS = 3
)A ON A.MEM_ID = O46D.MEM_ID LEFT JOIN ( SELECT S47M.USER_ID, S47M.USER_FULL_NAME FROM SYS0047M S47M WHERE 1=1
AND S47M.USER_FULL_NAME = #{approvedBy}
) B ON O46D.APPR_USER_ID = B.USER_ID LEFT JOIN SYS0013M S13M ON S13M.CODE_ID = O46D.SAL_ORG_REJOIN WHERE 1=1
AND O46D.NRIC LIKE '%'|| TRIM(#{icNum})
AND O46D.MEM_CODE LIKE TRIM(#{code}) ||'%'
AND O46D.NAME = #{name}
AND A.STUS = #{currentStatus}
AND A.TRM_REJOIN = 1 AND A.STUS = 3
AND O46D.SAL_ORG_REJOIN = #{orgToJoin}
AND O46D.APPR_STUS = #{approvalStatus}
AND B.USER_FULL_NAME = #{approvedBy}
) WHERE 1=1
AND NRIC LIKE '%'|| TRIM(#{icNum})
AND MEM_CODE LIKE TRIM(#{code}) ||'%'
AND NAME = #{name}
AND STUS = #{currentStatus}
AND TRM_REJOIN = 1 AND STUS = 3
AND SAL_ORG_REJOIN_ID = #{orgToJoin}
AND APPR_STUS = #{approvalStatus}
AND APPROVED_BY = #{approvedBy}

