mapper.select:
WITH D AS ( SELECT UPLOAD_ID , COUNT(*) TOTAL_UPLOAD , SUM(CASE WHEN VALID_STUS_ID = 4 THEN 1 ELSE 0 END) TOTAL_VALID , SUM(CASE WHEN VALID_STUS_ID <> 4 THEN 1 ELSE 0 END) TOTAL_INVALID FROM ORG0039D GROUP BY UPLOAD_ID ) SELECT t1.UPLOAD_ID UPLOAD_ID, t1.UPLOAD_TYPE_ID UPLOAD_TYPE_ID, t1.STUS_ID AS STUS_ID, D.TOTAL_UPLOAD, D.TOTAL_VALID, D.TOTAL_INVALID, to_char(t1.ACTN_DT,'mm-yyyy') AS ACTN_DT, to_char(t1.CRT_DT,'DD-MM-YYYY HH24:MI:SS') AS CRT_DT, t1.CRT_USER_ID AS CRT_USER_ID, to_char(t1.UPD_DT,'DD-MM-YYYY HH24:MI:SS') AS UPD_DT, t1.UPD_USER_ID AS UPD_USER_ID, t1.MEM_TYPE_ID AS MEM_TYPE_ID, t2.CODE AS CODE, t2.NAME AS NAME, t3.CODE_NAME AS CODE_NAME, t4.CODE_NAME AS CODENAME1, CASE WHEN (t5.USER_ID IS NOT NULL) THEN t5.USER_NAME ELSE 'N' END AS C1, CASE WHEN (t6.USER_ID IS NOT NULL) THEN t6.USER_NAME ELSE 'N' END AS C2 FROM ORG0038D t1 JOIN D ON D.UPLOAD_ID = T1.UPLOAD_ID JOIN SYS0038M t2 ON t2.STUS_CODE_ID = t1.STUS_ID JOIN SYS0013M t3 ON t3.CODE_ID = t1.UPLOAD_TYPE_ID JOIN SYS0013M t4 ON t4.CODE_ID = t1.MEM_TYPE_ID LEFT OUTER JOIN SYS0047M t5 ON t5.USER_ID = t1.CRT_USER_ID LEFT OUTER JOIN SYS0047M t6 ON t6.USER_ID = t1.UPD_USER_ID WHERE 1=1
AND t1.UPLOAD_ID= #{uploadId}
AND t1.STUS_ID IN
#{item}
AND t1.UPLOAD_TYPE_ID IN
#{item}
AND t1.ACTN_DT = to_date(#{actionDate},'mm-yyyy')
AND t1.CRT_DT >= TO_DATE(#{uploadDateFr},'dd/mm/yyyy')
AND t1.CRT_DT < TO_DATE(#{uploadDateTo},'dd/mm/yyyy') + 1
AND t6.USER_NAME = #{creator}
AND t1.MEM_TYPE_ID IN
#{item}
ORDER BY t1.UPLOAD_ID DESC

SELECT
  COUNT(*) CNT
FROM ORG0038D t1
WHERE 1=1
  
      AND t1.STUS_ID = #{statusId}
  
  
      AND t1.UPLOAD_TYPE_ID = #{cmbType}
  
  
      AND t1.MEM_TYPE_ID = #{cmbMemberType}
  
  
      AND t1.UPLOAD_ID = #{uploadId}

mapper.insert:

  SELECT ORG0038D_UPLOAD_ID_SEQ.nextval FROM DUAL

INSERT INTO ORG0038D
    ( UPLOAD_ID
    , UPLOAD_TYPE_ID
    , STUS_ID
    , ACTN_DT
    , CRT_DT
    , CRT_USER_ID
    , UPD_DT
    , UPD_USER_ID
    , MEM_TYPE_ID)
VALUES
    (
          #{seq}
        , #{uploadID}
        , #{statusID}
        , to_date(#{actionDate},'YYYYMM')
        , sysdate
        , #{creator}
        , sysdate
        , #{updator}
        , #{memberTypeID}
    )

INSERT INTO ORG0039D( UPLOAD_DET_ID , UPLOAD_ID , STUS_ID , VALID_STUS_ID , VALID_REM , USER_MEM_CODE , SYS_MEM_ID , UPD_DT , UPD_USER_ID , SYS_MEM_TYPE_ID) ( SELECT ORG0039D_UPLOAD_DET_ID_SEQ.nextval , #{seq} , CASE WHEN O.MEM_ID IS NULL THEN 21 ELSE 1 END , CASE WHEN O.MEM_ID IS NULL THEN 21 ELSE 4 END , CASE WHEN O.MEM_ID IS NULL THEN 'Invalid MemberCode or MemberType Uploaded' END , D.MEMCODE , O.MEM_ID , SYSDATE , #{updator} , O.MEM_TYPE FROM (
SELECT #{item.memberCode} MEMCODE FROM DUAL
) D LEFT JOIN ORG0001D O ON O.MEM_CODE = D.MEMCODE AND MEM_TYPE = #{memberTypeID}
)

mapper.update:
UPDATE ORG0038D
SET
    STUS_ID = #{statusId},
    UPD_DT = sysdate,
    UPD_USER_ID = #{userId}
WHERE UPLOAD_ID = #{uploadId}
