mapper.select:
select b.mem_org_lvl as position_level
       , b.mem_org_desc as position_name
  from sys0013m a,
        ORG0012D b
where a.code_id = b.mem_type_id
and b.mem_type_id = #{memberType}

SELECT row_number, Project1.MEM_ID MemberID, Project1.MEM_TYPE MemberType, Project1.MEM_CODE MemberCode, Project1.CODE_NAME CodeName, Project1.NAME Name, Project1.NRIC NRIC, Project1.status_name, Project1.UPD_DT Updated, Project1.STUS Status, Project1.position_name, Project1.brnch_id, Project1.ct_sub_grp, Project1.C1 C1, project1.TRAINEE_TYPE, project1.TEST_RESULT, project1.approved_by, project1.branch, project1.CRT_DT, project1.ADDRESS, project1.GRP_CODE, project1.PV_YEAR || '/' || TO_CHAR(project1.PV_MONTH, '00') AS LAST_ACT_DT, project1.VACCINE_STATUS, project1.REASON_ID, project1.NEO_PRO_STATUS, project1.HP_TYPE, project1.ATCH_FILE_GRP_ID ATCH_FILE_GRP_ID_DOC, project1.UNIFORM_SIZE, project1.MUSLIMAH_SCARFT, project1.INNER_TYPE, project1.EMRGCY_CNTC_NAME, project1.EMRGCY_CNTC_NO, project1.EMRGCY_CNTC_INIT, project1.TRM_REJOIN, project1.EMAIL FROM ( SELECT Project1.C1 C1, Project1.MEM_ID MEM_ID, Project1.MEM_CODE MEM_CODE, Project1.MEM_TYPE MEM_TYPE, Project1.NAME NAME, Project1.NRIC NRIC, Project1.STUS STUS, TO_CHAR(Project1.UPD_DT, 'DD-MM-YYYY') UPD_DT, Project1.CODE_NAME CODE_NAME, Project1.status_name, Project1.position_name, Project1.brnch_id, Project1.ct_sub_grp, project1.TRAINEE_TYPE, project1.TEST_RESULT, project1.approved_by, project1.branch, TO_CHAR(Project1.CRT_DT, 'DD-MM-YYYY') CRT_DT, project1.ADDRESS, project1.GRP_CODE, project1.PV_MONTH, project1.PV_YEAR, project1.VACCINE_STATUS, project1.REASON_ID, project1.NEO_PRO_STATUS, row_number() OVER ( ORDER BY Project1.MEM_CODE ASC ) AS row_number, project1.HP_TYPE, project1.ATCH_FILE_GRP_ID, project1.UNIFORM_SIZE, project1.MUSLIMAH_SCARFT, project1.INNER_TYPE, project1.EMRGCY_CNTC_NAME, project1.EMRGCY_CNTC_NO, project1.EMRGCY_CNTC_INIT, project1.TRM_REJOIN, project1.EMAIL FROM ( SELECT DISTINCT GroupBy1.A1 C1, Extent1.MEM_ID MEM_ID, Extent1.MEM_CODE MEM_CODE, Extent1.MEM_TYPE MEM_TYPE, Extent1.NAME NAME, Extent1.NRIC NRIC, Extent1.STUS STUS, Extent1.UPD_DT UPD_DT, Extent2.CODE_NAME CODE_NAME, Extent3.NAME as status_name, Extent1.brnch as brnch_id, Extent5.ct_sub_grp, extent1.TRAINEE_TYPE, Extend6.MEM_ORG_DESC position_name, CASE WHEN (Extend8.COURS_TEST_RESULT = 'P') THEN 'Pass' WHEN (Extend8.COURS_TEST_RESULT = 'F') THEN 'Fail' WHEN (Extend8.COURS_TEST_RESULT = 'AB') THEN 'Absent' ELSE '' END TEST_RESULT, Extent9.USER_NAME as approved_by, Extent10.CODE as branch, Extent1.CRT_DT, CASE WHEN (Extent1.AREA_ID IS NOT NULL OR Extent1.ADDR_DTL IS NOT NULL) THEN (Extent1.ADDR_DTL || ' ' || CASE WHEN Extent1.STREET IS NOT NULL THEN Extent1.STREET || ', ' ELSE '' END || CASE WHEN Extent11.AREA IS NOT NULL THEN Extent11.AREA || ', ' ELSE '' END || CASE WHEN Extent11.POSTCODE IS NOT NULL THEN Extent11.POSTCODE || ', ' ELSE '' END || CASE WHEN Extent11.CITY IS NOT NULL THEN Extent11.CITY || ', ' ELSE '' END || CASE WHEN Extent11.STATE IS NOT NULL THEN Extent11.STATE || ', ' ELSE '' END || Extent11.COUNTRY) ELSE Extent1.ADDR1 || ', ' || Extent1.ADDR2 || ', ' || (CASE WHEN Extent1.ADDR3 IS NULL THEN '' ELSE Extent1.ADDR3 || ', ' END) || (CASE WHEN Extent1.ADDR4 IS NULL THEN '' ELSE Extent1.ADDR4 || ', ' END) || Extent1.POSTCODE || ' ' ||Extent15.AREA_NAME || ', ' || Extent14.NAME || ', ' || Extent13.NAME END AS ADDRESS, Extent12.GRP_CODE, B.PV_MONTH, B.PV_YEAR, /*vaccination information */ V.VACCINE_STATUS, V.REASON_ID, CASE WHEN Extent16.HP_CODE is not null THEN 'Yes' ELSE 'No' END AS NEO_PRO_STATUS, extent17.code_name HP_TYPE, Extent1.ATCH_FILE_GRP_ID, Extent1.UNIFORM_SIZE, Extent1.MUSLIMAH_SCARFT, Extent1.INNER_TYPE, Extent1.EMRGCY_CNTC_NAME, Extent1.EMRGCY_CNTC_NO, Extent1.EMRGCY_CNTC_INIT, Extent1.TRM_REJOIN, Extent1.EMAIL FROM ORG0001D Extent1 /*Member eg.get mem_type eg HP, CODY, CT*/ INNER JOIN SYS0013M Extent2 ON Extent1.MEM_TYPE = Extent2.CODE_ID /*CodeDetail eg.HP, CODY, CT full desc*/ INNER JOIN SYS0038M Extent3 ON Extent1.STUS = Extent3.STUS_CODE_ID /*StatusCode eg.active, not active*/ LEFT JOIN ORG0005D Extent5 ON Extent5.MEM_ID = Extent1.MEM_ID /*MemberOrganization eg.get mem_LVL*/ LEFT JOIN ORG0012D Extend6 ON Extend6.MEM_TYPE_ID = Extent1.MEM_TYPE and Extend6.MEM_ORG_LVL = Extent5.MEM_LVL /* MemberOrganizationDesc eg get full desc of mem_type and mem_LVL*/ LEFT JOIN SYS0047M Extent9 ON Extent1.CRT_USER_ID = Extent9.USER_ID LEFT JOIN SYS0005M Extent10 ON Extent1.BRNCH = Extent10.BRNCH_ID LEFT JOIN SYS0064M Extent11 ON Extent1.AREA_ID = Extent11.AREA_ID LEFT JOIN ORG1001V Extent12 ON Extent1.MEM_ID = Extent12.MEM_ID LEFT JOIN SYS0015M Extent13 ON Extent1.COUNTRY = Extent13.CNTY_ID LEFT JOIN SYS0035M Extent14 ON Extent1.STATE = Extent14.STATE_ID LEFT JOIN SYS0039M Extent15 ON Extent1.STATE = Extent15.AREA_STATE_ID AND Extent1.AREA = Extent15.AREA_ID LEFT JOIN ORG0040D V ON V.MEM_ID = Extent1.MEM_ID /* Vaccination info */ LEFT JOIN ORG0011D Extent16 ON Extent1.MEM_CODE = Extent16.HP_CODE AND PV_YEAR = TO_CHAR(SYSDATE, 'YYYY') AND PV_MONTH = TO_CHAR(SYSDATE, 'MM') AND NVL(IS_NW,0)=0 LEFT JOIN SYS0013M Extent17 ON Extent1.rank = Extent17.code_id LEFT JOIN ( SELECT * FROM ( SELECT B.*, ROW_NUMBER() OVER ( PARTITION BY B.MEM_ID ORDER BY B.SALES_ORD_ID DESC ) ITM_ROWS FROM SAL0001D B JOIN ORG1001V EXTENT ON B.MEM_ID = EXTENT.MEM_ID WHERE B.PV_YEAR <> 0 AND B.PV_MONTH <> 0 AND B.STUS_CODE_ID = 4
AND EXTENT.ORG_CODE = #{orgCode}
AND EXTENT.GRP_CODE = #{grpCode}
AND EXTENT.DEPT_CODE = #{deptCode}
AND EXTENT.DEPT_CODE = #{deptCode}
) WHERE ITM_ROWS = 1 ) B ON Extent1.MEM_ID = B.MEM_ID CROSS JOIN ( SELECT COUNT( 1) A1 FROM ORG0001D Extent4, ORG0005D Extent7 WHERE 1=1 AND Extent4.MEM_ID = Extent7.MEM_ID
AND Extent4.MEM_TYPE = #{memTypeCom}
AND Extent4.NAME in ( select /*+precompute_subquery*/ NAME from ORG0001D where UPPER(name) LIKE UPPER('%' || #{name} || '%'))
AND Extent4.MEM_CODE in ( select /*+precompute_subquery*/ mem_code from ORG0001D where UPPER(mem_code) LIKE UPPER('%' || #{code} || '%'))
AND Extent4.NRIC LIKE '%' || #{icNum} || '%'
AND Extent4.DOB = TO_DATE(#{birth}, 'DD/MM/YYYY')
AND Extent4.NATION = #{nation}
AND Extent4.RACE = #{race}
AND Extent4.STUS = 3 AND Extent4.TRM_REJOIN = 1
AND Extent4.STUS = #{status}
AND Extent4.CRT_USER_ID = #{keyUser}
AND Extent4.BRNCH = #{keyBranch}
AND TO_DATE(Extent4.CRT_DT) between TO_DATE(#{createDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND ( ( Extent4.TEL_HUSE LIKE '%' || #{contact}|| '%' ) OR ( Extent4.TEL_OFFICE LIKE '%' || #{contact}|| '%' ) OR ( Extent4.TEL_MOBILE LIKE '%' || #{contact}|| '%' ) ) AND ( Extent4.TEL_MOBILE = #{contact} OR Extent4.TEL_HUSE = #{contact} OR Extent4.TEL_OFFICE = #{contact})
AND UPPER(Extent4.SPONS_CODE) LIKE UPPER('%' || #{sponsor} || '%')
AND Extent7.MEM_LVL= #{position}
AND Extent7.LAST_ORG_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
AND Extent7.LAST_GRP_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE =#{userName} )
AND Extent7.LAST_DEPT_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
AND Extent4.MEM_CODE = #{userName}
) GroupBy1 LEFT JOIN ( SELECT * FROM ( SELECT A.*, ROW_NUMBER() OVER ( PARTITION BY COURS_MEM_ID ORDER BY COURS_ITM_ID DESC ) ITM_ROWS FROM MSC0009D A left join MSC0008d b on b.cours_id = a.cours_id WHERE COURS_MEM_STUS_ID <> 8 AND b.cours_stus_id not in (3,8) ) WHERE ITM_ROWS = 1 ) Extend8 ON Extent1.MEM_ID = Extend8.COURS_MEM_ID WHERE 1=1
AND Extent1.MEM_TYPE = #{memTypeCom}
AND Extent1.MEM_CODE in ( select /*+precompute_subquery*/ mem_code from ORG0001D where UPPER(mem_code) LIKE UPPER('%' || #{code} || '%'))
AND Extent1.NAME in ( select /*+precompute_subquery*/ NAME from ORG0001D where UPPER(name) LIKE UPPER('%' || #{name} || '%'))
AND Extent1.NRIC LIKE '%' || #{icNum} || '%'
AND Extent1.DOB = TO_DATE(#{birth}, 'DD/MM/YYYY')
AND Extent1.NATION = #{nation}
AND Extent1.RACE = #{race}
AND Extent1.STUS = 3 AND Extent1.TRM_REJOIN = 1
AND Extent1.STUS = #{status}
AND Extent1.CRT_USER_ID = #{keyUser}
AND TO_NUMBER(Extent1.BRNCH,99999.99) = #{keyBranch}
AND TO_DATE(Extent1.CRT_DT) between TO_DATE(#{createDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND ((Extent1.TEL_HUSE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.TEL_OFFICE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.TEL_MOBILE LIKE '%' || #{contact}|| '%' ) ) AND ( Extent1.TEL_MOBILE = #{contact} OR Extent1.TEL_HUSE = #{contact} OR Extent1.TEL_OFFICE = #{contact})
AND UPPER(Extent1.SPONS_CODE) LIKE UPPER('%' || #{sponsor} || '%')
AND Extent5.MEM_LVL= #{position}
AND EXTENT12.ORG_CODE = #{orgCode}
AND EXTENT12.GRP_CODE = #{grpCode}
AND EXTENT12.DEPT_CODE = #{deptCode}
AND extent12.ORG_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
AND extent12.GRP_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE =#{userName} )
AND extent12.DEPT_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
/* here */ AND Extent1.MEM_CODE = #{userName}
) Project1 ) Project1 WHERE Project1.row_number > 0 ORDER BY Project1.MEM_CODE ASC

WITH LAST_ACT AS ( SELECT MAX(PV_YEAR || '/' || TO_CHAR(PV_MONTH, '00')) LAST_ACT_DT, MEM_ID FROM SAL0001D WHERE PV_YEAR <> 0 AND PV_MONTH <> 0 AND STUS_CODE_ID = 4 GROUP BY MEM_ID ), TEST_RESULT_TABLE AS (
SELECT COURS_MEM_ID, MAX(COURS_ITM_ID) COURS_ITM_ID FROM MSC0009D A JOIN MSC0008d b on b.cours_id = a.cours_id WHERE COURS_MEM_STUS_ID <> 8 AND b.cours_stus_id not in (3,8) GROUP BY COURS_MEM_ID
)
SELECT memberid, membercode, membertype, name, nric, status, updated, codename, status_name, brnch_id, ct_sub_grp, trainee_type, position_name, test_result, approved_by, branch, crt_dt, address, grp_code, last_act_dt, vaccine_status, reason_id, neo_pro_status, hp_type, atch_file_grp_id, uniform_size, muslimah_scarft, inner_type, emrgcy_cntc_name, emrgcy_cntc_no, emrgcy_cntc_init, trm_rejoin, email, join_dt
FROM ( SELECT Extent1.MEM_ID MemberID, Extent1.MEM_CODE MemberCode, Extent1.MEM_TYPE MemberType, Extent1.NAME Name, Extent1.NRIC NRIC, Extent1.STUS Status, TO_CHAR(Extent1.UPD_DT,'DD-MM-YYYY') Updated, Extent2.CODE_NAME CodeName, Extent3.NAME as status_name, Extent1.brnch as brnch_id, Extent5.ct_sub_grp, extent1.TRAINEE_TYPE, Extend6.MEM_ORG_DESC position_name, CASE x.COURS_TEST_RESULT WHEN 'P' THEN 'Pass' WHEN 'F' THEN 'Fail' WHEN 'AB' THEN 'Absent' ELSE '' END TEST_RESULT, -- ( SELECT TEST_RESULT FROM TEST_RESULT_TABLE WHERE COURS_MEM_ID = EXTENT1.MEM_ID AND ROWNUM <= 1 ) TEST_RESULT, Extent9.USER_NAME as approved_by, Extent10.CODE as branch, TO_CHAR(Extent1.CRT_DT, 'DD-MM-YYYY') CRT_DT, CASE WHEN (Extent1.AREA_ID IS NOT NULL OR Extent1.ADDR_DTL IS NOT NULL) THEN (Extent1.ADDR_DTL || ' ' || CASE WHEN Extent1.STREET IS NOT NULL THEN Extent1.STREET || ', ' ELSE '' END || CASE WHEN Extent11.AREA IS NOT NULL THEN Extent11.AREA || ', ' ELSE '' END || CASE WHEN Extent11.POSTCODE IS NOT NULL THEN Extent11.POSTCODE || ', ' ELSE '' END || CASE WHEN Extent11.CITY IS NOT NULL THEN Extent11.CITY || ', ' ELSE '' END || CASE WHEN Extent11.STATE IS NOT NULL THEN Extent11.STATE || ', ' ELSE '' END || Extent11.COUNTRY) ELSE Extent1.ADDR1 || ', ' || Extent1.ADDR2 || ', ' || (CASE WHEN Extent1.ADDR3 IS NULL THEN '' ELSE Extent1.ADDR3 || ', ' END) || (CASE WHEN Extent1.ADDR4 IS NULL THEN '' ELSE Extent1.ADDR4 || ', ' END) || Extent1.POSTCODE || ' ' ||Extent15.AREA_NAME || ', ' || Extent14.NAME || ', ' || Extent13.NAME END AS ADDRESS, Extent12.GRP_CODE, ( SELECT LAST_ACT_DT FROM LAST_ACT WHERE MEM_ID = EXTENT1.MEM_ID) LAST_ACT_DT, /*vaccination information */ V.VACCINE_STATUS, V.REASON_ID, DECODE(Extent16.HP_CODE,NULL,'No','Yes') NEO_PRO_STATUS, extent17.code_name HP_TYPE, Extent1.ATCH_FILE_GRP_ID, Extent1.UNIFORM_SIZE, Extent1.MUSLIMAH_SCARFT, Extent1.INNER_TYPE, Extent1.EMRGCY_CNTC_NAME, Extent1.EMRGCY_CNTC_NO, Extent1.EMRGCY_CNTC_INIT, Extent1.TRM_REJOIN, Extent1.EMAIL, TO_CHAR(Extent1.JOIN_DT,'DD-MM-YYYY') JOIN_DT, RANK() OVER( ORDER BY extent1.mem_code ) rank_no
FROM ORG0001D Extent1 JOIN SYS0013M Extent2 ON Extent1.MEM_TYPE = Extent2.CODE_ID JOIN SYS0038M Extent3 ON Extent1.STUS = Extent3.STUS_CODE_ID LEFT JOIN ORG0005D Extent5 ON Extent5.MEM_ID = Extent1.MEM_ID LEFT JOIN ORG0012D Extend6 ON Extend6.MEM_TYPE_ID = Extent1.MEM_TYPE and Extend6.MEM_ORG_LVL = Extent5.MEM_LVL LEFT JOIN SYS0047M Extent9 ON Extent1.CRT_USER_ID = Extent9.USER_ID LEFT JOIN SYS0005M Extent10 ON Extent1.BRNCH = Extent10.BRNCH_ID LEFT JOIN SYS0064M Extent11 ON Extent1.AREA_ID = Extent11.AREA_ID LEFT JOIN ORG1001V Extent12 ON Extent1.MEM_ID = Extent12.MEM_ID LEFT JOIN SYS0015M Extent13 ON Extent1.COUNTRY = Extent13.CNTY_ID LEFT JOIN SYS0035M Extent14 ON Extent1.STATE = Extent14.STATE_ID LEFT JOIN SYS0039M Extent15 ON Extent1.STATE = Extent15.AREA_STATE_ID AND Extent1.AREA = Extent15.AREA_ID LEFT JOIN ORG0040D V ON V.MEM_ID = Extent1.MEM_ID /* Vaccination info */ LEFT JOIN ORG0011D Extent16 ON Extent1.MEM_CODE = Extent16.HP_CODE AND PV_YEAR = TO_CHAR(SYSDATE, 'YYYY') AND PV_MONTH = TO_CHAR(SYSDATE, 'MM') AND NVL(IS_NW,0)=0 LEFT JOIN SYS0013M Extent17 ON Extent1.rank = Extent17.code_id LEFT JOIN TEST_RESULT_TABLE C ON C.COURS_MEM_ID = Extent1.MEM_ID LEFT JOIN MSC0009D x ON x.cours_itm_id = c.cours_itm_id
WHERE 1=1
AND Extent1.MEM_TYPE = #{memTypeCom}
AND Extent1.MEM_CODE LIKE '%' || #{code} || '%'
AND Extent1.NAME LIKE UPPER('%' || #{name} || '%')
AND Extent1.NRIC LIKE '%' || #{icNum} || '%'
AND Extent1.DOB = TO_DATE(#{birth}, 'DD/MM/YYYY')
AND Extent1.NATION = #{nation}
AND Extent1.RACE = #{race}
AND Extent1.STUS = 3 AND Extent1.TRM_REJOIN = 1
AND Extent1.STUS = #{status}
AND Extent1.CRT_USER_ID = #{keyUser}
AND TO_NUMBER(Extent1.BRNCH,99999.99) = #{keyBranch}
AND TO_DATE(Extent1.CRT_DT) between TO_DATE(#{createDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND ((Extent1.TEL_HUSE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.TEL_OFFICE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.TEL_MOBILE LIKE '%' || #{contact}|| '%' ) ) AND ( Extent1.TEL_MOBILE = #{contact} OR Extent1.TEL_HUSE = #{contact} OR Extent1.TEL_OFFICE = #{contact})
AND UPPER(Extent1.SPONS_CODE) LIKE UPPER('%' || #{sponsor} || '%')
AND Extent5.MEM_LVL= #{position}
AND EXTENT12.ORG_CODE = #{orgCode}
AND EXTENT12.GRP_CODE = #{grpCode}
AND EXTENT12.DEPT_CODE = #{deptCode}
AND LOWER(EXTENT1.EMAIL) = LOWER(#{email})
) ORDER BY rank_no ASC

SELECT row_number, Project1.MEM_ID MemberID, Project1.MEM_TYPE MemberType, Project1.MEM_CODE MemberCode, Project1.CODE_NAME CodeName, Project1.NAME Name, Project1.NRIC NRIC, Project1.status_name, Project1.UPD_DT Updated, Project1.STUS Status, Project1.position_name, Project1.brnch_id, Project1.ct_sub_grp, Project1.C1 C1, Project1.trm_rejoin FROM ( SELECT Project1.C1 C1, Project1.MEM_ID MEM_ID, Project1.MEM_CODE MEM_CODE, Project1.MEM_TYPE MEM_TYPE, Project1.NAME NAME, Project1.NRIC NRIC, Project1.STUS STUS, TO_CHAR(Project1.UPD_DT,'DD-MM-YYYY') UPD_DT, Project1.CODE_NAME CODE_NAME, Project1.status_name, Project1.position_name, Project1.brnch_id, Project1.ct_sub_grp, Project1.trm_rejoin, row_number() OVER (ORDER BY Project1.MEM_CODE ASC) AS row_number FROM ( SELECT '' C1, Extent1.APLCTN_ID MEM_ID, Extent1.APLICNT_CODE MEM_CODE, Extent1.APLICNT_TYPE MEM_TYPE, Extent1.APLICNT_NAME NAME, Extent1.APLICNT_NRIC NRIC, Extent1.STUS_ID STUS, Extent1.UPD_DT UPD_DT, Extent2.CODE_NAME CODE_NAME, Extent3.NAME as status_name, '' as brnch_id, '' ct_sub_grp, '' position_name, Extent4.LAST_GRP_CODE GRP_CODE, Extent4.LAST_ORG_CODE ORG_CODE, '' trm_rejoin FROM ORG0003D Extent1 /*Member eg.get mem_type eg HP, CODY, CT*/ INNER JOIN SYS0013M Extent2 ON Extent1.APLICNT_type = Extent2.CODE_ID /*CodeDetail eg.HP, CODY, CT full desc*/ INNER JOIN SYS0038M Extent3 ON Extent1.STUS_ID = Extent3.STUS_CODE_ID /*StatusCode eg.active, not active*/ INNER JOIN ORG0005D Extent4 ON Extent1.DEPT_CODE = Extent4.DEPT_CODE AND Extent4.MEM_LVL = '3'
WHERE 1=1 AND Extent1.APLICNT_TYPE IN ('1', '2803')
AND UPPER(Extent1.APLICNT_CODE) LIKE UPPER('%' || #{code} || '%')
AND UPPER(Extent1.APLICNT_NAME) LIKE UPPER('%' || #{name} || '%')
AND Extent1.APLICNT_NRIC LIKE '%' || #{icNum} || '%'
AND Extent1.APLICNT_DOB = TO_DATE(#{birth}, 'DD/MM/YYYY')
AND Extent1.APLICNT_NATION = #{nation}
AND Extent1.APLICNT_RACE = #{race}
AND Extent1.STUS_ID = 0
AND Extent1.STUS_ID = #{status}
AND Extent1.CRT_USER_ID = #{keyUser}
AND TO_DATE(TO_CHAR(Extent1.CRT_DT,'YYYY-MM-DD'),'YYYY-MM-DD') >= TO_DATE(#{createDate}, 'DD/MM/YYYY')
AND TO_DATE(TO_CHAR(Extent1.CRT_DT,'YYYY-MM-DD'),'YYYY-MM-DD') <= TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND Extent1.CRT_DT between TO_DATE(#{createDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND ((Extent1.APLICNT_TEL_HUSE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.APLICNT_TEL_OFFICE LIKE '%' || #{contact}|| '%' ) OR ( Extent1.APLICNT_TEL_MOBILE LIKE '%' || #{contact}|| '%' ) ) AND ( Extent1.APLICNT_TEL_MOBILE = #{contact} OR Extent1.APLICNT_TEL_HUSE = #{contact} OR Extent1.APLICNT_TEL_OFFICE = #{contact})
AND UPPER(Extent1.APLICNT_SPONS_CODE) LIKE UPPER('%' || #{sponsor} || '%')
AND Extent1.DEPT_CODE = #{deptCodeHd}
AND Extent4.LAST_GRP_CODE = #{grpCodeHd}
AND Extent4.LAST_ORG_CODE = #{orgCodeHd}
) Project1 ) Project1 WHERE Project1.row_number > 0 ORDER BY Project1.MEM_CODE ASC

