SELECT Distinct1.C1 C1 , Distinct1.DEPT_CODE CODE_ID , Distinct1.C2 CODE_NAME FROM ( SELECT DISTINCT Extent1.DEPT_CODE DEPT_CODE , 1 C1 , Extent1.DEPT_CODE || ' - ' || Extent2.NAME C2 FROM ORG0005D Extent1 JOIN ORG0001D Extent2 ON Extent1.MEM_ID = Extent2.MEM_ID WHERE ( Extent1.MEM_LVL = #{memberLvl} ) AND (Extent1.BRNCH_ID = #{branchVal}) AND ( Extent1.DEPT_CODE LIKE #{flag}
OR Extent1.DEPT_CODE LIKE #{flag2}
) AND ( 1 = Extent1.ORG_STUS_CODE_ID ) ) Distinct1 ORDER BY Distinct1.C2

