mapper.select:
SELECT * FROM ( SELECT MEM_CODE, POSITION_NAME, MEMBER_CODE_HISTORY, NAME, GMCODE, SMCODE, HMCODE, MOTHER_MANAGER_CODE, MOTHER_MANAGER_NAME, MEMBER_ID, SPONS_CODE, PROMOTION_DATE, CASE WHEN ( SELECT PROMO_TYPE_ID FROM ORG0007D WHERE PROMO_ID = ( SELECT MAX(PROMO_ID) FROM ORG0007D o WHERE o.MEM_ID = T.MEM_ID ) ) IN ('747','749') THEN '-' ELSE DISCONTINUE_DATE END AS DISCONTINUE_DATE, MEM_LVL, ORG_CODE, GRP_CODE, DEPT_CODE, ROW_NUMBER() OVER( PARTITION BY MEM_ID ORDER BY PROMO_ID DESC ), CASE WHEN MEM_LVL_CURR = MEM_LVL AND MEM_LVL_CURR = 4 THEN 1 WHEN MEM_LVL_CURR != 4 AND ROW_NUMBER() OVER( PARTITION BY MEM_ID ORDER BY PROMO_ID DESC ) = 1 THEN 1 ELSE 0 END MAIN, MEM_LVL_CURR, PROMO_ID FROM ( SELECT O1.MEM_ID, O1.MEM_CODE, O3.CODE_NAME POSITION_NAME, '' MEMBER_CODE_HISTORY, NAME NAME, O2.ORG_CODE GMCODE, O2.GRP_CODE SMCODE, O2.DEPT_CODE HMCODE, '' MOTHER_MANAGER_CODE, '' MOTHER_MANAGER_NAME, '' MEMBER_ID, SPONS_CODE SPONS_CODE, TO_CHAR(JOIN_DT, 'DD/MM/YYYY') PROMOTION_DATE, '' DISCONTINUE_DATE, 4 MEM_LVL, O2.ORG_CODE, O2.GRP_CODE, O2.DEPT_CODE, O2.MEM_LVL MEM_LVL_CURR, 0 PROMO_ID FROM ORG0001D O1 JOIN ORG1001V O2 ON O2.MEM_ID = O1.MEM_ID JOIN SYS0013M O3 ON O3.CODE_ID = O1.MEM_TYPE WHERE MEM_TYPE IN ( '1', '2', '3', '7' )
AND O1.MEM_CODE =#{promoDisHisCode}
AND O2.ORG_CODE =#{orgCodeHd}
AND O2.GRP_CODE = #{grpCodeHd}
AND O2.DEPT_CODE = #{deptCodeHd}
UNION SELECT EXTENT1.MEM_ID, EXTENT1.MEM_CODE, EXTENT3.MEM_ORG_DESC POSITION_NAME, EXTENT2.DEPT_CODE_TO MEMBER_CODE_HISTORY, EXTENT1.NAME NAME, CASE WHEN EXTENT2.MEM_LVL_TO IN ( '2', '3' ) THEN EXTENT2.LAST_ORG_CODE END GMCODE, CASE WHEN EXTENT2.MEM_LVL_TO IN ( '3', '4' ) THEN EXTENT2.LAST_GRP_CODE END SMCODE, CASE WHEN EXTENT2.MEM_LVL_TO = 4 THEN EXTENT2.LAST_DEPT_CODE END HMCODE, EXTENT2.PR_CODE MOTHER_MANAGER_CODE, EXTENT11.NAME MOTHER_MANAGER_NAME, '' MEMBER_ID, EXTENT1.SPONS_CODE SPONS_CODE, TO_CHAR(EXTENT2.CRT_DT, 'DD/MM/YYYY') PROMOTION_DATE, TO_CHAR(EXTENT7.CRT_DT, 'DD/MM/YYYY') DISCONTINUE_DATE, EXTENT2.MEM_LVL_TO MEM_LVL, EXTENT4.ORG_CODE, EXTENT4.GRP_CODE, EXTENT4.DEPT_CODE, EXTENT4.MEM_LVL MEM_LVL_CURR, EXTENT2.PROMO_ID FROM ORG0001D EXTENT1 JOIN ORG0007D EXTENT2 ON EXTENT2.MEM_ID = EXTENT1.MEM_ID JOIN ORG0012D EXTENT3 ON (EXTENT3.MEM_TYPE_ID = EXTENT2.MEM_TYPE_ID) AND (EXTENT3.MEM_ORG_LVL = EXTENT2.MEM_LVL_TO) JOIN ORG1001V EXTENT4 ON EXTENT1.MEM_CODE = EXTENT4.MEM_CODE LEFT JOIN ORG0005D EXTENT8 ON EXTENT8.MEM_ID = EXTENT1.MEM_ID LEFT JOIN ORG0007D EXTENT7 ON (EXTENT7.MEM_ID = EXTENT2.MEM_ID) AND (EXTENT7.DEPT_CODE_FROM = EXTENT2.DEPT_CODE_TO) AND ( EXTENT7.PROMO_TYPE_ID IN ( '747', '748' ) ) JOIN ORG0001D EXTENT11 ON EXTENT11.MEM_ID = EXTENT2.PARENT_ID_FROM WHERE 1 = 1 AND EXTENT2.PROMO_TYPE_ID IN ( '747' )
AND Extent1.MEM_TYPE = #{promoDisHismemTypeCom}
AND Extent1.MEM_CODE = #{promoDisHisCode}
AND Extent1.NAME LIKE '%' || #{promoDisHisName} || '%'
AND Extent1.NRIC LIKE '%' || #{promoDisHisIcNum} || '%'
AND ((Extent1.TEL_HUSE LIKE '%' || #{promoDisHisContact}|| '%' ) OR ( Extent1.TEL_OFFICE LIKE '%' || #{promoDisHisContact}|| '%' ) OR ( Extent1.TEL_MOBILE LIKE '%' || #{promoDisHisContact}|| '%' ) ) AND ( Extent1.TEL_MOBILE = #{promoDisHisContact} OR Extent1.TEL_HUSE = #{promoDisHisContact} OR Extent1.TEL_OFFICE = #{promoDisHisContact})
AND Extent1.SPONS_CODE LIKE '%' || #{promoDisHisSponsor} || '%'
AND Extent8.MEM_LVL= #{position}
AND O1.MEM_CODE =#{promoDisHisCode}
AND EXTENT4.ORG_CODE =#{orgCodeHd}
AND EXTENT4.GRP_CODE = #{grpCodeHd}
AND EXTENT4.DEPT_CODE = #{deptCodeHd}
AND Extent4.ORG_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
AND Extent4.GRP_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE =#{userName} )
AND Extent4.DEPT_CODE IN (SELECT B.DEPT_CODE FROM ORG0001D A, ORG0005D B WHERE A.MEM_ID = B.MEM_ID AND A.MEM_CODE = #{userName} )
AND Extent4.ORG_CODE = #{orgCodeHd}
AND Extent4.GRP_CODE = #{grpCodeHd}
AND Extent4.DEPT_CODE = #{deptCodeHd}
AND Extent1.MEM_CODE = #{promoDisHisCode}
) T
) WHERE 1=1 ORDER BY MEM_CODE,PROMO_ID

