mapper.select:
SELECT A.*, B.NAME FROM ( SELECT 'MFAR'||LPAD(A.REQ_ID, 5, '0') REQNO, A.REQ_ID, B.USER_NAME, START_DT, END_DT, A.REMARK, COUNT(CASE WHEN C.STUS_CODE_ID = 1 THEN 1 END) ACTIVE, COUNT(CASE WHEN C.STUS_CODE_ID = 5 THEN 1 END) APPROVE, COUNT(CASE WHEN C.STUS_CODE_ID = 6 THEN 1 END) REJECT, A.CRT_USER_ID FROM ORG0052D A JOIN SYS0047M B ON A.CRT_USER_ID = B.USER_ID JOIN ORG0053D C ON C.REQ_ID = A.REQ_ID GROUP BY A.REQ_ID, B.USER_NAME, START_DT, END_DT, A.REMARK, A.CRT_USER_ID ) A LEFT JOIN ( SELECT REQ_ID, A.STUS_CODE_ID, B.NAME, B.MEM_ID, LAG(STUS_CODE_ID) OVER (PARTITION BY REQ_ID ORDER BY SEQ) PREV_STUS FROM ORG0053D A JOIN ORG0001D B ON A.MEM_ID = B.MEM_ID ) B ON A.REQ_ID = B.REQ_ID AND (PREV_STUS IS NULL OR PREV_STUS = 5) AND STUS_CODE_ID IN (1,6) WHERE 1=1
AND A.CRT_USER_ID = #{userId}
AND B.MEM_ID = #{curr} AND B.STUS_CODE_ID = 1
AND ACTIVE = 0 AND REJECT = 0 AND TO_CHAR(SYSDATE,'YYYY/MM/DD') BETWEEN TO_CHAR(A.START_DT,'YYYY/MM/DD') AND TO_CHAR(A.END_DT,'YYYY/MM/DD') AND A.CRT_USER_ID = #{userId}
ORDER BY REQNO DESC

