SELECT B.BRNCH_ID CODE_ID , A.CT_ID MEM_ID , A.MORNG_SESION_AS , A.MORNG_SESION_INS , A.MORNG_SESION_RTN , A.AFTNON_SESION_AS , A.AFTNON_SESION_INS , A.AFTNON_SESION_RTN , A.EVNG_SESION_AS , A.EVNG_SESION_INS , A.EVNG_SESION_RTN , A.STUS , A.CRT_DT , A.CRT_USER_ID , A.UPD_DT , A. UPD_USER_ID , B.CODE , C.MEM_CODE FROM ORG0015M A LEFT OUTER JOIN SYS0005M B ON A.BRNCH_ID = B.BRNCH_ID LEFT OUTER JOIN ORG0001D C ON C.MEM_ID = A.CT_ID
AND B.BRNCH_ID IN
#{item}
AND B.MEM_CODE = #{CTCode}

SELECT BRNCH_ID CODE_ID, CODE CODE_NAME FROM SYS0005M WHERE 1=1
AND TYPE_ID=#{groupCode}
GROUP BY BRNCH_ID,CODE ORDER BY CODE
