mapper.select:
SELECT F.brnch_id CODE_ID, E.mem_id, D.morng_sesion_as, D.morng_sesion_ins, D.morng_sesion_rtn, D.aftnon_sesion_as, D.aftnon_sesion_ins, D.aftnon_sesion_rtn, D.evng_sesion_as, D.evng_sesion_ins, D.evng_sesion_rtn, D.stus, D.crt_dt, D.crt_user_id, D.upd_dt, D. upd_user_id, F.code || ' - ' || F.NAME CODE, E.mem_code || ' - ' || E.NAME MEM_CODE FROM (SELECT A.brnch_id CODE_ID, A.ct_id MEM_ID, A.morng_sesion_as, A.morng_sesion_ins, A.morng_sesion_rtn, A.aftnon_sesion_as, A.aftnon_sesion_ins, A.aftnon_sesion_rtn, A.evng_sesion_as, A.evng_sesion_ins, A.evng_sesion_rtn, A.stus, A.crt_dt, A.crt_user_id, A.upd_dt, A. upd_user_id, B.code || ' - ' || B.NAME CODE, C.mem_code || ' - ' || C.NAME MEM_CODE FROM org0015m A INNER JOIN sys0005m B ON A.brnch_id = B.brnch_id INNER JOIN org0001d C ON C.mem_id = A.ct_id WHERE 1 = 1
AND B.brnch_id = #{cmbbranchId}
AND C.MEM_ID = #{cmbctId}
) D
RIGHT JOIN org0001d E ON D.mem_id = E.mem_id INNER JOIN sys0005m F ON F.brnch_id = E.brnch WHERE 1 = 1 AND D.mem_id IS NULL
AND E.brnch = #{cmbbranchId}
AND E.MEM_ID = #{cmbctId}
AND E.STUS = '1' ORDER BY D.MEM_ID ASC, E.MEM_ID ASC
INNER JOIN org0001d E ON D.mem_id = E.mem_id INNER JOIN sys0005m F ON F.brnch_id = E.brnch WHERE 1 = 1
AND E.brnch = #{cmbbranchId}
AND E.MEM_ID = #{cmbctId}
AND E.STUS = '1' ORDER BY D.MEM_ID ASC, E.MEM_ID ASC
FULL OUTER JOIN org0001d E ON D.mem_id = E.mem_id INNER JOIN sys0005m F ON F.brnch_id = E.brnch WHERE 1 = 1
AND E.brnch = #{cmbbranchId}
AND E.MEM_ID = #{cmbctId}
AND E.STUS = '1' ORDER BY D.MEM_ID ASC, E.MEM_ID ASC

SELECT BRNCH_ID CODE_ID, CODE ||' - '||NAME CODE_NAME FROM SYS0005M WHERE 1=1
AND TYPE_ID=#{groupCode}
GROUP BY BRNCH_ID,CODE,NAME ORDER BY CODE
