mapper.select:

      SELECT 
			     (SELECT
			         COUNT(AREA_ID) AS   ACNT
			        FROM SYS0064M  WHERE AREA_ID =#{areaId}
			     ) ACNT ,
			     (SELECT
			         COUNT(CODY_BRNCH_CODE) AS   BCNT
			        FROM SYS0064M WHERE CODY_BRNCH_CODE =#{branch}
			     ) BCNT ,
			     (SELECT
			         COUNT(CODY_MANGR_USER_ID) AS   UCNT
			        FROM SYS0064M  WHERE CODY_MANGR_USER_ID =#{extBranch}
			     ) UCNT 
	   FROM DUAL


SELECT 
           (SELECT
               COUNT(AREA_ID) AS   ACNT
              FROM SYS0064M  WHERE AREA_ID =#{areaId}
           ) ACNT ,
           (SELECT
               COUNT(CT_BRNCH_CODE) AS   BCNT
              FROM SYS0064M  WHERE CT_BRNCH_CODE =#{branch}
           ) BCNT ,
           (SELECT
               COUNT(CT_SUB_GRP) AS   UCNT
              FROM SYS0064M  WHERE CT_SUB_GRP =#{extBranch}
           ) UCNT 
 FROM DUAL

SELECT A.REQST_NO REQST_NO , A.REQST_DT REQST_DT, A.REQST_USER_ID REQST_USER_ID, A.BRNCH_TYPE BRNCH_TYPE, A.BRNCH_NAME BRNCH_NAME, A.CNFM_STUS CNFM_STUS , A.CNFM_STUS_NAME CNFM_STUS_NAME FROM (SELECT REQST_NO, TO_CHAR(REQST_DT,'YYYY-MM-DD') REQST_DT, REQST_USER_ID, BRNCH_TYPE, CNFM_STUS, CASE WHEN (BRNCH_TYPE = 42) THEN 'CODY BRANCH' ELSE 'DREAM SERVICE CENTER' END BRNCH_NAME, CASE WHEN (CNFM_STUS = 1) THEN 'ACT' WHEN (CNFM_STUS = 4) THEN 'COM' ELSE '' END CNFM_STUS_NAME FROM ORG0019M ) A WHERE 1=1
AND A.BRNCH_TYPE IN
#{item}
AND A.REQST_NO = #{requestNo}
AND A.REQST_USER_ID = #{requestUserId}
AND TO_DATE (A.REQST_DT, 'YYYY-MM-DD') = TO_DATE(#{requestDt},'DD/MM/YYYY')
GROUP BY A.REQST_NO,A.REQST_DT ,A.REQST_USER_ID,A.BRNCH_TYPE,A.BRNCH_NAME,A.CNFM_STUS,A.CNFM_STUS_NAME ORDER BY A.REQST_NO

mapper.update:
UPDATE SYS0064M SET 
CODY_BRNCH_CODE =  #{codyBrnchCode},
CODY_MANGR_USER_ID = #{codyMangrUserId}
WHERE AREA_ID=#{areaId}

	   UPDATE ORG0019M SET
          AVAIL_FLAG = 'N'
          WHERE AREA_ID=#{areaId} AND BRNCH_TYPE= #{brnchType}

UPDATE SYS0064M SET 
CT_BRNCH_CODE =  #{ctBrnchCode},
CT_SUB_GRP = #{ctSubGrp}
WHERE AREA_ID=#{areaId}
