	       ,REQST_USER_ID
	       ,CODY_BRNCH_CODE
	       ,CODY_MANGR_USER_ID
	       ,BRNCH_TYPE
	       ,CNFM_STUS
	       )

            SELECT #{item.reqstNo}
            ,#{item.areaId}
            ,SYSDATE
            ,#{item.requester}
            ,#{item.branch}
            ,#{item.extBranch}
            ,#{item.brnchId}
            ,'1'
            FROM DUAL

