mapper.update:
UPDATE SYS0064M SET
CT_BRNCH_CODE =  #{ctBrnchCode},
CT_SUB_GRP = #{ctSubGrp}
WHERE AREA_ID=#{areaId}

UPDATE SYS0064M SET
SO_BRNCH_CODE =  #{soBrnchCode}
WHERE AREA_ID=#{areaId}

