SELECT Project1.F_BANK_JRNL_ID F_BANK_JRNL_ID , Project1.F_BANK_JRNL_REF_NO F_BANK_JRNL_REF_NO , Project1.F_BANK_JRNL_ACC_ID F_BANK_JRNL_ACC_ID , Project1.ACC_CODE ACC_CODE , Project1.ACC_DESC ACC_DESC , Project1.F_BANK_JRNL_REM F_BANK_JRNL_REM , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.NAME NAME , TO_CHAR(Project1.C1 , 'DD-MM-YYYY hh:mi:ss A.M.') AS CRT_DT, Project1.F_BANK_JRNL_CRT_USER_ID F_BANK_JRNL_CRT_USER_ID , Project1.USER_NAME USER_NAME , Project1.F_BANK_JRNL_ADJ F_BANK_JRNL_ADJ FROM ( SELECT Project1.F_BANK_JRNL_ID F_BANK_JRNL_ID , Project1.F_BANK_JRNL_REF_NO F_BANK_JRNL_REF_NO , Project1.F_BANK_JRNL_ACC_ID F_BANK_JRNL_ACC_ID , Project1.F_BANK_JRNL_REM F_BANK_JRNL_REM , Project1.F_BANK_JRNL_CRT_USER_ID F_BANK_JRNL_CRT_USER_ID , Project1.F_BANK_JRNL_ADJ F_BANK_JRNL_ADJ , Project1.C1 C1 , Project1.ACC_CODE ACC_CODE , Project1.ACC_DESC ACC_DESC , Project1.USER_NAME USER_NAME , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.NAME NAME , ROW_NUMBER() OVER ( ORDER BY Project1.F_BANK_JRNL_ID DESC ) ROW_NUMBER FROM ( SELECT Extent1.F_BANK_JRNL_ID F_BANK_JRNL_ID , Extent1.F_BANK_JRNL_REF_NO F_BANK_JRNL_REF_NO , Extent1.F_BANK_JRNL_ACC_ID F_BANK_JRNL_ACC_ID , Extent1.F_BANK_JRNL_REM F_BANK_JRNL_REM , Extent1.F_BANK_JRNL_CRT_USER_ID F_BANK_JRNL_CRT_USER_ID , Extent1.F_BANK_JRNL_ADJ F_BANK_JRNL_ADJ , TO_DATE(Extent1.F_BANK_JRNL_CRT_DT) C1 , Extent2.ACC_CODE ACC_CODE , Extent2.ACC_DESC ACC_DESC , Extent3.USER_NAME USER_NAME , Extent4.STUS_CODE_ID STUS_CODE_ID , Extent4.NAME NAME FROM PAY0079D Extent1 LEFT JOIN SYS0001M Extent2 ON Extent1.F_BANK_JRNL_ACC_ID = Extent2.ACC_ID LEFT JOIN SYS0047M Extent3 ON Extent1.F_BANK_JRNL_CRT_USER_ID = Extent3.USER_ID LEFT JOIN SYS0038M Extent4 ON Extent1.F_BANK_JRNL_STUS_ID = Extent4.STUS_CODE_ID WHERE 1=1
AND Extent1.F_BANK_JRNL_REF_NO LIKE '%' || #{refNo} || '%' ESCAPE '~'
AND Extent4.STUS_CODE_ID IN
#{item}
AND Extent3.USER_NAME LIKE '%' || #{uploadBy} || '%' ESCAPE '~'
AND Extent1.F_BANK_JRNL_ACC_ID = #{accountId}
AND TO_DATE(Extent1.F_BANK_JRNL_CRT_DT) >= TO_DATE(#{fromUploadDate}, 'DD/MM/YYYY') AND TO_DATE(Extent1.F_BANK_JRNL_CRT_DT) <= TO_DATE(#{toUploadDate}, 'DD/MM/YYYY') +1
) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.F_BANK_JRNL_ID DESC
