mapper.select:
SELECT Extent1.FILE_ID FILE_ID , Extent1.FILE_NAME FILE_NAME , Extent1.FILE_DT FILE_DT , Extent1.FILE_REF_NO FILE_REF_NO , Extent1.TOT_RCORD TOT_RCORD , Extent1.TOT_AMT TOT_AMT , Extent1.FILE_STUS FILE_STUS FROM CMM0052M Extent1 WHERE 1=1
AND Extent1.FILE_ID = #{fileId}
ORDER BY Extent1.FILE_ID DESC
