SELECT /* [com.coway.trust.biz.payment.payment.service.impl.PaymentListApiMapper.selectPaymentList] 20191019 - KR HAN */ Extent1.MOB_PAY_NO MOB_PAY_NO ,Extent1.SALES_ORD_NO SALES_ORD_NO ,Extent1.PAY_MODE PAY_MODE ,Extent2.CODE_NAME PAY_MODE_NM ,Extent1.PAY_AMT PAY_AMT ,NVL(Extent1.SLIP_NO, Extent1.CHEQUE_NO) SERIAL_NO ,DECODE( Extent1.UPLOAD_IMG, '', 'N', 'Y') IMG_YN ,Extent1.SLIP_NO SLIP_NO ,Extent1.CHEQUE_NO CHEQUE_NO FROM PAY0297D Extent1 LEFT JOIN SYS0013M Extent2 ON Extent1.PAY_MODE = Extent2.CODE_ID AND Extent2.CODE_MASTER_ID = 439 AND Extent1.CRT_USER_ID = #{userId}
AND TO_CHAR (Extent1.CRT_DT, 'YYYY-MM-DD') >= TO_CHAR(TO_DATE (#{fromDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND TO_CHAR (Extent1.CRT_DT, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE (#{toDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND Extent1.PAY_STUS_ID = #{ticketType}
ORDER BY Extent1.CRT_DT DESC
