mapper.select:
SELECT TRX_ID, TRX_DT, TRX_AMT, PAY_ID, OR_NO, TR_NO, PAY_DT, LAST_UPD_DT, PAY_TYPE_ID, PAY_TYPE_NAME, LAST_UPD_USER_ID, LAST_UPD_USER_NAME, SALES_ORD_ID, SALES_ORD_NO, OR_AMT, PO_NO, CLCTR_BRNCH_ID, CLCTR_BRNCH_CODE, CLCTR_BRNCH_NAME, CUST_ID, CUST_NAME, CUST_IC, CUST_TYPE_ID, CUST_TYPE_NAME, PRODUCT_CTGRY_ID, PRODUCT_CTGRY_NAME, PRODUCT_ID, PRODUCT_CODE, PRODUCT_DESC, KEYIN_USER_ID, KEYIN_USER_NAME, APP_TYPE_ID, APP_TYPE_NAME, CLCTR_NAME, CLCTR_CODE, KEYIN_BRNCH_ID, KEYIN_BRNCH_CODE, KEYIN_BRNCH_NAME, DEBTOR_ACC_ID, DEBTOR_ACC_CODE, DEBTOR_ACC_DESC, SALES_MEM_ID, SALES_MEM_CODE, SALES_MEM_NAME, VIRTL_ACC_NO, HP_CODE, HP_NAME, HP_ID, CLCTR_ID, ALLOW_COMM, ADV_MONTH, TR_ISSU_DT, BATCH_PAY_ID FROM PAY1001V WHERE 1=1
AND SALES_ORD_NO = #{orderNo}
AND PAY_DT >= TO_DATE(TO_CHAR(TO_DATE(#{payDate1},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') AND PAY_DT < TO_DATE(TO_CHAR(TO_DATE(#{payDate2},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') + 1
AND APP_TYPE_ID = #{applicationType}
AND OR_NO = #{orNo}
AND PO_NO = #{poNo}
AND PAY_TYPE_ID = #{paymentType}
AND CUST_ID = #{customerId}
AND KEYIN_BRNCH_ID = #{branchId}
AND KEYIN_USER_ID = #{userId}
AND CUST_NAME LIKE '%' || #{customerName} || '%'
AND CUST_IC = #{customerIC}
AND TR_NO = #{trNo}
AND PAY_ID IN (SELECT PAY_ID from PAY0065D WHERE PAY_ITM_CHQ_NO = #{chequeNo})
AND PAY_ID IN (SELECT PAY_ID from PAY0065D WHERE PAY_ITM_ORI_CC_NO = #{crcNo})
AND PAY_ID IN (SELECT PAY_ID from PAY0065D WHERE PAY_ITM_ISSU_BANK_ID = #{bankAccount})
AND BATCH_PAY_ID = #{batchPaymentId}
ORDER BY PAY_ID

SELECT V.ORG_CODE tOrgCode , V.GRP_CODE tGrpCode , V.DEPT_CODE tDeptCode , V.ORG_CODE OrgCode , V.GRP_CODE GrpCode, V.DEPT_CODE DeptCode, R1.MEM_CODE MemberCode, COUNT(ACC.ACC_DEBT_ORD_ID) sUnit , SUM(CASE WHEN Sub.ACC_DEBT_SUB_OPNG_OTSTND < 0 THEN 0 ELSE Sub.ACC_DEBT_SUB_OPNG_OTSTND END) sLMOS , SUM(Sub.ACC_DEBT_SUB_CURR_CHRG) sCMCHG , SUM(Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET) sCLCTG , SUM(Sub.ACC_DEBT_SUB_CURR_COLCT) sCOL , SUM(Sub.ACC_DEBT_SUB_CURR_ADJ) sADJ , (SUM(Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET) + (SUM(Sub.ACC_DEBT_SUB_CURR_COLCT) + SUM(Sub.ACC_DEBT_SUB_CURR_ADJ) )) sOUT , /*SUM(Sub.AccDebtSubTotalOutstanding)CUROUTSTD,*/ (CASE WHEN (SUM(Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET) ) = 0 THEN 0 ELSE round(((SUM(Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET) + (SUM(Sub.ACC_DEBT_SUB_CURR_COLCT) + SUM(Sub.ACC_DEBT_SUB_CURR_ADJ) )) / (SUM(Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET) ) * 100), 2) END) sRate FROM PAY0052S ACC JOIN PAY0053S Sub ON Sub.ACC_DEBT_ORD_ID = ACC.ACC_DEBT_ORD_ID JOIN SAL0001D SOM ON SOM.SALES_ORD_ID = ACC.ACC_DEBT_ORD_ID LEFT JOIN SAL0071D SCH ON SCH.SALES_ORD_ID = SOM.SALES_ORD_ID JOIN ORG0001D R1 ON SOM.MEM_ID = R1.MEM_ID AND R1.MEM_TYPE = 2 JOIN ORG1001V V ON V.MEM_ID = R1.MEM_ID WHERE R1.STUS = 1 AND SOM.APP_TYPE_ID = 66
--AND SOM.DeptCode like '%CCS%' AND Sub.ACC_DEBT_SUB_OPNG_COLCT_TRGET > 0 AND SOM.STUS_CODE_ID = 4 AND V.DEPT_CODE <> ' ' AND ( SCH.STUS_CODE_ID = 'INV' OR SCH.STUS_CODE_ID = 'REG' )
AND V.ORG_CODE = #{orgCode}
AND V.GRP_CODE = #{grpCode}
AND V.DEPT_CODE = #{deptCode}
AND R1.MEM_CODE = #{memberCode}
GROUP BY V.ORG_CODE,V.GRP_CODE,V.DEPT_CODE,R1.MEM_CODE ORDER BY V.ORG_CODE,V.GRP_CODE,V.DEPT_CODE,R1.MEM_CODE

SELECT S_ID AS sID, T_ORG_CODE AS tOrgCode, T_GRP_CODE AS tGrpCode, T_DEPT_CODE AS tDeptCode, ORG_CODE AS orgCode, GRP_CODE AS grpCode, DEPT_CODE AS deptCode, MEM_CODE AS memCode, S_UNIT AS sUnit, S_LMOS AS sLmos, S_CM_CHG AS sCmChg, S_CL_CTG AS sClCtg, S_COL AS sCol, S_ADJ AS sAdj, S_OUT AS sOut, S_OUT_RATE AS sOutRate FROM REP0014S WHERE 1=1
AND T_ORG_CODE = #{orgCode}
AND T_GRP_CODE = #{grpCode}
AND T_DEPT_CODE = #{deptCode}
AND MEM_CODE = #{memCode}
ORDER BY S_ID

mapper.update:
UPDATE PAY0064D
SET
UPD_DT= SYSDATE 

  , TR_NO=#{trNo}


  ,COLL_MEM_ID = #{collMemId}


  , BRNCH_ID = #{brnchId}


  , TR_ISSU_DT = #{trIssueDate}


  , ALLOW_COMM = #{allowComm}

WHERE PAY_ID=#{payId}

UPDATE PAY0065D
SET UPD_DT = SYSDATE

    , PAY_ITM_REF_NO = #{payItemRefNo}


    , PAY_ITM_ISSU_BANK_ID = #{payItemIssuedBankId}


    , PAY_ITM_REF_DT = #{payItemRefDate}


    , PAY_ITM_REM = #{payItemRemark}


    , PAY_ITM_CC_HOLDER_NAME = #{payItemCCHolderName}


    , PAY_ITM_CC_EXPR_DT = #{payItemCCExpiryDate}


    , PAY_ITM_CC_TYPE_ID = #{payItemCCTypeId}


    , PAY_ITM_EFT_NO = #{payItemEFTNo}


    , PAY_ITM_RUNNG_NO = #{payItemRunningNo}


    , PAY_ITM_CARD_TYPE_ID = #{payItemCardTypeId}


    , UPD_USER_ID = #{updator}

 WHERE PAY_ITM_ID = #{payItemId}

UPDATE PAY0097D
SET UPD_DT = SYSDATE

    , REF_NO = #{refNo}


    , BANK_ID = #{bankId}


    , REF_DT = #{refDate}


    , CC_HOLDER_NAME = #{ccHolderName}


    , CC_EXPR = #{ccExpiry}


    , CC_TYPE_ID = #{ccTypeId}


    , UPD_USER_ID = #{updator}

 WHERE ITM_ID = #{itemId}

