WHEN NOT EXISTS(SELECT * FROM PAY0260D EXTENT10 WHERE EX11.GROUP_SEQ = EXTENT10.SRC_GRP_SEQ AND EXTENT10.SRC_AMT = #{payItemAmt} AND (SRC_PAY_ID = #{payId} OR FT_ORD_NO = extent12.SALES_ORD_NO )) THEN EXTENT1.PAY_ITM_REM
ELSE EXTENT10.FT_APPV_REM END PAY_ITM_REM, Extent1.PAY_ITM_STUS_ID, Extent6.NAME AS NAME1, Extent1.PAY_ITM_IS_LOK, Extent1.PAY_ITM_BANK_CHRG_AMT, Extent1.PAY_ITM_IS_THRD_PARTY, Extent1.PAY_ITM_THRD_PARTY_IC, Extent1.PAY_ITM_MID, Extent1.PAY_ITM_GRP_ID, Extent1.PAY_ITM_REF_ITM_ID, Extent1.ETC3, CASE WHEN Extent1.PAY_ITM_EFT_NO IS NOT NULL THEN Extent1.PAY_ITM_EFT_NO ELSE ' ' END AS C4, CASE WHEN Extent1.PAY_ITM_RUNNG_NO IS NOT NULL THEN Extent1.PAY_ITM_RUNNG_NO ELSE ' ' END AS C5, CASE WHEN Extent1.PAY_ITM_CARD_TYPE_ID IS NOT NULL THEN Extent1.PAY_ITM_CARD_TYPE_ID ELSE 0 END AS C6, CASE WHEN Extent7.CODE_ID IS NOT NULL THEN Extent7.CODE_NAME ELSE ' ' END AS C7, CASE WHEN Extent1.PAY_ITM_CARD_MODE_ID IS NOT NULL THEN CASE WHEN 1298 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'On' WHEN 1299 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'Off' WHEN 1300 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'PNP' ELSE CARDMODE.CODE_NAME END WHEN 1 = Extent1.PAY_ITM_IS_ONLINE THEN 'On' WHEN 0 = Extent1.PAY_ITM_IS_ONLINE THEN 'Off' ELSE '-' END AS C8 FROM PAY0065D Extent1 LEFT OUTER JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.PAY_ITM_MODE_ID LEFT OUTER JOIN SYS0013M Extent3 ON Extent3.CODE_ID = Extent1.PAY_ITM_CC_TYPE_ID LEFT OUTER JOIN SYS0004M Extent4 ON Extent4.BANK_ID = Extent1.PAY_ITM_ISSU_BANK_ID LEFT OUTER JOIN SYS0001M Extent5 ON Extent5.ACC_ID = Extent1.PAY_ITM_BANK_ACC_ID LEFT OUTER JOIN SYS0038M Extent6 ON Extent6.STUS_CODE_ID = Extent1.PAY_ITM_STUS_ID LEFT OUTER JOIN SYS0013M Extent7 ON Extent7.CODE_ID = Extent1.PAY_ITM_CARD_TYPE_ID LEFT OUTER JOIN SYS0013M CARDMODE ON Extent1.PAY_ITM_CARD_MODE_ID = CARDMODE.CODE_ID LEFT JOIN PAY0064D extent8 ON extent1.PAY_ID = extent8.PAY_ID LEFT JOIN SAL0001D extent9 ON extent8.SALES_ORD_ID = extent9.SALES_ORD_ID LEFT JOIN PAY0252T EX11 ON EX11.PAY_ID = EXTENT8.PAY_ID
LEFT JOIN PAY0260D extent10 ON EX11.GROUP_SEQ = extent10.SRC_GRP_SEQ AND extent10.SRC_AMT = #{payItemAmt}
LEFT JOIN PAY0260D extent10 ON EX11.GROUP_SEQ = extent10.SRC_GRP_SEQ
LEFT JOIN SAL0001D extent12 ON extent8.SALES_ORD_ID = extent12.SALES_ORD_ID WHERE Extent1.PAY_ITM_STUS_ID = 1 AND Extent1.PAY_ID = #{payId}

