AND A.T_ORG_CODE = #{orgCode}
AND A.T_GRP_CODE = #{grpCode}
AND A.T_DEPT_CODE = #{deptCode}
AND A.MEM_CODE = #{memCode}
AND A.MEM_TYPE = #{memType}
AND A.SRV_TYPE = #{cmbSrvType}
ORDER BY A.S_ID

SELECT DISTINCT Extent1.PAY_ITM_ID PAY_ITM_ID , Extent1.PAY_ID PAY_ID , Extent1.PAY_ITM_MODE_ID PAY_ITM_MODE_ID , Extent2.CODE_NAME CODE_NAME , CASE WHEN Extent1.PAY_ITM_REF_NO IS NULL then Extent8.REF_DTL ELSE Extent1.PAY_ITM_REF_NO END AS PAY_ITM_REF_NO , (CASE WHEN Extent1.PAY_ITM_ORI_CC_NO IS NULL THEN ' ' WHEN LENGTH(Extent1.PAY_ITM_ORI_CC_NO) >= 13 THEN SUBSTR(Extent1.PAY_ITM_ORI_CC_NO,0,6) || '******' || SUBSTR(Extent1.PAY_ITM_ORI_CC_NO,13,LENGTH(Extent1.PAY_ITM_ORI_CC_NO)) ELSE Extent1.PAY_ITM_ORI_CC_NO END) PAY_ITM_CC_NO, (CASE WHEN Extent1.PAY_ITM_ORI_CC_NO IS NULL THEN ' ' WHEN LENGTH(Extent1.PAY_ITM_ORI_CC_NO) >= 13 THEN SUBSTR(Extent1.PAY_ITM_ORI_CC_NO,0,6) || '******' || SUBSTR(Extent1.PAY_ITM_ORI_CC_NO,13,LENGTH(Extent1.PAY_ITM_ORI_CC_NO)) ELSE Extent1.PAY_ITM_ORI_CC_NO END) C1, Extent1.PAY_ITM_ORI_CC_NO AS PAY_ITM_ENCRYPT_CC_NO , Extent1.PAY_ITM_CC_TYPE_ID PAY_ITM_CC_TYPE_ID , Extent3.CODE_NAME CODENAME1 , Extent1.PAY_ITM_CC_HOLDER_NAME PAY_ITM_CC_HOLDER_NAME , Extent1.PAY_ITM_CC_EXPR_DT PAY_ITM_CC_EXPR_DT , Extent1.PAY_ITM_CHQ_NO PAY_ITM_CHQ_NO , Extent1.PAY_ITM_ISSU_BANK_ID PAY_ITM_ISSU_BANK_ID , CASE WHEN ( Extent4.BANK_ID IS NOT NULL ) THEN Extent4.CODE ELSE ' ' END C2 , Extent4.NAME NAME , Extent1.PAY_ITM_AMT PAY_ITM_AMT , Extent1.PAY_ITM_IS_ONLINE PAY_ITM_IS_ONLINE , Extent1.PAY_ITM_BANK_ACC_ID PAY_ITM_BANK_ACC_ID , CASE WHEN ( Extent5.ACC_ID IS NOT NULL ) THEN Extent5.ACC_CODE ELSE ' ' END C3 , Extent5.ACC_DESC ACC_DESC , Extent1.PAY_ITM_REF_DT PAY_ITM_REF_DT , Extent1.PAY_ITM_APPV_NO PAY_ITM_APPV_NO , CASE WHEN EXISTS(SELECT * FROM PAY0260D EXTENT10 WHERE EX11.GROUP_SEQ = EXTENT10.SRC_GRP_SEQ AND EXTENT10.SRC_AMT = #{payItemAmt} AND SRC_PAY_ID = #{payId} ) THEN EXTENT1.PAY_ITM_REM
WHEN NOT EXISTS(SELECT * FROM PAY0260D EXTENT10 WHERE EX11.GROUP_SEQ = EXTENT10.SRC_GRP_SEQ AND EXTENT10.SRC_AMT = #{payItemAmt} AND (SRC_PAY_ID = #{payId} OR FT_ORD_NO = extent12.SALES_ORD_NO )) THEN EXTENT1.PAY_ITM_REM
ELSE EXTENT10.FT_APPV_REM END PAY_ITM_REM, Extent1.PAY_ITM_STUS_ID PAY_ITM_STUS_ID , Extent6.NAME NAME1 , Extent1.PAY_ITM_IS_LOK PAY_ITM_IS_LOK , Extent1.PAY_ITM_BANK_CHRG_AMT PAY_ITM_BANK_CHRG_AMT , Extent1.PAY_ITM_IS_THRD_PARTY PAY_ITM_IS_THRD_PARTY , Extent1.PAY_ITM_THRD_PARTY_IC PAY_ITM_THRD_PARTY_IC , Extent1.PAY_ITM_MID PAY_ITM_MID , Extent1.PAY_ITM_GRP_ID PAY_ITM_GRP_ID , Extent1.PAY_ITM_REF_ITM_ID PAY_ITM_REF_ITM_ID , Extent1.ETC3 ETC3 , CASE WHEN ( Extent1.PAY_ITM_EFT_NO IS NOT NULL ) THEN Extent1.PAY_ITM_EFT_NO ELSE ' ' END C4 , CASE WHEN ( Extent1.PAY_ITM_RUNNG_NO IS NOT NULL ) THEN Extent1.PAY_ITM_RUNNG_NO ELSE ' ' END PAY_ITM_RUNNG_NO , CASE WHEN ( Extent1.PAY_ITM_CARD_TYPE_ID IS NOT NULL ) THEN Extent1.PAY_ITM_CARD_TYPE_ID ELSE 0 END C6 , CASE WHEN ( Extent7.CODE_ID IS NOT NULL ) THEN Extent7.CODE_NAME ELSE ' ' END C7 , CASE WHEN ( Extent1.PAY_ITM_CARD_MODE_ID IS NOT NULL ) THEN CASE WHEN 1298 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'On' WHEN 1299 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'Off' WHEN 1300 = Extent1.PAY_ITM_CARD_MODE_ID THEN 'PNP' ELSE CARDMODE.CODE_NAME END WHEN ( 1 = Extent1.PAY_ITM_IS_ONLINE ) THEN 'On' WHEN ( 0 = Extent1.PAY_ITM_IS_ONLINE ) THEN 'Off' ELSE '-' END C8 FROM PAY0065D Extent1 LEFT JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.PAY_ITM_MODE_ID LEFT JOIN SYS0013M Extent3 ON Extent3.CODE_ID = Extent1.PAY_ITM_CC_TYPE_ID LEFT JOIN SYS0004M Extent4 ON Extent4.BANK_ID = Extent1.PAY_ITM_ISSU_BANK_ID LEFT JOIN SYS0001M Extent5 ON Extent5.ACC_ID = Extent1.PAY_ITM_BANK_ACC_ID LEFT JOIN SYS0038M Extent6 ON Extent6.STUS_CODE_ID = Extent1.PAY_ITM_STUS_ID LEFT JOIN SYS0013M Extent7 ON Extent7.CODE_ID = Extent1.PAY_ITM_CARD_TYPE_ID LEFT JOIN SYS0013M CARDMODE ON Extent1.PAY_ITM_CARD_MODE_ID = CARDMODE.CODE_ID LEFT JOIN PAY0064D Extent8 ON Extent1.PAY_ID = Extent8.PAY_ID LEFT JOIN SAL0001D extent9 ON extent8.SALES_ORD_ID = extent9.SALES_ORD_ID LEFT JOIN PAY0252T EX11 ON EX11.PAY_ID = EXTENT8.PAY_ID
LEFT JOIN PAY0260D extent10 ON EX11.GROUP_SEQ = extent10.SRC_GRP_SEQ AND extent10.SRC_AMT = #{payItemAmt}
LEFT JOIN PAY0260D extent10 ON EX11.GROUP_SEQ = extent10.SRC_GRP_SEQ
LEFT JOIN SAL0001D extent12 ON extent8.SALES_ORD_ID = extent12.SALES_ORD_ID WHERE Extent1.PAY_ITM_STUS_ID = 1 AND Extent1.PAY_ID = #{payId}

