SELECT ACC_DEBT_SUB_CURR_ADJ , ACC_DEBT_SUB_CURR_COLCT , ACC_DEBT_SUB_TOT_OTSTND , IS_BOOLEAN , IS_BS_BOOLEAN , ACC_DEBT_SUB_OPNG_OTSTND_AGING , ACC_DEBT_ORD_NO , ACC_DEBT_ORD_ID , MODE_ID , PAY_MODE , ACC_DEBT_SUB_OPNG_COLCT_TRGET , CUSTOMER_TYPE_ID , CUSTOMER_NAME , MEM_ID , MEM_CODE , MEMBER_NAME , TOP_ORG_CODE , ORG_CODE , GRP_CODE , DEPT_CODE , TEL_M1 , TEL_M2 , TEL_R , TEL_O , TELF, DEDUCT_STUS, BANK_DTL_DR_BANK_TYPE_ID FROM PAY1012V INNER JOIN ( SELECT SALES_ORD_ID, MAX(RENT_INST_NO) RENT_INST_NO, MAX(RENT_INST_DT) RENT_INST_DT FROM SAL0070D GROUP BY SALES_ORD_ID ) SAL0070D ON ACC_DEBT_ORD_ID = SALES_ORD_ID AND SYSDATE <= FN_GET_FIRST_DAY_MONTH(ADD_MONTHS(SAL0070D.RENT_INST_DT, 6)) WHERE 1=1
AND ORG_CODE = #{orgCode}
AND GRP_CODE = #{grpCode}
AND DEPT_CODE = #{deptCode}
AND MEM_CODE = #{memCode}
AND ACC_DEBT_SUB_OPNG_OTSTND_AGING BETWEEN #{cmbOutstandMonth} AND (#{cmbOutstandMonth}+1)
AND CUSTOMER_TYPE_ID = #{cmbCustTypeId}
AND (ACC_DEBT_SUB_CURR_ADJ + ACC_DEBT_SUB_CURR_COLCT + ACC_DEBT_SUB_OPNG_COLCT_TRGET ) = 0
AND (ACC_DEBT_SUB_CURR_ADJ + ACC_DEBT_SUB_CURR_COLCT + ACC_DEBT_SUB_OPNG_COLCT_TRGET ) > 0 AND ACC_DEBT_SUB_CURR_COLCT < 0
AND ACC_DEBT_SUB_CURR_COLCT = 0
AND MODE_ID = #{cmbPaymode}
AND IS_BS_BOOLEAN = #{cmbBsMonth}

SELECT A.SALES_ORD_ID, A.BS_MONTH, A.REN_MEM, A.SALES_ORD_NO, A.CODY_CODE, A.CODY_NAME, SYD.CODE AS STATUS, A.CUST_NAME, A.TEL_MOBILE, A.TEL_HOME, A.TEL_OFFICE, A.PAY_MODE, A.TARGET, A.COLLECTION, NVL(C.BANK_DTL_APPR_CODE,'00') APPR_THS_MNTH , CASE WHEN D.ctrl_stus_id = 1 THEN '-' WHEN SYS.IND = 'SUCCESS' OR (C.BANK_DTL_APPR_CODE IS NULL AND C.BANK_DTL_ID IS NOT NULL) AND A.PAY_MODE IN ('CRC','DD') THEN 'Success' WHEN SYS.IND = 'INSFUNDS' THEN 'Insufficient Funds' WHEN SYS.IND = 'INVCARD' THEN 'Return pin/Invalid Card' WHEN SYS.IND = 'CHGCARD' THEN 'Change Card' WHEN SYS.IND = 'TRXNOTALW' AND D.CTRL_IS_CRC = 1 THEN 'Transaction Not Allowed' ELSE (CASE WHEN A.PAY_MODE = 'CRC' AND C.BANK_DTL_ID IS NOT NULL THEN 'Failed' ELSE '' END) END THIS_MONTH ,NVL(E.BANK_DTL_APPR_CODE,'00') APPR_PREV_MNTH , CASE WHEN F.ctrl_stus_id = 1 THEN '-' WHEN (SYC.IND = 'SUCCESS' OR (E.BANK_DTL_APPR_CODE IS NULL AND E.BANK_DTL_ID IS NOT NULL )) AND A.PAY_MODE IN ('CRC','DD') THEN 'Success' WHEN SYC.IND = 'INSFUNDS' THEN 'Insufficient Funds' WHEN SYC.IND = 'INVCARD' THEN 'Return pin/Invalid Card' WHEN SYC.IND = 'CHGCARD' THEN 'Change Card' WHEN SYC.IND = 'TRXNOTALW' AND F.CTRL_IS_CRC = 1 THEN 'Transaction Not Allowed' ELSE (CASE WHEN A.PAY_MODE = 'CRC' AND E.BANK_DTL_ID IS NOT NULL THEN 'Failed' ELSE '' END) END PREVIOUS_MONTH ,CASE WHEN C.MONTH_TYPE = 'M2' AND A.TARGET <> A.COLLECTION THEN C.MONTH_TYPE ELSE ' ' END AS MONTH_TYPE ,JOMPAY.CUST_BILL_PAY_REF_NO1 JOMPAY ,CASE WHEN PAYMODE.MODE_ID = 130 THEN '' ELSE SUBSTR(CARD.CUST_ORI_CRC_NO,-4) END AS CRC ,CASE WHEN PAYMODE.MODE_ID = 130 THEN '' ELSE CUST_CRC_EXPR END AS CRC_EXPR ,B.ACC_DEBT_SUB_CURR_OTSTND_AGING AGING ,B.ACC_DEBT_SUB_OPNG_OTSTND_AGING OPNG_AGING ,A.DEPT_CODE AS CM_CODE ,A.GRP_CODE AS SCMCODE ,A.SRV_TYPE AS srvType FROM REP0087S A INNER JOIN PAY0053S B ON A.ACC_DEBT_SUB_ID = B.ACC_DEBT_SUB_ID LEFT JOIN PAY0042D C ON A.SALES_ORD_ID = C.SALES_ORD_ID AND A.THSMNTH = C.BANK_DTL_ID LEFT JOIN PAY0041D D ON D.CTRL_ID = C.BANK_DTL_CTRL_ID LEFT JOIN SYS0094M SYS ON SYS.CODE = C.BANK_DTL_APPR_CODE AND SYS.IND IN ('SUCCESS','INSFUNDS','INVCARD','CHGCARD','TRXNOTALW') LEFT JOIN PAY0042D E ON A.SALES_ORD_ID = E.SALES_ORD_ID AND A.PRVMNTH = E.BANK_DTL_ID LEFT JOIN PAY0041D F ON D.CTRL_ID = E.BANK_DTL_CTRL_ID LEFT JOIN SYS0094M SYC ON SYC.CODE = E.BANK_DTL_APPR_CODE AND SYC.IND IN ('SUCCESS','INSFUNDS','INVCARD','CHGCARD','TRXNOTALW') JOIN ORG0001D G ON G.MEM_CODE = A.CODY_CODE JOIN SYS0038M SYD ON SYD.STUS_CODE_ID = G.STUS JOIN SAL0001D SOM ON SOM.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SAL0024D JOMPAY ON JOMPAY.CUST_BILL_ID = SOM.CUST_BILL_ID LEFT JOIN SAL0074D PAYMODE ON PAYMODE.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SAL0028D CARD ON CARD.CUST_CRC_ID = PAYMODE.CUST_CRC_ID
LEFT JOIN SAL0095D H ON H.SRV_SALES_ORD_ID = A.SALES_ORD_ID JOIN SAL0081D I ON H.SRV_MEM_PAC_ID = I.SRV_CNTRCT_PAC_ID AND I.SRV_CNTRCT_PAC_ID =2
WHERE 1=1
AND A.ORG_CODE = #{orgCode}
AND A.GRP_CODE = #{grpCode}
AND A.DEPT_CODE = #{deptCode}
AND A.CODY_CODE = #{memCode}
AND B.ACC_DEBT_SUB_CURR_OTSTND_AGING = #{cmbOutstandMonth}
AND B.ACC_DEBT_SUB_OPNG_OTSTND_AGING = #{cmbOpeningAging}
AND A.CUST_TYPE_ID = #{cmbCustTypeId}
AND (B.ACC_DEBT_SUB_CURR_ADJ + B.ACC_DEBT_SUB_CURR_COLCT + B.ACC_DEBT_SUB_OPNG_COLCT_TRGET ) = 0
AND (B.ACC_DEBT_SUB_CURR_ADJ + B.ACC_DEBT_SUB_CURR_COLCT + B.ACC_DEBT_SUB_OPNG_COLCT_TRGET ) > 0 AND B.ACC_DEBT_SUB_CURR_COLCT < 0
AND B.ACC_DEBT_SUB_CURR_COLCT = 0
AND A.PAY_MODE_ID = #{cmbPaymode}
AND A.BS_MONTH = #{cmbBsMonth}
AND A.REN_MEM LIKE '%' || #{renMem} || '%'
AND A.MEM_TYPE = #{memType}
AND H.SRV_EXPR_DT >= SYSDATE
AND H.SRV_EXPR_DT < SYSDATE
AND A.SRV_TYPE = #{cmbSrvType}

SELECT A.SALES_ORD_ID SALES_ORD_ID, A.SALES_ORD_NO SALES_ORD_NO, F.CODE ORD_STUS, (A.PV_MONTH || '/' || PV_YEAR) NET_MONTH , C.NAME CUST_NAME, G.STK_DESC STK_DESC, H.CODE_NAME PAY_MODE, TO_CHAR(ROUND(I.ACC_DEBT_SUB_CURR_OTSTND_AGING, 2),'fm999999999999999990.00') AGING_MONTH, TO_CHAR(ROUND(SUM(J.RENT_AMT), 2),'fm999999999999999990.00') OUTSTANDING_AMT,
E.MEM_CODE HP_CODE, A.DEPT_CODE DEPT_CODE, A.GRP_CODE GRP_CODE FROM SAL0001D A LEFT JOIN SAL0002D B ON B.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SAL0029D C ON C.CUST_ID = A.CUST_ID LEFT JOIN SAL0074D D ON D.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN ORG0001D E ON E.MEM_ID = A.MEM_ID LEFT JOIN SYS0038M F ON F.STUS_CODE_ID = A.STUS_CODE_ID LEFT JOIN SYS0026M G ON G.STK_ID = B.ITM_STK_ID LEFT JOIN SYS0013M H ON H.CODE_ID = D.MODE_ID LEFT JOIN PAY0053S I ON I.ACC_DEBT_ORD_ID = A.SALES_ORD_ID LEFT JOIN PAY0022D J ON J.RENT_SO_ID = A.SALES_ORD_ID LEFT JOIN SAL0046D SM ON SM.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN ( SELECT DISTINCT S.ORD_ID FROM REP0010S S JOIN SAL0001D SS ON SS.SALES_ORD_ID = S.ORD_ID JOIN SAL0029D C ON C.CUST_ID = SS.CUST_ID JOIN ORG0001D O ON O.NRIC = C.NRIC WHERE O.MEM_TYPE = 1 UNION ALL SELECT DISTINCT S.ORD_ID FROM REP0010S S JOIN SAL0001D SS ON SS.SALES_ORD_ID = S.ORD_ID JOIN SAL0017D P ON P.PROMO_ID = SS.PROMO_ID WHERE NVL(P.EMP_CHK,0) != 0 UNION ALL SELECT S.ORD_ID FROM CMM0095T S )EXC ON EXC.ORD_ID = A.SALES_ORD_ID WHERE 1=1
AND A.ORG_CODE = #{orgCode}
AND A.GRP_CODE = #{grpCode}
AND A.DEPT_CODE = #{deptCode}
AND E.MEM_CODE = #{memCode}
AND A.APP_TYPE_ID = #{cmbAppType}
AND C.TYPE_ID = #{cmbCustTypeId}
AND SM.STUS_CODE_ID = #{cmbInstallStatus}
AND SM.INSTALL_DT BETWEEN TO_DATE(#{prdStartDt}, 'DD/MM/YYYY') AND TO_DATE(#{prdEndDt}||' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND TO_DATE(SM.INSTALL_DT) >= TO_DATE(ADD_MONTHS(SYSDATE, -24))
GROUP BY A.SALES_ORD_ID, A.SALES_ORD_NO , F.CODE , (A.PV_MONTH || '/' || PV_YEAR) , C.NAME , G.STK_DESC , H.CODE_NAME , I.ACC_DEBT_SUB_CURR_OTSTND_AGING , E.MEM_CODE , A.DEPT_CODE , A.GRP_CODE

