mapper.select:
  		SELECT * FROM (
			SELECT PAD_NO FROM PAY0333M WHERE SALES_ORD_NO = #{salesOrdNo} AND STUS_CODE_ID = 1 ORDER BY PAD_ID DESC
		) WHERE ROWNUM = 1

SELECT p0333m.PAD_ID,p0333m.PAD_NO,p0333m.SALES_ORD_NO,TO_CHAR(p0333m.CRT_DT,'DD/MM/YYYY') AS CREATED_DATE, TO_CHAR(p0333m.UPT_DT,'DD/MM/YYYY') AS UPDATED_DATE, CASE WHEN p0333m.FAIL_REASON_CODE IS NULL THEN '' ELSE ('[' ||(s0047m2.USER_NAME) || '] ' || TO_CHAR(p0333m.UPT_DT,'DD/MM/YYYY HH12:MI:SS AM') || '>' || s0032m.CODE || '-' || s0032m.RESN_DESC || '>>' || p0333m.REMARKS) END AS REMARKS, s0038M.NAME AS STATUS_DESC,s0029d.NAME AS CUST_NAME FROM PAY0333M p0333m INNER JOIN SYS0038M s0038m ON s0038m.STUS_CODE_ID = p0333m.STUS_CODE_ID INNER JOIN SAL0029D s0029d ON s0029d.CUST_ID = p0333m.CUST_ID LEFT JOIN SYS0047M s0047m ON s0047m.USER_ID = p0333m.CRT_BY LEFT JOIN SYS0032M s0032m ON s0032m.RESN_ID = p0333m.FAIL_REASON_CODE LEFT JOIN SYS0047M s0047m2 ON s0047m2.USER_ID = p0333m.UPT_BY WHERE 1=1
AND TO_DATE(TO_CHAR(p0333m.CRT_DT, 'DD/MM/YYYY'),'DD/MM/YYYY') >= TO_DATE(#{dateFrom},'DD/MM/YYYY') AND TO_DATE(p0333m.CRT_DT,'DD/MM/YYYY') <= TO_DATE(#{dateTo},'DD/MM/YYYY')
AND p0333m.SALES_ORD_NO = #{salesOrdNo}
AND p0333m.STUS_CODE_ID = #{statusCode}
AND s0047m.USER_NAME = #{userName} ORDER BY p0333m.CRT_DT DESC

SELECT p0333m.PAD_ID, p0333m.PAD_NO, TO_CHAR(p0333m.CRT_DT,'DD/MM/YYYY') AS KEY_IN_DATE, TO_CHAR(p0333m.CRT_DT,'HH12:MI:SS AM') AS KEY_IN_TIMESTAMP, s0038m.NAME AS STATUS_DESC, p0333m.SALES_ORD_NO, s0029d.NAME AS CUST_NAME, s0032m.CODE || '-' || s0032m.RESN_DESC AS RESN_DESC, p0333m.REMARKS, s0047m1.USER_NAME AS CREATOR, s0005m.CODE ||' '|| s0005m.NAME AS USER_BRANCH, TO_CHAR(p0333m.UPT_DT,'DD/MM/YYYY HH12:MI:SS AM') ||','|| (s0047m2.USER_NAME) AS LAST_UPDATED_DATE, p0333m.CUST_CRC_ID, TO_CHAR(s0028d.CUST_CRC_CRT_DT,'DD/MM/YYYY') AS CRC_APPLY_DATE FROM PAY0333M p0333m LEFT JOIN SAL0029D s0029d ON p0333m.CUST_ID = s0029d.CUST_ID LEFT JOIN SYS0038M s0038m ON p0333m.STUS_CODE_ID = s0038m.STUS_CODE_ID LEFT JOIN SYS0047M s0047m1 ON p0333m.CRT_BY = s0047m1.USER_ID LEFT JOIN SYS0047M s0047m2 ON p0333m.UPT_BY = s0047m2.USER_ID LEFT JOIN SYS0005M s0005m ON p0333m.USER_BRANCH = s0005m.BRNCH_ID LEFT JOIN ORG0001D o0001d ON s0047m1.USER_NAME = o0001d.MEM_CODE LEFT JOIN ORG0005D o0005d ON o0005d.MEM_ID = o0001d.MEM_ID LEFT JOIN SYS0032M s0032m ON s0032m.RESN_ID = p0333m.FAIL_REASON_CODE LEFT JOIN SAL0028D s0028d ON s0028d.CUST_CRC_ID = p0333m.CUST_CRC_ID WHERE 1=1
AND p0333m.PAD_NO = #{padNo}
AND p0333m.SALES_ORD_NO = #{orderNo}
AND TO_DATE(TO_CHAR(p0333m.CRT_DT, 'DD/MM/YYYY'),'DD/MM/YYYY') BETWEEN TO_DATE(#{requestDateFrom},'DD/MM/YYYY') AND TO_DATE(#{requestDateTo},'DD/MM/YYYY')
AND p0333m.CRT_DT BETWEEN TO_DATE(#{requestDateFrom} || #{requestTimeFrom}, 'dd/mm/yyyy hh24:mi') AND TO_DATE(#{requestDateTo} || #{requestTimeTo}, 'dd/mm/yyyy hh24:mi')
AND TO_DATE(TO_CHAR(s0028d.CUST_CRC_CRT_DT, 'DD/MM/YYYY'),'DD/MM/YYYY') BETWEEN TO_DATE(#{crcRequestDateFrom},'DD/MM/YYYY') AND TO_DATE(#{crcRequestDateTo},'DD/MM/YYYY')
AND s0029d.NAME LIKE '%'||UPPER(#{custName})||'%'
AND s0029d.NRIC = #{nricCompanyNo}
AND o0005d.LAST_ORG_CODE = #{orgCode}
AND o0005d.LAST_GRP_CODE = #{grpCode}
AND o0005d.LAST_DEPT_CODE = #{deptCode}
AND p0333m.STUS_CODE_ID in
#{item}
AND s0005m.BRNCH_ID in
#{item}
AND p0333m.REGN_ID in
#{item}
AND s0028d.CARD_TYPE_ID in
#{item}
AND o0001d.MEM_ID = #{memId}
AND o0001d.MEM_CODE = #{memCode}
ORDER BY p0333m.CRT_DT DESC

