mapper.select:
  		SELECT * FROM (
			SELECT PAD_NO FROM PAY0333M WHERE SALES_ORD_NO = #{salesOrdNo} AND STUS_CODE_ID = 1 ORDER BY PAD_ID DESC
		) WHERE ROWNUM = 1

SELECT p0333m.PAD_ID,p0333m.PAD_NO,p0333m.SALES_ORD_NO,TO_CHAR(p0333m.CRT_DT,'DD/MM/YYYY') AS CREATED_DATE, TO_CHAR(p0333m.UPT_DT,'DD/MM/YYYY') AS UPDATED_DATE, CASE WHEN p0333m.FAIL_REASON_CODE IS NULL THEN '' ELSE ('[' ||(s0047m2.USER_NAME) || '] ' || TO_CHAR(p0333m.UPT_DT,'DD/MM/YYYY HH12:MI:SS AM') || '>' || s0032m.CODE || '-' || s0032m.RESN_DESC || '>>' || p0333m.REMARKS) END AS REMARKS, s0038M.NAME AS STATUS_DESC,s0029d.NAME AS CUST_NAME FROM PAY0333M p0333m INNER JOIN SYS0038M s0038m ON s0038m.STUS_CODE_ID = p0333m.STUS_CODE_ID INNER JOIN SAL0029D s0029d ON s0029d.CUST_ID = p0333m.CUST_ID LEFT JOIN SYS0047M s0047m ON s0047m.USER_ID = p0333m.CRT_BY LEFT JOIN SYS0032M s0032m ON s0032m.RESN_ID = p0333m.FAIL_REASON_CODE LEFT JOIN SYS0047M s0047m2 ON s0047m2.USER_ID = p0333m.UPT_BY WHERE 1=1
AND TO_DATE(TO_CHAR(p0333m.CRT_DT, 'DD/MM/YYYY'),'DD/MM/YYYY') >= TO_DATE(#{dateFrom},'DD/MM/YYYY') AND TO_DATE(p0333m.CRT_DT,'DD/MM/YYYY') <= TO_DATE(#{dateTo},'DD/MM/YYYY')
AND p0333m.SALES_ORD_NO = #{salesOrdNo}
AND p0333m.STUS_CODE_ID = #{statusCode}
AND s0047m.USER_NAME = #{userName} ORDER BY p0333m.CRT_DT DESC

