mapper.select:
    	SELECT utl_raw.cast_to_varchar2(utl_encode.base64_encode(dbms_lob.substr(SIGN_IMG))) AS SIGN_IMG
    	FROM PAY0333M p0333m
		WHERE p0333m.PAD_ID = #{padId}

/* [com.coway.trust.biz.payment.autodebit.service.impl.AutoDebitMapper.selectCustomerList] 20190916 - KR JAEMAJEM */ SELECT CUST_ID , NAME , ADDR , TYPE_ID_NAME , CUST_ADD_ID , NRIC FROM ( SELECT A.CUST_ID AS CUST_ID , A.NAME AS NAME , A.NRIC , B.ADDR_DTL ||' '|| NVL(B.STREET, '') ||' '|| NVL(C.AREA, '') ||' '|| NVL(C.POSTCODE, '') ||' '|| NVL(C.CITY,'') ||' '|| NVL(C.STATE,'') ||' '|| NVL(C.COUNTRY,'') AS ADDR , CASE WHEN E.CODE_NAME IS NOT NULL THEN D.CODE_NAME||'('||E.CODE_NAME||')' ELSE D.CODE_NAME END AS TYPE_ID_NAME , B.CUST_ADD_ID AS CUST_ADD_ID , ROW_NUMBER() OVER(PARTITION BY B.CUST_ID ORDER BY B.CUST_ADD_ID DESC)AS ROW_NUMBER FROM SAL0029D A INNER JOIN SAL0023D B ON A.CUST_ID = B.CUST_ID AND B.STUS_CODE_ID = 9 INNER JOIN SYS0064M C ON B.AREA_ID = C.AREA_ID INNER JOIN SYS0013M D ON A.TYPE_ID = D.CODE_ID LEFT OUTER JOIN SYS0013M E ON A.CORP_TYPE_ID = E.CODE_ID AND A.CORP_TYPE_ID != 0 WHERE A.STUS_CODE_ID != 8
AND A.TYPE_ID = #{typeId}
AND REPLACE(UPPER(A.NAME), ' ', '') = UPPER(#{selectKeyword})
AND UPPER(A.NRIC) = UPPER(#{selectKeyword})
AND UPPER(A.NRIC) = UPPER(#{selectKeyword})
) WHERE ROW_NUMBER = 1

