mapper.insert:
INSERT INTO SAL0323T (FAIL_RESN_ID,CUST_ID,NRIC,ORDER_NO,CARD_HOLDER,CARD_NO,CARD_EXP,CARD_TYPE,BANKNAME,CRC_TYPE) select case when b.cust_id is null then 3488 when b1.cust_id is null then 3490 when b2.cust_id is null then 3489 when length(replace(a.CardNumber,'-','')) <> 16 then 3491 when c1.bank_id is null then 3492 when d1.code_id is null then 3493 else 0 end as fail_resn, a.CowayCustId, regexp_replace(a.NricOrPassport, '(\d+)\.(\d+)(\d)(.+)', '\1\2'), a.OrderNo, a.FullName, replace(a.CardNumber,'-',''), TO_CHAR(TO_DATE(#{item.9},'Mon-YY'),'MMYY'), case when a.CardType = 'Credit Card' then 1241 when a.CardType = 'Debit Card' then 1240 end as Card_Type, c1.bank_id, a.crc_type FROM (
select #{item.0} CowayCustId, #{item.1} NricOrPassport, #{item.2} Status, #{item.3} DocumentPath, #{item.4} DateCreated, #{item.5} OrderNo, #{item.6} FullName, #{item.7} AccHolderName, #{item.8} CardNumber, #{item.9} CardEXP, #{item.10} CardType, #{item.11} BankName, CASE WHEN substr(#{item.8},0,1) = 4 THEN 112 WHEN substr(#{item.8},0,1) = 5 THEN 111 ELSE 0 END crc_type FROM DUAL
) a left join sal0029d b on b.cust_id = a.CowayCustId left join sal0029d b1 on b1.cust_id = a.CowayCustId and b1.nric like '%'||regexp_replace(a.NricOrPassport, '(\d+)\.(\d+)(\d)(.+)', '\1\2')||'%' left join sal0029d b2 on UPPER(trim(b2.name)) = a.FullName and b2.cust_id = a.CowayCustId left join sys0109m c on SELFCARE_BANK_NAME = A.BankName left join sys0004m c1 on c1.code = c.etrust_bank_name left join sys0013m d1 on a.crc_type = d1.code_id

mapper.select:
SELECT A.BATCH_TOKENIZE_NO Batch_NO, COUNT(B.BATCH_TOKENIZE_ID) AS Total_Item ,A.BATCH_TOKENIZE_ID Batch_ID, D.NAME Batch_Status,A.CRT_DT Crt_Dt, C.USER_Name as Crt_User FROM SAL0319M A JOIN SAL0320D B ON A.BATCH_TOKENIZE_ID = B.BATCH_TOKENIZE_ID JOIN SYS0047M C ON A.CRT_USER_ID = C.USER_ID JOIN SYS0038M D ON A.STATUS = D.STUS_CODE_ID WHERE 1=1
AND A.BATCH_TOKENIZE_NO = #{batchNo}
AND A.CRT_DT >= TO_DATE(#{createStDate}, 'DD/MM/YYYY')
AND A.CRT_DT <= TO_DATE(#{createEnDate}, 'DD/MM/YYYY') +1
AND C.USER_Name = #{crtUserName}
GROUP BY A.BATCH_TOKENIZE_NO,D.NAME,A.CRT_DT,C.USER_Name,A.BATCH_TOKENIZE_ID ORDER BY A.BATCH_TOKENIZE_ID DESC

mapper.update:
UPDATE SAL0320D
    SET CARD_No = substr(Card_No,0,6)||'******'||substr(Card_No,-4,4)
    WHERE   BATCH_TOKENIZE_ID = #{batchid}
