AND Extent1.STUS_ID IN
#{item}
AND Extent1.PAY_MODE_ID IN
#{item}
AND Extent1.BANK_ID IN
#{item}
AND Extent1.CLM_DAY IN
#{item}
AND Extent1.DEBT_DT BETWEEN TO_DATE(#{debitDt1},'DD/MM/YYYY') AND TO_DATE(#{debitDt2},'DD/MM/YYYY')
AND NVL(Extent1.SCHDUL_CLM_DT,TO_DATE('19000101','YYYYMMDD')) >= TO_DATE(#{createDt1},'DD/MM/YYYY') AND NVL(Extent1.SCHDUL_CLM_DT,TO_DATE('19000101','YYYYMMDD')) < TO_DATE(#{createDt2},'DD/MM/YYYY') + 1
ORDER BY Extent1.SCHDUL_CLM_ID

SELECT Extent1.SCHDUL_ID SCHDUL_ID , Extent1.STUS_ID STUS_ID , Extent1.PAY_MODE_ID PAY_MODE_ID , Extent1.BANK_ID BANK_ID , Extent1.CLM_DAY CLM_DAY , Extent1.SCHDUL_DT SCHDUL_DT , Extent1.CRT_USER_ID CRT_USER_ID , Extent1.CRT_DT CRT_DT , Extent1.UPD_USER_ID UPD_USER_ID , Extent1.UPD_DT UPD_DT , Extent1.DEBT_DT DEBT_DT , Extent2.CODE CODE , Extent2.CODE_NAME CODE_NAME , CASE WHEN ( Extent3.BANK_ID IS NOT NULL ) THEN Extent3.CODE ELSE '' END BANK_CODE , CASE WHEN ( Extent3.BANK_ID IS NOT NULL ) THEN Extent3.NAME ELSE '' END BANK_NAME , CASE WHEN ( Extent4.USER_ID IS NOT NULL ) THEN Extent4.USER_NAME ELSE '' END CRT_USER_NAME , CASE WHEN ( Extent5.USER_ID IS NOT NULL ) THEN Extent5.USER_NAME ELSE '' END UPD_USER_NAME FROM PAY0039D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.PAY_MODE_ID LEFT JOIN SYS0004M Extent3 ON Extent3.BANK_ID = Extent1.BANK_ID LEFT JOIN SYS0047M Extent4 ON Extent4.USER_ID = Extent1.CRT_USER_ID LEFT JOIN SYS0047M Extent5 ON Extent5.USER_ID = Extent1.UPD_USER_ID WHERE Extent1.STUS_ID = 1 AND Extent1.SCHDUL_DT BETWEEN TRUNC(TO_DATE(#{batchMonth},'MM/YYYY')) AND LAST_DAY(TRUNC(TO_DATE(#{batchMonth},'MM/YYYY')))
AND Extent1.PAY_MODE_ID = #{claimType}
AND Extent1.BANK_ID = #{issueBank}
ORDER BY Extent1.SCHDUL_DT ASC, Extent1.SCHDUL_ID DESC

SELECT CTRL_ID, FILE_NO, FILE_PATH, FILE_NAME, FILE_PATH || FILE_NAME AS FILE_FULL_PATH FROM PAY0267M WHERE CTRL_ID = #{ctrlId}
AND FILE_TYPE = #{fileType}
