mapper.select:
SELECT Project1.ENROLLUPDATEID as enrollUpdateId , TO_CHAR(Project1.CRT_DT, 'DD-MM-YYYY HH:MI:SS AM') as createDate, Project1.CRT_USER_ID as userId, Project1.C1 as creator, Project1.TOT_FAIL as totalFail, Project1.TOT_SUCCES as totalSuccess, Project1.TOT_UP_DT as totalUpdate, Project1.TYPE_ID as typeId, Project1.CODE_NAME as type FROM ( SELECT Project1.ENROLLUPDATEID ENROLLUPDATEID , Project1.TYPE_ID TYPE_ID , Project1.CRT_DT CRT_DT , Project1.CRT_USER_ID CRT_USER_ID , Project1.TOT_UP_DT TOT_UP_DT , Project1.TOT_SUCCES TOT_SUCCES , Project1.TOT_FAIL TOT_FAIL , Project1.CODE_NAME CODE_NAME , Project1.C1 C1 , ROW_NUMBER() OVER ( ORDER BY Project1.ENROLLUPDATEID ASC ) ROW_NUMBER FROM ( SELECT Extent1.ENRL_UPD_ID ENROLLUPDATEID , Extent1.TYPE_ID TYPE_ID , Extent1.CRT_DT CRT_DT , Extent1.CRT_USER_ID CRT_USER_ID , Extent1.TOT_UP_DT TOT_UP_DT , Extent1.TOT_SUCCES TOT_SUCCES , Extent1.TOT_FAIL TOT_FAIL , Extent2.CODE_NAME CODE_NAME , CASE WHEN ( Extent3.USER_ID IS NOT NULL ) THEN Extent3.USER_NAME ELSE ' ' END C1 FROM PAY0058D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.TYPE_ID LEFT JOIN SYS0047M Extent3 ON Extent3.USER_ID = Extent1.CRT_USER_ID ) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0
AND Project1.ENROLLUPDATEID = #{udtBatchId}
AND Project1.C1 = #{creator}
AND Project1.CRT_DT >= TO_DATE(#{createDate1}, 'DD-MM-YYYY hhmiss') AND Project1.CRT_DT < TO_DATE(#{createDate2}, 'DD-MM-YYYY hhmiss') + 1
AND Project1.TYPE_ID = #{updateType}
ORDER BY Project1.ENROLLUPDATEID ASC
