mapper.select:
SELECT enrlId, crtDt, crtUserId, C1, debtDtFrom, createDate, debtDtTo, bankId, code, name FROM ( SELECT Project1.ENRL_ID enrlId , Project1.CRT_DT crtDt , Project1.CRT_USER_ID crtUserId , Project1.C1 C1 , Project1.DEBT_DT_FROM debtDtFrom, TO_CHAR(Project1.CRT_DT, 'DD-MM-YYYY HH:MI:SS AM') AS createDate, Project1.DEBT_DT_TO debtDtTo , Project1.BANK_ID bankId , Project1.CODE code , Project1.NAME name FROM ( SELECT Project1.ENRL_ID ENRL_ID , Project1.BANK_ID BANK_ID , Project1.DEBT_DT_FROM DEBT_DT_FROM , Project1.CRT_USER_ID CRT_USER_ID , Project1.CRT_DT CRT_DT , Project1.DEBT_DT_TO DEBT_DT_TO , Project1.CODE CODE , Project1.NAME NAME , Project1.C1 C1 , ROW_NUMBER() OVER ( ORDER BY Project1.ENRL_ID ASC ) ROW_NUMBER FROM ( SELECT Extent1.ENRL_ID ENRL_ID , Extent1.BANK_ID BANK_ID , Extent1.DEBT_DT_FROM DEBT_DT_FROM , Extent1.CRT_USER_ID CRT_USER_ID , Extent1.CRT_DT CRT_DT , Extent1.DEBT_DT_TO DEBT_DT_TO , Extent2.CODE CODE , Extent2.NAME NAME , CASE WHEN ( Extent3.USER_ID IS NOT NULL ) THEN Extent3.USER_NAME ELSE 'N' END C1 FROM PAY0056D Extent1 JOIN SYS0004M Extent2 ON Extent2.BANK_ID = Extent1.BANK_ID LEFT JOIN SYS0047M Extent3 ON Extent3.USER_ID = Extent1.CRT_USER_ID WHERE 1=1
AND Extent1.ENRL_ID = #{enrollID}
AND ( (CASE WHEN ( Extent3.USER_ID IS NOT NULL ) THEN Extent3.USER_NAME ELSE 'N' END) = #{creator} )
AND Extent1.BANK_ID = #{cmbIssueBank}
AND Extent1.CRT_DT >= TO_DATE(TO_CHAR(TO_DATE(#{rdpCreateDateFr},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') AND Extent1.CRT_DT < TO_DATE(TO_CHAR(TO_DATE(#{rdpCreateDateTo},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') + 1
) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.ENRL_ID ASC )
