mapper.select:
SELECT C.CUST_ID, C.NAME AS CUST_NAME, A.PRO_FORMA_ID, A.REF_NO, B.SALES_ORD_ID, B.SALES_ORD_NO, B.BRNCH_ID, D.CODE || '-' || D.NAME AS BRNCH_NAME, A.SALESMAN_CODE, F.DEPT_CODE, A.CRT_DT, CASE WHEN A.PACKAGE_TYPE = '12' THEN '1 Year Advance' ELSE '2 Year Advance' END AS PACK_TYPE, A.PACKAGE_TYPE DISC_PERIOD, TO_CHAR(A.ADV_START_DT, 'MM/YYYY') || ' - ' || TO_CHAR(A.ADV_END_DT, 'MM/YYYY') AS ADV_PERIOD, TO_CHAR(A.ADV_START_DT, 'MM/YYYY') AS ADV_START_DT, TO_CHAR(A.ADV_END_DT, 'MM/YYYY') AS ADV_END_DT, A.STUS_ID, G.NAME AS STATUS, H.USER_NAME AS CRT_USER, A.ADV_PAY_KEY, B.MTH_RENT_AMT, A.PACK_PRICE FROM PAY0334D A JOIN SAL0001D B ON A.SALES_ORD_ID = B.SALES_ORD_ID JOIN SAL0029D C ON B.CUST_ID = C.CUST_ID JOIN SYS0005M D ON D.BRNCH_ID = B.BRNCH_ID JOIN ORG0001D E ON A.SALESMAN_CODE = E.MEM_CODE JOIN ORG1001V F ON E.MEM_ID = F.MEM_ID LEFT JOIN SYS0038M G ON A.STUS_ID = G.STUS_CODE_ID JOIN SYS0047M H ON A.CRT_USER_ID = H.USER_ID WHERE 1=1 AND A.STUS_ID != 8
AND A.PRO_FORMA_ID = #{proFormaId}
AND A.REF_NO = UPPER(#{refNo})
AND B.BRNCH_ID IN
#{item}
AND A.ADV_PAY_KEY = UPPER(#{advPayId})
AND B.CUST_ID IN
#{item}
AND F.DEPT_CODE = #{deptCode}
AND H.USER_NAME LIKE '%'||UPPER(#{creator})||'%'
AND B.SALES_ORD_NO = #{ordNo}
AND A.CRT_DT BETWEEN TO_DATE(#{startDt}, 'DD/MM/YYYY') AND TO_DATE(#{endDt}, 'DD/MM/YYYY')
AND A.STUS_ID IN
#{item}
AND A.PACKAGE_TYPE = #{packageType}
ORDER BY A.PRO_FORMA_ID ASC

mapper.insert:
    INSERT INTO PAY0334D
    (
	    PRO_FORMA_ID
	    , REF_NO
	    , SALES_ORD_ID
	    , STUS_ID
	    , PACKAGE_TYPE
	    , SALESMAN_CODE
	    , ADV_START_DT
	    , ADV_END_DT
	    , TOTAL_AMT
	    , PACK_PRICE
	    , REMARK
	    , DISC_PROMO
	    , CRT_USER_ID
	    , CRT_DT
	    )
	    VALUES
	    (
	    PAY0334D_PRO_FORMA_ID_SEQ.NEXTVAL
	    , #{pfNo}
	    , #{orderId}
	    , #{stus}
	    , #{packType}
	    , #{memCode}
	    , TO_DATE(#{adStartDt}, 'MM/YYYY')
	    , TO_DATE(#{adEndDt}, 'MM/YYYY')
	    , #{totalAmt}
	    , #{packPrice}
	    , #{remark}
	    , #{discount}
	    , #{creator}
	    , SYSDATE
    )

mapper.update:
UPDATE PAY0334D
SET
    STUS_ID = #{stusId},
    UPD_DT = SYSDATE,
    UPD_USER_ID = #{updator}
WHERE PRO_FORMA_ID = #{proFormaId}
