mapper.select:
SELECT COUNT(*) FROM SAL0001D Extent1 JOIN SAL0029D Extent2 ON Extent1.CUST_ID = Extent2.CUST_ID LEFT JOIN ORG0001D Extent3 ON Extent1.MEM_ID = Extent3.MEM_ID LEFT JOIN ORG1001V Extent4 ON Extent3.MEM_ID = Extent4.MEM_ID LEFT JOIN SYS0013M Extent5 ON Extent1.APP_TYPE_ID = Extent5.CODE_ID WHERE Extent1.APP_TYPE_ID IN (67,68) AND Extent1.STUS_CODE_ID = 4
AND Extent1.SALES_ORD_NO = #{orderNo}
AND Extent2.NAME LIKE '%' || #{custName} || '%' ESCAPE '~'
AND Extent1.APP_TYPE_ID IN (
'${appType[index]}'
)

SELECT DISTINCT Extent1.BILL_ID BILL_ID , Extent1.CUST_NAME CUST_NAME , Extent2.SO_NO SO_NO , Extent3.CUST_BILL_GRP_NO CUST_BILL_GRP_NO , 1 C1 , TO_NUMBER(Extent1.PV_YEAR) YEAR , TO_NUMBER(Extent1.PV_MONTH) MONTH , Extent7.EMAIL EMAIL FROM PAY0082D Extent1 JOIN PAY0083D Extent2 ON ( Extent2.BILL_ID = Extent1.BILL_ID ) AND ( TO_NUMBER(Extent2.PV_MONTH) = TO_NUMBER(Extent1.PV_MONTH) ) AND ( TO_NUMBER(Extent2.PV_YEAR) = TO_NUMBER(Extent1.PV_YEAR) ) JOIN SAL0024D Extent3 ON Extent1.BILL_ID = Extent3.CUST_BILL_ID LEFT JOIN ( SELECT StatementCompanyRelatedOrder.PV_MONTH PV_MONTH , StatementCompanyRelatedOrder.PV_YEAR PV_YEAR , StatementCompanyRelatedOrder.BILL_ID BILL_ID , StatementCompanyRelatedOrder.SO_ID SO_ID , StatementCompanyRelatedOrder.CRT_DT CRT_DT FROM PAY0101M StatementCompanyRelatedOrder ) Extent4 ON Extent1.BILL_ID = Extent4.BILL_ID LEFT JOIN SAL0001D Extent5 ON Extent4.SO_ID = Extent5.SALES_ORD_ID LEFT JOIN SAL0029D Extent7 ON Extent7.CUST_ID = Extent5.CUST_ID LEFT JOIN PAY0022D Extent6 ON ( Extent5.SALES_ORD_ID = Extent6.RENT_SO_ID ) AND ( Extent6.RENT_AMT > 0 ) WHERE 1=1
AND Extent3.CUST_BILL_GRP_NO = #{billNo}
AND Extent2.SO_NO = #{orderNo}
AND ( Extent1.CUST_NAME LIKE '%' || #{custName} || '%' ESCAPE '~' )
AND Extent1.PV_MONTH = #{sMonth}
AND Extent1.PV_YEAR = #{sYear}
ORDER BY Extent1.BILL_ID DESC

SELECT Extent1.SALES_ORD_ID, Extent1.CUST_BILL_ID, Extent1.SALES_ORD_NO, CASE WHEN ( Extent1.SALES_DT IS NOT NULL ) THEN TO_CHAR(Extent1.SALES_DT, 'YYYY-MM-DD HH24:MI:SS') ELSE '1900-01-01 00:00:00' END OrderDate, Extent8.STK_DESC, Extent1.CUST_ID, Extent4.NAME CUST_NM, CASE WHEN LENGTH(Extent4.NRIC) = 12 THEN REGEXP_REPLACE(SUBSTR(Extent4.NRIC, 0,8),'[A-Za-z0-9]','x') || SUBSTR(Extent4.NRIC, 9,12) ELSE Extent4.NRIC END NRIC, Extent1.APP_TYPE_ID, Extent2.CODE_NAME APP_TYPE_NM, Extent1.PV_YEAR, Extent1.PV_MONTH, Extent1.STUS_CODE_ID STUS_CODE_ID, Extent3.STUS_CODE_ID STUS_CODE_NM, CASE WHEN Extent6.REBATE_AMT_PER_INSTLMT IS NOT NULL THEN Extent1.MTH_RENT_AMT - Extent6.REBATE_AMT_PER_INSTLMT ELSE Extent1.MTH_RENT_AMT END MTH_RENT_AMT, Extent1.TOT_AMT, 0 AS BALANCE, '' AS LAST_PAYMENT, Extent1.MEM_ID MEM_ID, NVL(Extent5.RPF_CNT,0) AS RPF_CNT, NVL(Extent5.RPF_CHARGE,0) AS RPF_CHARGE, 0 AS BTN_CHECK FROM SAL0001D Extent1 JOIN SAL0002D Extent9 ON Extent1.SALES_ORD_ID = Extent9.SALES_ORD_ID JOIN ORG0001D Extent7 ON Extent7.MEM_ID = Extent1.MEM_ID JOIN SYS0026M Extent8 ON Extent8.STK_ID = Extent9.ITM_STK_ID JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.APP_TYPE_ID JOIN SAL0071D Extent3 ON Extent3.SALES_ORD_ID = Extent1.SALES_ORD_ID JOIN SAL0029D Extent4 ON Extent4.CUST_ID = Extent1.CUST_ID LEFT JOIN ( SELECT RENT_SO_ID, COUNT(1) AS RPF_CNT, NVL(SUM(RENT_AMT),0) AS RPF_CHARGE FROM PAY0022D WHERE RENT_DOC_TYPE_ID = 161 AND RENT_AMT >= 0 GROUP BY RENT_SO_ID ) Extent5 ON Extent5.RENT_SO_ID = Extent1.SALES_ORD_ID LEFT JOIN pay0286d extent6 ON extent6.ord_id = extent1.sales_ord_id WHERE 1=1 AND Extent1.APP_TYPE_ID = 66
AND Extent1.SALES_ORD_NO = #{orderNo}
AND Extent1.SALES_DT >= TO_DATE(#{orderDateFrom}, 'YYYY-MM-DD HH24:MI:SS')
AND Extent1.SALES_DT <= TO_DATE(#{orderDateTo}, 'YYYY-MM-DD HH24:MI:SS')
AND Extent1.BRNCH_ID IN (
'${keyInBranchList[index]}'
)
AND Extent1.CUST_ID = #{custId}
AND UPPER(Extent4.NAME) LIKE UPPER('%' || #{custName}|| '%' )
AND Extent4.NRIC LIKE '%' || #{custIC} || '%' ESCAPE '~'
AND Extent6.ITM_STK_ID = #{productId}
AND Extent7.MEM_CODE = #{memberCode}
AND Extent3.STUS_CODE_ID IN (
'${rentStatus[index]}'
)
ORDER BY Extent1.SALES_ORD_NO

SELECT Extent1.SALES_ORD_ID , Extent1.SALES_ORD_NO , Extent2.STK_RETN_ID , Extent2.RETN_NO , NVL(TO_NUMBER(TO_CHAR(Extent2.UPD_DT,'YYYY')),1900) AS BILL_DT_YEAR, NVL(TO_NUMBER(TO_CHAR(Extent2.UPD_DT,'MM')),1) AS BILL_DT_MONTH, NVL(Extent2.UPD_DT,TO_DATE('1900-01-01','YYYY-MM-DD')) AS BILL_DT FROM SAL0001D Extent1 JOIN LOG0038D Extent2 ON Extent2.SALES_ORD_ID = Extent1.SALES_ORD_ID AND Extent2.STUS_CODE_ID = 4 AND Extent2.TYPE_ID = 296 WHERE 1=1
AND Extent1.SALES_ORD_NO = #{orderNo}
AND Extent2.RETN_NO = #{billNo}
ORDER BY Extent2.STK_RETN_ID DESC

SELECT A.CUST_ID, CM.NAME AS CUSTOMER_NAME, CC.NAME AS CONTACT_PIC, TRIM(X.ADDR_DTL) ADD_1, TRIM(X.STREET) ADD_2, UPPER(Y.AREA ||' '|| Y.POSTCODE ||' '|| Y.CITY ||' '|| Y.STATE ||' '|| Y.COUNTRY) ADD_3, TO_CHAR(TAX_INVC_REF_DT,'DD/MM/YYYY') AS INVOICE_DATE, B.INVC_ITM_ORD_NO AS ORDER_NO , TRIM(E.ADDR_DTL) ||' '|| TRIM(E.STREET) ||' '|| UPPER(MA.AREA ||' '|| MA.POSTCODE ||' '|| MA.CITY ||' '|| MA.STATE ||' '|| MA.COUNTRY) INSTL_ADDR, STK_CODE ||' '|| STK_DESC PRODUCT , IR.INSTALL_DT, B.INVC_ITM_INSTLMT_NO||'/'||CNT.CNTRCT_RENTAL_PRIOD AS BILL_PERIOD, B.INVC_ITM_PO_NO, C.TAX_INVC_REF_NO AS DOC_NO, B.INVC_ITM_RENTAL_FEE AS INVOICE_AMOUNT , LPAD(TO_CHAR(TO_NUMBER(A.CUST_ID)),8,'0')||'_'||TO_CHAR(TAX_INVC_REF_DT,'MMYYYY') AS REF_NO FROM SAL0001D A JOIN SAL0002D SS ON SS.SALES_ORD_ID = A.SALES_ORD_ID JOIN SAL0029D CM ON CM.CUST_ID = A.CUST_ID LEFT JOIN SAL0027D CC ON CC.CUST_CNTC_ID = A.CUST_CNT_ID AND CC.STUS_CODE_ID = 9 LEFT JOIN SAL0003D CNT ON CNT.CNTRCT_SALES_ORD_ID = A.SALES_ORD_ID JOIN SYS0026M STK ON STK_ID = SS.ITM_STK_ID JOIN PAY0030D B ON B.INVC_ITM_ORD_NO = A.SALES_ORD_NO JOIN PAY0029D C ON C.TAX_INVC_ID = B.TAX_INVC_ID JOIN SAL0045D D ON D.SALES_ORD_ID = A.SALES_ORD_ID JOIN SAL0023D E ON E.CUST_ADD_ID=D.ADD_ID LEFT JOIN SAL0046D IE ON IE.INSTALL_ENTRY_ID = FN_GET_SAL0046D_MAX_ID( A.SALES_ORD_ID ,'2' ) LEFT JOIN SAL0047D IR ON IR.ENTRY_ID = IE.INSTALL_ENTRY_ID LEFT OUTER JOIN SYS0064M MA ON MA.AREA_ID = E.AREA_ID AND MA.STATUS_ID = 1 JOIN SAL0023D X ON X.CUST_ADD_ID= (SELECT MIN(CUST_ADD_ID) FROM SAL0001D WHERE 1=1 AND EXTRACT (DAY FROM TAX_INVC_REF_DT ) = 1
AND CUST_ID = #{custID}
AND STUS_CODE_ID= 4 ) LEFT OUTER JOIN SYS0064M Y ON Y.AREA_ID = X.AREA_ID AND Y.STATUS_ID = 1 WHERE 1=1
AND A.CUST_ID = #{custID}
AND TO_CHAR(TAX_INVC_REF_DT,'MMYYYY') = TO_CHAR( TO_DATE( #{invoiceRefDate},'MM/YYYY'),'MMYYYY')
AND C.TAX_INVC_REF_NO = #{refNo}
ORDER BY DOC_NO
