mapper.select:
/* [com.coway.trust.biz.payment.cardpayment.service.impl.CardTransactionListMapper.selectCardTransactionList] 20190930 - KR OHK */ SELECT T.CRC_STATE_ID , T.CRC_STATE_CRT_DT , T.CRC_STATE_REF_NO , T.CRC_STATE_ACC_ID , T.CRC_STATE_ACC_NM , T.CRC_TRNSC_MID , T.CRCMODE , T.TENURE , T.CRC_TRNSC_DT , T.CRC_TRNSC_NO , T.CRC_TRNSC_APPV , T.CRC_GROS_AMT , T.CRC_TOT_BC_AMT , T.CRC_GST_AMT , T.CRC_NET_AMT , T.CRC_TRNSC_IS_MTCH , T.CRC_STATE_MAPPING_DT , T.CRC_STATE_MAPPING_ID , T.PAY_ITM_AMT , T.SALES_ORD_NO , T.OR_NO , T.TRX_ID FROM (SELECT DISTINCT A.CRC_STATE_ID , TO_CHAR(A.CRC_STATE_CRT_DT , 'DD/MM/YYYY') CRC_STATE_CRT_DT , A.CRC_STATE_REF_NO , A.CRC_STATE_ACC_ID , H.ACC_CODE || ' - ' || H.ACC_DESC CRC_STATE_ACC_NM , B.CRC_TRNSC_MID , CASE WHEN E.PAY_ITM_CARD_MODE_ID IS NOT NULL THEN CASE WHEN 1298 = E.PAY_ITM_CARD_MODE_ID THEN 'On' WHEN 1299 = E.PAY_ITM_CARD_MODE_ID THEN 'Off' WHEN 1300 = E.PAY_ITM_CARD_MODE_ID THEN 'PNP' ELSE G.CODE_NAME END WHEN 1 = E.PAY_ITM_IS_ONLINE THEN 'On' WHEN 0 = E.PAY_ITM_IS_ONLINE THEN 'Off' ELSE '-' END CRCMODE , E.TENURE , B.CRC_TRNSC_DT , B.CRC_TRNSC_NO , B.CRC_TRNSC_APPV , B.CRC_GROS_AMT , B.CRC_TOT_BC_AMT , B.CRC_GST_AMT , B.CRC_NET_AMT , DECODE(C.CRC_STATE_MAPPING_ID, NULL, 'Unknown', 'Matched') AS CRC_TRNSC_IS_MTCH , C.CRC_STATE_MAPPING_DT , C.CRC_STATE_MAPPING_ID , E.PAY_ITM_AMT , NVL(F.SALES_ORD_NO, J.SRV_ORD_NO) SALES_ORD_NO , D.OR_NO , C.TRX_ID , E.PAY_ITM_ID FROM PAY0075D A INNER JOIN PAY0076D B ON A.CRC_STATE_ID = B.CRC_STATE_ID LEFT OUTER JOIN PAY0252T C ON B.CRC_TRNSC_ID = C.CRC_STATE_MAPPING_ID LEFT OUTER JOIN PAY0064D D ON C.PAY_ID = D.PAY_ID LEFT OUTER JOIN PAY0065D E ON C.PAY_ID = E.PAY_ID AND C.PAY_ITM_ID = E.PAY_ITM_ID LEFT OUTER JOIN SAL0001D F ON D.SALES_ORD_ID = F.SALES_ORD_ID LEFT OUTER JOIN SYS0013M G ON E.PAY_ITM_CARD_MODE_ID = G.CODE_ID LEFT OUTER JOIN SYS0001M H ON A.CRC_STATE_ACC_ID = H.ACC_ID AND H.ACC_STUS_ID = 1 AND H.IS_PAY_CRC = 1 LEFT OUTER JOIN PAY0007D I ON D.BILL_ID = I.BILL_ID LEFT OUTER JOIN SAL0225D J ON I.BILL_SO_ID = J.SRV_ORD_ID WHERE 1 = 1
AND (F.SALES_ORD_NO = #{salesOrdNo} OR J.SRV_ORD_NO = #{salesOrdNo})
AND A.CRC_STATE_REF_NO = #{stateNo}
AND B.CRC_TRNSC_MID = #{mid}
AND A.CRC_STATE_CRT_DT >=TO_DATE(#{stateDateFr}, 'DD/MM/YYYY') AND A.CRC_STATE_CRT_DT <TO_DATE(#{stateDateTo}, 'DD/MM/YYYY') +1
AND B.CRC_TRNSC_DT >=TO_DATE(#{transDateFr}, 'DD/MM/YYYY') AND B.CRC_TRNSC_DT <TO_DATE(#{transDateTo}, 'DD/MM/YYYY') +1
AND H.ACC_ID = #{bankAccount}
AND E.PAY_ITM_MODE_ID = #{paymentMode}
) T ORDER BY T.CRC_STATE_ID DESC, T.CRC_STATE_CRT_DT DESC, T.CRC_TRNSC_APPV ASC, T.OR_NO ASC
