mapper.select:
SELECT TO_CHAR(SubBank.F_TRNSC_DT, 'DD/MM/YYYY') AS F_TRNSC_DT, SubCrc.CRC_TRNSC_MID, SubCrc.NET_AMT, SubBank.F_TRNSC_ID, SubBank.CREDIT_AMT, SubBank.CODE_ID, SubCrc.CRC_STATE_ID, TO_CHAR((SELECT sysdate FROM DUAL), 'DD/MM/YYYY') AS MAPPING_DATE FROM( -- CREDIT CARD STATEMENT SELECT B.CRC_STATE_ID, B.CRC_TRNSC_MID, D.CODE_ID, B.CRC_TRNSC_DT, (CASE WHEN SUM(B.CRC_TOT_NET_AMT) != 0 THEN SUM(B.CRC_TOT_NET_AMT) ELSE SUM(B.CRC_NET_AMT) END) AS NET_AMT FROM PAY0075D A JOIN PAY0076D B ON A.CRC_STATE_ID = B.CRC_STATE_ID LEFT JOIN SYS0001M C ON A.CRC_STATE_ACC_ID = C.ACC_ID JOIN SYS0013M D ON C.ACC_CODE = D.CODE_NAME WHERE 1=1 AND D.CODE_MASTER_ID = 384 AND KEYIN_STUS_ID = 4 AND B.BANK_MAPPING_STUS_ID = 1 GROUP BY B.CRC_STATE_ID, B.CRC_TRNSC_MID, D.CODE_ID, B.CRC_TRNSC_DT) SubCrc JOIN (--BANK STATEMENT SELECT B.F_TRNSC_ID, B.F_TRNSC_REF4 AS MID, SUM(B.F_TRNSC_CRDIT_AMT) AS CREDIT_AMT, B.F_TRNSC_DT, D.CODE_ID FROM PAY0079D A JOIN PAY0081D B ON A.F_BANK_JRNL_ID = B.F_BANK_JRNL_ID LEFT JOIN SYS0001M C ON A.F_BANK_JRNL_ACC_ID = C.ACC_ID JOIN SYS0013M D ON C.ACC_CODE = D.CODE WHERE B.F_TRNSC_REM = 'CRC' AND B.CRC_MAPPING_STUS_ID = 1 AND D.CODE_MASTER_ID = 384 GROUP BY B.F_TRNSC_ID, B.F_TRNSC_REF4, B.F_TRNSC_DT, D.CODE_ID ) SubBank ON SubCrc.CRC_TRNSC_MID = SubBank.MID and SubCrc.NET_AMT = SubBank.CREDIT_AMT and SubCrc.CODE_ID = SubBank.CODE_ID WHERE 1=1
AND SubBank.F_TRNSC_DT BETWEEN TO_DATE(#{tranDateFr}, 'DD/MM/YYYY') AND TO_DATE(#{tranDateTo}, 'DD/MM/YYYY')
AND SubBank.CODE_ID = #{bankAccount}

mapper.update:
        UPDATE PAY0076D
		SET BANK_MAPPING_STUS_ID = 4,
		BANK_MAPPING_DT = TO_DATE(#{mappingDate}, 'DD/MM/YYYY'),
		BANK_MAPPING_USER_ID = #{userId},
		BANK_STATE_MAPPING_ID =#{fTrnscId}
	    WHERE CRC_STATE_ID = #{crcStateId}

UPDATE PAY0081D
SET CRC_MAPPING_STUS_ID = 4,
CRC_MAPPING_DT = TO_DATE(#{mappingDate}, 'DD/MM/YYYY'),
CRC_MAPPING_USER_ID = #{userId}
WHERE F_TRNSC_ID = #{fTrnscId}

mapper.select:
SELECT CRC_TRNSC_ID, CRC_TRNSC_APPV, CRC_GROS_AMT
FROM PAY0076D
WHERE CRC_STATE_ID = #{crcStateId}
