mapper.select:
SELECT Extent1.SALES_ORD_ID, Extent1.CUST_BILL_ID, Extent1.SALES_ORD_NO, Extent1.CUST_ID, Extent4.NAME CUST_NM, Extent1.APP_TYPE_ID, Extent2.CODE_NAME APP_TYPE_NM, Extent1.PV_YEAR, Extent1.PV_MONTH, Extent1.STUS_CODE_ID STUS_CODE_ID, Extent3.STUS_CODE_ID STUS_CODE_NM, Extent1.MTH_RENT_AMT, Extent1.TOT_AMT, 0 AS BALANCE, '' AS LAST_PAYMENT, Extent1.MEM_ID MEM_ID, NVL(Extent5.RPF_CNT,0) AS RPF_CNT, NVL(Extent5.RPF_CHARGE,0) AS RPF_CHARGE FROM SAL0001D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.APP_TYPE_ID JOIN SAL0071D Extent3 ON Extent3.SALES_ORD_ID = Extent1.SALES_ORD_ID JOIN SAL0029D Extent4 ON Extent4.CUST_ID = Extent1.CUST_ID LEFT JOIN ( SELECT RENT_SO_ID, COUNT(1) AS RPF_CNT, NVL(SUM(RENT_AMT),0) AS RPF_CHARGE FROM PAY0022D WHERE RENT_DOC_TYPE_ID = 161 AND RENT_AMT >= 0 GROUP BY RENT_SO_ID ) Extent5 ON Extent5.RENT_SO_ID = Extent1.SALES_ORD_ID WHERE Extent1.APP_TYPE_ID = 66
AND Extent1.SALES_ORD_ID = #{orderId}
AND Extent1.CUST_BILL_ID = #{billGrpId}
ORDER BY Extent1.SALES_ORD_NO

{
    call SP_GET_OTSTND_MBRSH_BY_ORD_ID(#{orderId} , #{resultMemSvm, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultMembershipMap})            
}
