SELECT Extent1.SALES_ORD_ID, Extent1.CUST_BILL_ID, Extent1.SALES_ORD_NO, Extent1.CUST_ID, Extent4.NAME CUST_NM, Extent1.APP_TYPE_ID, Extent2.CODE_NAME APP_TYPE_NM, Extent1.PV_YEAR, Extent1.PV_MONTH, Extent1.STUS_CODE_ID STUS_CODE_ID, Extent3.STUS_CODE_ID STUS_CODE_NM, CASE WHEN Extent6.REBATE_AMT_PER_INSTLMT IS NOT NULL THEN Extent1.MTH_RENT_AMT - Extent6.REBATE_AMT_PER_INSTLMT ELSE Extent1.MTH_RENT_AMT END MTH_RENT_AMT, Extent1.TOT_AMT, 0 AS BALANCE, '' AS LAST_PAYMENT, Extent1.MEM_ID MEM_ID, NVL(Extent5.RPF_CNT,0) AS RPF_CNT, NVL(Extent5.RPF_CHARGE,0) AS RPF_CHARGE, 0 AS BTN_CHECK FROM SAL0001D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.APP_TYPE_ID JOIN SAL0071D Extent3 ON Extent3.SALES_ORD_ID = Extent1.SALES_ORD_ID JOIN SAL0029D Extent4 ON Extent4.CUST_ID = Extent1.CUST_ID LEFT JOIN ( SELECT RENT_SO_ID, COUNT(1) AS RPF_CNT, NVL(SUM(RENT_AMT),0) AS RPF_CHARGE FROM PAY0022D WHERE RENT_DOC_TYPE_ID = 161 AND RENT_AMT >= 0 GROUP BY RENT_SO_ID ) Extent5 ON Extent5.RENT_SO_ID = Extent1.SALES_ORD_ID LEFT JOIN pay0286d extent6 ON extent6.ord_id = extent1.sales_ord_id WHERE Extent1.APP_TYPE_ID = 66
AND Extent1.SALES_ORD_ID = #{orderId}
AND Extent1.CUST_BILL_ID = #{billGrpId}
ORDER BY Extent1.SALES_ORD_NO

{
    call SP_GET_OTSTND_MBRSH_BY_ORD_ID(#{orderId} , #{resultMemSvm, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultMembershipMap})
}

SELECT Extent1.SRV_CNTRCT_ID SRV_CNTRCT_ID , Extent1.SRV_CNTRCT_REF_NO SRV_CNTRCT_REF_NO , Extent1.SRV_CNTRCT_NET_MONTH SRV_CNTRCT_NET_MONTH , Extent1.SRV_CNTRCT_NET_YEAR SRV_CNTRCT_NET_YEAR , Extent3.SALES_ORD_ID SALES_ORD_ID , Extent3.SALES_ORD_NO SALES_ORD_NO , Extent2.CNTRCT_RENTAL_STUS CNTRCT_RENTAL_STUS , Extent1.SRV_CNTRCT_STUS_ID SRV_CNTRCT_STUS_ID , Extent5.CODE STATUS_CODE , Extent4.CUST_ID CUST_ID , Extent4.NAME CUST_NAME , CASE WHEN ( Extent3.CUST_BILL_ID IS NOT NULL ) THEN Extent3.CUST_BILL_ID ELSE 0 END CUST_BILL_ID , Extent1.SRV_CNTRCT_RENTAL SRV_CNTRCT_RENTAL , Extent1.SRV_CNTRCT_EXP_FILTER SRV_CNTRCT_EXP_FILTER , TO_DATE('1900-01-01','YYYY-MM-DD') AS LAST_PAY_DT, 0 AS BTN_CHECK FROM SAL0077D Extent1 JOIN SAL0078D Extent2 ON Extent2.SRV_CNTRCT_ID = Extent1.SRV_CNTRCT_ID JOIN SAL0001D Extent3 ON Extent3.SALES_ORD_ID = Extent1.SRV_CNTRCT_ORD_ID JOIN SAL0029D Extent4 ON Extent4.CUST_ID = Extent3.CUST_ID JOIN SYS0038M Extent5 ON Extent5.STUS_CODE_ID = Extent1.SRV_CNTRCT_STUS_ID WHERE 1=1
AND Extent1.SRV_CNTRCT_ID = #{srvcId}
AND Extent3.CUST_BILL_ID = #{billGrpId}

