SELECT TAX_INVC_ID , TAX_INVC_TYPE , TAX_INVC_REF_NO , TAX_INVC_GRP_NO , TAX_INVC_SVC_NO , TAX_INVC_REF_DT , MONTH , YEAR , TAX_INVC_CUST_NAME , TAX_INVC_AMT_DUE FROM ( SELECT TAX_INVC_ID AS TAX_INVC_ID , TAX_INVC_TYPE AS TAX_INVC_TYPE , TAX_INVC_REF_NO AS TAX_INVC_REF_NO , '' AS TAX_INVC_GRP_NO , TAX_INVC_SVC_NO AS TAX_INVC_SVC_NO , TAX_INVC_REF_DT AS TAX_INVC_REF_DT , TO_NUMBER(TO_CHAR(TAX_INVC_REF_DT,'MM')) AS MONTH , TO_NUMBER(TO_CHAR(TAX_INVC_REF_DT,'YYYY')) AS YEAR , TAX_INVC_CUST_NAME AS TAX_INVC_CUST_NAME , TAX_INVC_AMT_DUE AS TAX_INVC_AMT_DUE FROM PAY0031D UNION ALL SELECT TAX_INVC_ID, 2, TAX_INVC_REF_NO, TAX_INVC_GRP_NO, '' TAX_INVC_SVC_NO , TAX_INVC_REF_DT, TAX_INVC_MONTH, TAX_INVC_YEAR, TAX_INVC_CUST_NAME, TAX_INVC_AMT_DUE FROM PAY0029D UNION ALL SELECT Extent3.TAX_INVC_ID TAX_INVC_ID , 1 AS TAX_INVC_TYPE , Extent3.TAX_INVC_REF_NO TAX_INVC_REF_NO , Extent4.INVC_ITM_ORD_NO INVC_ITM_ORD_NO , '' , Extent3.TAX_INVC_REF_DT TAX_INVC_REF_DT , TO_NUMBER(TO_CHAR(Extent3.TAX_INVC_REF_DT,'MM')) AS MONTH , TO_NUMBER(TO_CHAR(Extent3.TAX_INVC_REF_DT,'YYYY')) AS YEAR , Extent3.TAX_INVC_CUST_NAME TAX_INVC_CUST_NAME , Extent3.TAX_INVC_AMT_DUE TAX_INVC_AMT_DUE FROM PAY0033D Extent3 LEFT JOIN PAY0034D Extent4 ON Extent3.TAX_INVC_ID = Extent4.TAX_INVC_ID ) WHERE TAX_INVC_TYPE = #{invoiceType}
AND TAX_INVC_REF_NO = #{invoiceNo}
AND TAX_INVC_SVC_NO = #{serviceNo}
AND TAX_INVC_GRP_NO = #{orderNo}
AND MONTH = #{month}
AND YEAR = #{year}
AND TAX_INVC_CUST_NAME LIKE '%' || UPPER(#{customerName}) ||'%'

SELECT Project1.SRV_CNTRCT_ID SRV_CNTRCT_ID , Project1.SRV_CNTRCT_REF_NO SRV_CNTRCT_REF_NO , Project1.SRV_CNTRCT_NET_MONTH SRV_CNTRCT_NET_MONTH , Project1.SRV_CNTRCT_NET_YEAR SRV_CNTRCT_NET_YEAR , Project1.SRV_CNTRCT_PAC_DESC SRV_CNTRCT_PAC_DESC , Project1.SALES_ORD_NO SALES_ORD_NO , Project1.SRV_PRD_START_DT SRV_PRD_START_DT , Project1.SRV_PRD_EXPR_DT SRV_PRD_EXPR_DT , Project1.CNTRCT_RENTAL_STUS CNTRCT_RENTAL_STUS , Project1.SRV_CNTRCT_STUS_ID SRV_CNTRCT_STUS_ID , Project1.CODE CODE , Project1.USER_NAME USER_NAME , Project1.SRV_CNTRCT_CRT_DT SRV_CNTRCT_CRT_DT , Project1.CUST_ID CUST_ID , Project1.NAME NAME , Project1.NRIC NRIC , Project1.SALES_ORD_ID SALES_ORD_ID, Project1.CUST_BILL_ID CUST_BILL_ID FROM ( SELECT Extent1.SRV_CNTRCT_ID SRV_CNTRCT_ID , Extent1.SRV_CNTRCT_REF_NO SRV_CNTRCT_REF_NO , Extent1.SRV_CNTRCT_STUS_ID SRV_CNTRCT_STUS_ID , Extent1.SRV_CNTRCT_NET_MONTH SRV_CNTRCT_NET_MONTH , Extent1.SRV_CNTRCT_NET_YEAR SRV_CNTRCT_NET_YEAR , Extent1.SRV_CNTRCT_CRT_DT SRV_CNTRCT_CRT_DT , Extent2.CNTRCT_RENTAL_STUS CNTRCT_RENTAL_STUS , Extent3.SALES_ORD_ID SALES_ORD_ID , Extent3.SALES_ORD_NO SALES_ORD_NO , Extent3.CUST_BILL_ID CUST_BILL_ID , Extent4.CUST_ID CUST_ID , Extent4.NAME NAME , Extent4.NRIC NRIC , Extent5.SRV_CNTRCT_PAC_DESC SRV_CNTRCT_PAC_DESC , Extent6.USER_NAME USER_NAME , Extent7.SRV_PRD_START_DT SRV_PRD_START_DT , Extent7.SRV_PRD_EXPR_DT SRV_PRD_EXPR_DT , Extent8.CODE CODE FROM SAL0077D Extent1 JOIN SAL0078D Extent2 ON Extent2.SRV_CNTRCT_ID = Extent1.SRV_CNTRCT_ID JOIN SAL0001D Extent3 ON Extent3.SALES_ORD_ID = Extent1.SRV_CNTRCT_ORD_ID JOIN SAL0029D Extent4 ON Extent4.CUST_ID = Extent3.CUST_ID JOIN SAL0081D Extent5 ON Extent5.SRV_CNTRCT_PAC_ID = Extent1.SRV_CNTRCT_PCKG_ID JOIN SYS0047M Extent6 ON Extent6.USER_ID = Extent1.SRV_CNTRCT_CRT_USER_ID JOIN SAL0088D Extent7 ON Extent7.SRV_PRD_CNTRCT_ID = Extent1.SRV_CNTRCT_ID JOIN SYS0038M Extent8 ON Extent8.STUS_CODE_ID = Extent1.SRV_CNTRCT_STUS_ID WHERE 1=1
AND Extent2.CNTRCT_RENTAL_STUS IN
#{item}
AND Extent1.SRV_CNTRCT_REF_NO = #{contractRefNo}
AND TO_CHAR(Extent1.SRV_CNTRCT_CRT_DT, 'DD/MM/YYYY') = #{salesDate}
AND Extent3.SALES_ORD_NO = #{orderNo}
AND Extent4.CUST_ID = #{custId}
AND Extent6.USER_NAME = '%' || UPPER(#{custName}) ||'%'
AND Extent4.NRIC = #{custNric}
) Project1 ORDER BY Project1.SRV_CNTRCT_ID ASC
