SELECT Project1.ITM_ID ITM_ID , Project1.BATCH_ID BATCH_ID , Project1.ITM_STUS_ID ITM_STUS_ID , Project1.TRX_ID TRX_ID , Project1.AMT AMT , Project1.PAY_MODE_ID PAY_MODE_ID , Project1.IS_ONLINE IS_ONLINE , Project1.ORI_CC_NO ORI_CC_NO , Project1.CC_HOLDER_NAME CC_HOLDER_NAME , Project1.CC_EXPR CC_EXPR , Project1.BANK_ID BANK_ID , Project1.APPV_NO APPV_NO , Project1.REF_NO REF_NO , Project1.UPD_DT UPD_DT , Project1.UPD_USER_ID UPD_USER_ID , Project1.MID MID , Project1.BRNCH_ID BRNCH_ID , TO_CHAR(Project1.PAY_DT, 'DD-MM-YYYY') PAY_DT , Project1.ACC_ID ACC_ID , Project1.BATCH_NO BATCH_NO , Project1.BATCH_STUS_ID BATCH_STUS_ID , Project1.CRT_DT CRT_DT , Project1.CRT_USER_ID CRT_USER_ID , Project1.NAME NAME , Project1.CODE CODE , Project1.ACC_CODE ACC_CODE , Project1.CODE2 CODE2 , Project1.NAME2 NAME2 , Project1.CC_TYPE_ID CC_TYPE_ID , Project1.CODE_NAME1 CODE_NAME1 , Project1.USER_NAME1 USER_NAME1 , Project1.USER_NAME2 USER_NAME2 , Project1.REF_DT REF_DT FROM ( SELECT Filter1.ITM_ID ITM_ID , Filter1.BATCHID1 BATCH_ID , Filter1.ITM_STUS_ID ITM_STUS_ID , Filter1.TRX_ID TRX_ID , Filter1.AMT AMT , Filter1.PAY_MODE_ID PAY_MODE_ID , Filter1.IS_ONLINE IS_ONLINE , Filter1.ORI_CC_NO ORI_CC_NO , Filter1.CC_HOLDER_NAME CC_HOLDER_NAME , Filter1.CC_EXPR CC_EXPR , Filter1.BANKID1 BANK_ID , Filter1.APPV_NO APPV_NO , Filter1.REF_NO REF_NO , Filter1.UPDATED1 UPD_DT , Filter1.UPDATOR1 UPD_USER_ID , Filter1.MID MID , Filter1.BRANCHID1 BRNCH_ID , Filter1.PAY_DT PAY_DT , Filter1.ACC_ID ACC_ID , Filter1.BATCH_NO BATCH_NO , Filter1.BATCH_STUS_ID BATCH_STUS_ID , Filter1.CREATED1 CRT_DT , Filter1.CREATOR1 CRT_USER_ID , Filter1.NAME1 NAME , Filter1.CODE1 CODE , CASE WHEN ( Extent10.ACC_ID IS NOT NULL ) THEN Extent10.ACC_CODE ELSE ' ' END ACC_CODE , CASE WHEN ( Filter1.BANKID2 IS NOT NULL ) THEN Filter1.CODE2 ELSE ' ' END CODE2 , Filter1.CODE3 || ' - ' || Filter1.NAME2 NAME2 , CASE WHEN ( Filter1.CC_TYPE_ID IS NOT NULL ) THEN Filter1.CC_TYPE_ID ELSE 0 END CC_TYPE_ID , CASE WHEN ( Filter1.CODEID1 IS NOT NULL ) THEN Filter1.CODENAME1 ELSE ' ' END CODE_NAME1 , CASE WHEN ( Filter1.USERID1 IS NOT NULL ) THEN Filter1.USERNAME1 ELSE ' ' END USER_NAME1 , CASE WHEN ( Filter1.USERID2 IS NOT NULL ) THEN Filter1.USERNAME2 ELSE ' ' END USER_NAME2 , CASE WHEN ( TO_CHAR(Filter1.REF_DT, 'YYYY-MM-DD HH:MI:SS') IS NOT NULL ) THEN TO_CHAR(Filter1.REF_DT, 'YYYY-MM-DD HH:MI:SS')ELSE '1900-01-01 00:00:00' END REF_DT , ROW_NUMBER() OVER ( ORDER BY Filter1.ITM_ID ASC ) ROW_NUMBER FROM ( SELECT Extent1.ITM_ID ITM_ID , Extent1.BATCH_ID BATCHID1 , Extent1.ITM_STUS_ID ITM_STUS_ID , Extent1.TRX_ID TRX_ID , Extent1.AMT AMT , Extent1.PAY_MODE_ID PAY_MODE_ID , Extent1.IS_ONLINE IS_ONLINE , Extent1.ORI_CC_NO ORI_CC_NO , Extent1.CC_TYPE_ID CC_TYPE_ID , Extent1.CC_HOLDER_NAME CC_HOLDER_NAME , Extent1.CC_EXPR CC_EXPR , Extent1.BANK_ID BANKID1 , Extent1.REF_DT REF_DT , Extent1.APPV_NO APPV_NO , Extent1.REF_NO REF_NO , Extent1.UPD_DT UPDATED1 , Extent1.UPD_USER_ID UPDATOR1 , Extent1.MID MID , Extent1.BRNCH_ID BRANCHID1 , Extent1.PAY_DT PAY_DT , Extent1.ACC_ID ACC_ID , Extent2.BATCH_NO BATCH_NO , Extent2.BATCH_STUS_ID BATCH_STUS_ID , Extent2.CRT_DT CREATED1 , Extent2.CRT_USER_ID CREATOR1 , Extent3.NAME NAME1 , Extent4.CODE CODE1 , Extent5.CODE CODE3 , Extent5.NAME NAME2 , Extent6.BANK_ID BANKID2 , Extent6.CODE CODE2 , Extent7.CODE_ID CODEID1 , Extent7.CODE_NAME CODENAME1 , Extent8.USER_ID USERID1 , Extent8.USER_NAME USERNAME1 , Extent9.USER_ID USERID2 , Extent9.USER_NAME USERNAME2 FROM PAY0097D Extent1 JOIN PAY0170M Extent2 ON Extent2.BATCH_ID = Extent1.BATCH_ID JOIN SYS0038M Extent3 ON Extent3.STUS_CODE_ID = Extent1.ITM_STUS_ID JOIN SYS0013M Extent4 ON Extent4.CODE_ID = Extent1.PAY_MODE_ID JOIN SYS0005M Extent5 ON Extent5.BRNCH_ID = Extent1.BRNCH_ID LEFT JOIN SYS0004M Extent6 ON Extent6.BANK_ID = Extent1.BANK_ID LEFT JOIN SYS0013M Extent7 ON Extent7.CODE_ID = Extent1.CC_TYPE_ID LEFT JOIN SYS0047M Extent8 ON Extent8.USER_ID = Extent2.CRT_USER_ID LEFT JOIN SYS0047M Extent9 ON Extent9.USER_ID = Extent1.UPD_USER_ID WHERE 1=1
AND IS_ONLINE IN (
'${rOnline[index]}'
)
AND BATCH_STUS_ID IN (
'${rBatchStatus[index]}'
)
AND ITM_STUS_ID IN (
'${rItemStatus[index]}'
)
) Filter1 LEFT JOIN SYS0001M Extent10 ON Extent10.ACC_ID = Filter1.ACC_ID WHERE Filter1.PAY_MODE_ID = #{rPaymode}
AND Filter1.MID = #{rMerchantId}
AND Filter1.REF_DT = TO_DATE(#{rRefDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND Filter1.ORI_CC_NO LIKE '%' || #{rCreditCardNo} || '%'
AND Filter1.CC_HOLDER_NAME LIKE '%' || #{rCardHolderName} || '%'
AND Filter1.CC_TYPE_ID = #{rCreditCardType}
AND Filter1.BANKID1 = #{rIssueBank}
AND Filter1.PAY_DT >= TO_DATE(#{rPayStartDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') AND Filter1.PAY_DT < TO_DATE(#{rPayEndDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') + 1
AND Filter1.CREATED1 >= TO_DATE(#{rBatchStartDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') AND Filter1.CREATED1 < TO_DATE(#{rBatchEndDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') + 1
AND (CASE WHEN (Filter1.USERID1 IS NOT NULL) THEN Filter1.USERNAME1 ELSE '' END) = #{rBatchCreator}
AND Filter1.BATCH_NO = #{rBatchNo}
) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.ITM_ID ASC

SELECT GroupBy1.K1 TRX_ID , GroupBy1.K2 PAY_MODE_ID , GroupBy1.K3 IS_ONLINE , GroupBy1.K4 ORI_CC_NO , GroupBy1.K5 CC_TYPE_ID , GroupBy1.K6 CC_HOLDER_NAME , GroupBy1.K7 CC_EXPR , GroupBy1.K8 BANK_ID , GroupBy1.K9 APPV_NO , GroupBy1.K10 REF_NO , GroupBy1.K11 BRNCH_ID , GroupBy1.K12 MID , GroupBy1.K13 ACC_ID , GroupBy1.K14 ACC_CODE , GroupBy1.K16 PAY_DT , Extent2.CODE CODE , 0 C2 , 0 C3 , 1 C4 , '' C5 , CASE WHEN ( GroupBy1.A1 IS NOT NULL ) THEN TO_NUMBER(GroupBy1.A1) ELSE TO_NUMBER(0) END AMT , CASE WHEN ( Extent3.CODE_ID IS NOT NULL ) THEN Extent3.CODE_NAME ELSE '' END CODE_NAME , CASE WHEN ( Extent4.BANK_ID IS NOT NULL ) THEN Extent4.CODE ELSE '' END CODE2, CASE WHEN ( GroupBy1.K15 IS NOT NULL ) THEN GroupBy1.K15 ELSE '1900-01-01 00:00:00'END REF_DT FROM ( SELECT Project1.TRX_ID K1 , Project1.PAY_MODE_ID K2 , Project1.IS_ONLINE K3 , Project1.ORI_CC_NO K4 , Project1.CC_TYPE_ID K5 , Project1.CC_HOLDER_NAME K6 , Project1.CC_EXPR K7 , Project1.BANK_ID K8 , Project1.APPV_NO K9 , Project1.REF_NO K10 , Project1.BRNCH_ID K11 , Project1.MID K12 , Project1.ACC_ID K13 , Project1.ACC_CODE K14 , Project1.REF_DT K15 , Project1.PAY_DT K16 , SUM(Project1.AMT) A1 FROM ( SELECT Extent1.TRX_ID TRX_ID , Extent1.PAY_MODE_ID PAY_MODE_ID , Extent1.IS_ONLINE IS_ONLINE , Extent1.ORI_CC_NO ORI_CC_NO , Extent1.CC_TYPE_ID CC_TYPE_ID , Extent1.CC_HOLDER_NAME CC_HOLDER_NAME , Extent1.CC_EXPR CC_EXPR , Extent1.BANK_ID BANK_ID , Extent1.APPV_NO APPV_NO , Extent1.REF_NO REF_NO , Extent1.BRNCH_ID BRNCH_ID , Extent1.MID MID , Extent1.AMT AMT , Extent1.ACC_ID ACC_ID , Extent1.ACC_CODE ACC_CODE , CASE WHEN ( Extent1.REF_DT IS NOT NULL ) THEN TO_CHAR(Extent1.REF_DT, 'YYYY-MM-DD HH:MI:SS') ELSE '1900-01-01 00:00:00' END REF_DT , CASE WHEN ( Extent1.PAY_DT IS NOT NULL ) THEN TO_CHAR(Extent1.PAY_DT, 'YYYY-MM-DD HH:MI:SS') ELSE '1900-01-01 00:00:00' END PAY_DT FROM ( SELECT vPayDocumentControlWaitingItem.TRX_ID TRX_ID , vPayDocumentControlWaitingItem.PAY_MODE_ID PAY_MODE_ID , vPayDocumentControlWaitingItem.IS_ONLINE IS_ONLINE , vPayDocumentControlWaitingItem.ORI_CC_NO ORI_CC_NO , vPayDocumentControlWaitingItem.CC_TYPE_ID CC_TYPE_ID , vPayDocumentControlWaitingItem.CC_HOLDER_NAME CC_HOLDER_NAME , vPayDocumentControlWaitingItem.CC_EXPR CC_EXPR , vPayDocumentControlWaitingItem.BANK_ID BANK_ID , vPayDocumentControlWaitingItem.REF_DT REF_DT , vPayDocumentControlWaitingItem.APPV_NO APPV_NO , vPayDocumentControlWaitingItem.REF_NO REF_NO , vPayDocumentControlWaitingItem.BRNCH_ID BRNCH_ID , vPayDocumentControlWaitingItem.MID MID , vPayDocumentControlWaitingItem.AMT AMT , vPayDocumentControlWaitingItem.PAY_DT PAY_DT , vPayDocumentControlWaitingItem.ACC_ID ACC_ID , vPayDocumentControlWaitingItem.ACC_CODE ACC_CODE FROM PAY1010V vPayDocumentControlWaitingItem ) Extent1 WHERE ( Extent1.PAY_MODE_ID = #{cPaymode} )
AND IS_ONLINE IN (
'${cOnline[index]}'
)
AND Extent1.MID = #{cMerchantId}
AND (CASE WHEN(Extent1.REF_DT IS NOT NULL) THEN Extent1.REF_DT ELSE TO_DATE('1900-01-01 00:00:00', 'YYYY-MM-DD HH24:MI:SS') END ) = TO_DATE(#{cRefDt} || ' 00:00:00' , 'DD/MM/YYYY HH24:MI:SS')
AND Extent1.ORI_CC_NO LIKE '%' || #{cCreditCardNo} || '%'
AND Extent1.CC_HOLDER_NAME LIKE '%' || #{cCardHolderName} || '%'
AND Extent1.APPV_NO = #{cApprovalNo}
AND Extent1.CC_TYPE_ID = #{cCreditCardType}
AND Extent1.BANK_ID = #{cIssueBank}
AND PAY_DT >= TO_DATE(#{cPayStartDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') AND PAY_DT < TO_DATE(#{cPayEndDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') +1
AND Extent1.ACC_ID = #{cSetAccount}
AND ( Extent1.BRNCH_ID = #{cBranch} ) ) Project1 GROUP BY Project1.TRX_ID, Project1.PAY_MODE_ID, Project1.IS_ONLINE, Project1.ORI_CC_NO, Project1.CC_TYPE_ID, Project1.CC_HOLDER_NAME, Project1.CC_EXPR, Project1.BANK_ID, Project1.APPV_NO, Project1.REF_NO, Project1.BRNCH_ID, Project1.MID, Project1.ACC_ID, Project1.ACC_CODE, Project1.REF_DT, Project1.PAY_DT ) GroupBy1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = GroupBy1.K2 LEFT JOIN SYS0013M Extent3 ON Extent3.CODE_ID = GroupBy1.K5 LEFT JOIN SYS0004M Extent4 ON Extent4.BANK_ID = GroupBy1.K8 ORDER BY GroupBy1.K1 ASC
