mapper.select:
SELECT Extent1.FILE_BATCH_ID, Extent1.FILE_BATCH_NAME, Extent1.FILE_BATCH_BANK_ID, Extent3.CODE as BANK_CODE, Extent3.NAME as BANK_NAME, NVL(Extent1.FILE_BATCH_TOT_RCORD,0) AS FILE_BATCH_TOT_RCORD, NVL(Extent1.FILE_BATCH_TOT_AMT,0) AS FILE_BATCH_TOT_AMT, NVL(Extent1.FILE_BATCH_REJCT_RCORD,0) AS FILE_BATCH_REJCT_RCORD, NVL(Extent1.FILE_BATCH_APPV_RCORD,0) AS FILE_BATCH_APPV_RCORD, NVL(Extent1.FILE_BATCH_APPV_AMT,0) AS FILE_BATCH_APPV_AMT, Extent2.CODE AS STUS_CODE , Extent2.NAME AS STUS_NAME , NVL(Extent1.FILE_BATCH_STUS_ID,0) AS FILE_BATCH_STUS_ID, Extent1.FILE_BATCH_REM, Extent1.FILE_BATCH_CRT_USER_ID, Extent4.USER_NAME as FILE_BATCH_CRT_USER_NAME, NVL(TO_CHAR(Extent1.FILE_BATCH_CRT_DT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') AS FILE_BATCH_CRT_DT, Extent1.FILE_BATCH_UPD_USER_ID, Extent5.USER_NAME as FILE_BATCH_UPD_USER_NAME, NVL(TO_CHAR(Extent1.FILE_BATCH_UPD_DT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') AS FILE_BATCH_UPD_DT, (CASE WHEN Extent1.FILE_BATCH_APPV_DT IS NOT NULL THEN NVL(TO_CHAR(Extent1.FILE_BATCH_APPV_DT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') ELSE NULL END) AS FILE_BATCH_APPV_DT FROM PAY0245M Extent1 JOIN SYS0038M Extent2 ON Extent2.STUS_CODE_ID = Extent1.FILE_BATCH_STUS_ID LEFT JOIN SYS0004M Extent3 ON Extent3.BANK_ID = Extent1.FILE_BATCH_BANK_ID LEFT JOIN SYS0047M Extent4 ON Extent4.USER_ID = Extent1.FILE_BATCH_CRT_USER_ID LEFT JOIN SYS0047M Extent5 ON Extent5.USER_ID = Extent1.FILE_BATCH_UPD_USER_ID WHERE 1=1
AND Extent1.FILE_BATCH_ID = #{batchId}
AND EXTENT4.USER_NAME =#{creator}
AND Extent1.FILE_BATCH_CRT_DT >= TO_DATE(TO_CHAR(TO_DATE(#{createDt1},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') AND Extent1.FILE_BATCH_CRT_DT < TO_DATE(TO_CHAR(TO_DATE(#{createDt2},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') + 1
AND Extent1.FILE_BATCH_STUS_ID = #{status}
AND Extent1.FILE_BATCH_APPV_DT >= TO_DATE(TO_CHAR(TO_DATE(#{deductDt1},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') AND Extent1.FILE_BATCH_APPV_DT < TO_DATE(TO_CHAR(TO_DATE(#{deductDt2},'DD/MM/YYYY'),'YYYYMMDD') || '000000','yyyymmddhh24miss') +1
AND Extent1.FILE_BATCH_BANK_ID = #{issueBank}
AND Extent1.FILE_BATCH_NAME = #{batchName}
AND Extent1.FILE_BATCH_GROUP = #{IS_GRP}
ORDER BY Extent1.FILE_BATCH_ID

SELECT EXTENT1.FILE_ITM_ID, EXTENT1.FILE_BATCH_ID, EXTENT1.FILE_ITM_STUS_ID, EXTENT3.NAME as FILE_ITM_STUS_NAME, EXTENT4.CODE_NAME, EXTENT2.SALES_ORD_NO, EXTENT1.FILE_ITM_OR_NO, EXTENT1.FILE_ITM_AMT, (CASE WHEN Extent1.FILE_ITM_APPR_DT IS NOT NULL THEN NVL(TO_CHAR(Extent1.FILE_ITM_APPR_DT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') ELSE NULL END) AS FILE_ITM_APPR_DT, EXTENT5.USER_NAME as CRT_USER, NVL(TO_CHAR(Extent1.FILE_ITM_CRT,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') AS FILE_ITM_CRT, EXTENT6.USER_NAME as UPD_USER, (CASE WHEN Extent1.FILE_ITM_UPD IS NOT NULL THEN NVL(TO_CHAR(Extent1.FILE_ITM_UPD,'DD/MM/YYYY hh:mi:ss A.M.'),'01-01-1900 AM 00:00:00') ELSE NULL END) AS FILE_ITM_UPD from SAL0152M EXTENT1 join SAL0001D EXTENT2 on EXTENT2.SALES_ORD_ID = EXTENT1.FILE_ITM_ORD_ID left join SYS0038M EXTENT3 on EXTENT3.STUS_CODE_ID = EXTENT1.FILE_ITM_STUS_ID left join SYS0013M EXTENT4 on EXTENT4.CODE_ID = EXTENT1.FILE_ITM_TYPE_ID left join SYS0047M EXTENT5 on EXTENT5.USER_ID = EXTENT1.FILE_ITM_CRT_USER_ID left join SYS0047M EXTENT6 on EXTENT6.USER_ID =EXTENT1.FILE_ITM_UPD_USER_ID WHERE 1=1
AND Extent1.FILE_BATCH_ID = #{batchId}
AND Extent1.FILE_ITM_STUS_ID = #{status}
ORDER BY Extent1.FILE_ITM_ID

mapper.update:
UPDATE PAY0245M SET
      FILE_BATCH_STUS_ID = 8
    , FILE_BATCH_UPD_DT = SYSDATE
    , FILE_BATCH_UPD_USER_ID = #{userId}
WHERE
    FILE_BATCH_ID = #{fileBatchId}

UPDATE SAL0152M SET
      FILE_ITM_STUS_ID = 8
    , FILE_ITM_UPD = SYSDATE
    , FILE_ITM_UPD_USER_ID = #{userId}
WHERE
    FILE_BATCH_ID = #{fileBatchId}

choose.when:
{
    call SP_INST_ECASH_GENDEDUCTION_NEO( 19, #{new_cardType} ,#{userId},#{eCash, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultECashDeductionMap})
}

{
    call SP_INST_ECASH_GENDEDUCTION_WK( 19, #{new_cardType} ,#{userId},#{eCash, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultECashDeductionMap})
}

choose.otherwise:
{
    call SP_INST_ECASH_GENDEDUCTION( #{new_issueBank}, #{new_cardType} ,#{userId},#{eCash, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultECashDeductionMap})
}

mapper.select:
{
    call SP_INST_ECASH_GEN_GRP_DEDUCT_2( #{new_merchantBank}, #{new_cardType} , #{new_issueBank}, #{newDeductSales},#{userId},#{eCash, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=resultECashGrpDeductionMap})
}
