) UnionAll1 UNION ALL SELECT 1 C1 , Extent9.TAX_INVC_ID TXINVOICEID , Extent9.TAX_INVC_REF_NO BR_NO , Extent10.INVC_ITM_ID TXINVOICEITEMID , CASE WHEN ( 0 <> Extent11.ACC_BILL_CNTRCT_ID ) THEN CASE WHEN ( 0 = Extent10.INVC_ITM_INSTLMT_NO ) THEN 1327 ELSE 1326 END WHEN ( 0 = Extent10.INVC_ITM_INSTLMT_NO ) THEN 1280 ELSE 1279 END TXINVOICEITEMTYPEID , CASE WHEN ( 0 = Extent10.INVC_ITM_INSTLMT_NO ) THEN 'RPF' ELSE 'Rental' END BILLITEMTYPE , Extent10.INVC_ITM_ORD_NO BILLITEMREFNO , Extent10.INVC_ITM_RENTAL_FEE BILLITEMUNITPRICE , CASE WHEN ( Extent11.ACC_BILL_TAX_CODE_ID IS NULL ) THEN Extent12.ACC_BILL_TAX_CODE_ID ELSE Extent11.ACC_BILL_TAX_CODE_ID END BILLITEMTAXCODEID , '-' BILLITEMTAXCODE , Extent10.INVC_ITM_GST_RATE BILLITEMTAXRATE , 1 BILLITEMQTY , Extent10.INVC_ITM_GST_TXS BILLITEMTAXES , Extent10.INVC_ITM_RENTAL_FEE - Extent10.INVC_ITM_GST_TXS BILLITEMCHARGES , Extent10.INVC_ITM_RENTAL_FEE BILLITEMAMOUNT FROM PAY0029D Extent9 LEFT JOIN PAY0030D Extent10 ON Extent9.TAX_INVC_ID = Extent10.TAX_INVC_ID LEFT JOIN PAY0016D Extent11 ON ( Extent10.INVC_ITM_ORD_NO = Extent11.ACC_BILL_ORD_NO ) AND ( Extent9.TAX_INVC_TASK_ID = Extent11.ACC_BILL_TASK_ID ) AND ( Extent9.TAX_INVC_REF_NO = Extent11.ACC_BILL_REM ) AND ( Extent10.INVC_ITM_INSTLMT_NO = Extent11.ACC_BILL_SCHDUL_PRIOD ) LEFT JOIN PAY0016D Extent12 ON ( Extent10.INVC_ITM_ORD_NO = Extent12.ACC_BILL_ORD_NO ) AND ( 0 = Extent12.ACC_BILL_SCHDUL_PRIOD )
WHERE ( Extent9.TAX_INVC_REF_NO = #{refNo} ) AND ( Extent11.ACC_BILL_REM = #{refNo})
AND Extent10.INVC_ITM_ORD_NO = #{invcItmOrdNo}
AND Extent10.INVC_ITM_ID = #{txInvoiceItemId}
) UnionAll2 UNION ALL SELECT 1 C1 , Filter5.TAXINVOICEID2 TXINVOICEID , Filter5.TAX_INVC_REF_NO BR_NO , Filter5.INVC_ITM_ID TXINVOICEITEMID , CASE WHEN ( 1061 = Extent15.ACC_BILL_MODE_ID ) THEN 1281 ELSE 1328 END TXINVOICEITEMTYPEID , 'Handling Fees' BILLITEMTYPE , Filter5.INVC_ITM_ORD_NO BILLITEMREFNO , Filter5.INVC_ITM_HNDL_FEE BILLITEMUNITPRICE , Extent15.ACC_BILL_TAX_CODE_ID BILLITEMTAXCODEID , '-' BILLITEMTAXCODE , Filter5.INVC_ITM_GST_RATE BILLITEMTAXRATE , 1 BILLITEMQTY , Filter5.INVC_ITM_HNDL_FEE_TXS BILLITEMTAXES , Filter5.INVC_ITM_HNDL_FEE - Filter5.INVC_ITM_HNDL_FEE_TXS BILLITEMCHARGES , Filter5.INVC_ITM_HNDL_FEE BILLITEMAMOUNT FROM ( SELECT Extent13.TAX_INVC_ID TAXINVOICEID2 , Extent13.TAX_INVC_REF_NO TAX_INVC_REF_NO , Extent13.TAX_INVC_TASK_ID TAX_INVC_TASK_ID , Extent14.INVC_ITM_ID INVC_ITM_ID , Extent14.INVC_ITM_ORD_NO INVC_ITM_ORD_NO , Extent14.INVC_ITM_GST_RATE INVC_ITM_GST_RATE , Extent14.INVC_ITM_HNDL_FEE INVC_ITM_HNDL_FEE , Extent14.INVC_ITM_HNDL_FEE_TXS INVC_ITM_HNDL_FEE_TXS FROM PAY0029D Extent13 JOIN PAY0030D Extent14 ON Extent13.TAX_INVC_ID = Extent14.TAX_INVC_ID WHERE Extent14.INVC_ITM_HNDL_FEE > UTILS.CONVERT_TO_NUMBER(0,18) ) Filter5 JOIN PAY0016D Extent15 ON ( Filter5.INVC_ITM_ORD_NO = Extent15.ACC_BILL_ORD_NO ) AND ( Filter5.TAX_INVC_TASK_ID = Extent15.ACC_BILL_TASK_ID ) WHERE ( Extent15.ACC_BILL_MODE_ID IN ( 1061,1325 ))
AND ( Filter5.TAX_INVC_REF_NO = #{refNo}) AND ( Extent15.ACC_BILL_REM = #{refNo} )
AND Filter5.INVC_ITM_ORD_NO = #{invcItmOrdNo}
AND Filter5.INVC_ITM_ID = #{txInvoiceItemId}
) UnionAll3
