mapper.select:
SELECT Extent1.ACC_BILL_TASK_ID ACC_BILL_TASK_ID , Extent1.ACC_BILL_REF_NO ACC_BILL_REF_NO , Extent1.ACC_BILL_ORD_ID ACC_BILL_ORD_ID , Extent1.ACC_BILL_MODE_ID ACC_BILL_MODE_ID , Extent1.ACC_BILL_GRP_ID ACC_BILL_GRP_ID , Extent2.CODE_NAME CODE_NAME , Extent4.SALES_ORD_NO SALES_ORD_NO , Extent5.NAME NAME , 0 C1 , TO_NUMBER(Extent1.ACC_BILL_SCHDUL_AMT) ACC_BILL_SCHDUL_AMT , TO_NUMBER(Extent1.ACC_BILL_ADJ_AMT) ACC_BILL_ADJ_AMT , TO_NUMBER(Extent1.ACC_BILL_TXS_AMT) ACC_BILL_TXS_AMT , TO_NUMBER(Extent1.ACC_BILL_NET_AMT) ACC_BILL_NET_AMT , TO_DATE(Extent1.ACC_BILL_REF_DT) ACC_BILL_REF_DT , TO_NUMBER(Extent1.ACC_BILL_SCHDUL_PRIOD) ACC_BILL_SCHDUL_PRIOD FROM PAY0016D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.ACC_BILL_MODE_ID JOIN PAY0047D Extent3 ON Extent3.TASK_ID = Extent1.ACC_BILL_TASK_ID JOIN SAL0001D Extent4 ON Extent4.SALES_ORD_ID = Extent1.ACC_BILL_ORD_ID JOIN SAL0029D Extent5 ON Extent5.CUST_ID = Extent4.CUST_ID WHERE Extent1.ACC_BILL_TASK_ID = #{taskId}
AND Extent4.SALES_ORD_NO = #{orderNo}
AND Extent1.ACC_BILL_REF_NO = #{billNo}
AND Extent5.NAME LIKE '%' || #{custName} || '%'
AND Extent1.ACC_BILL_GRP_ID = #{group}
AND ACC_BILL_MODE_ID = #{billType}
ORDER BY Extent1.ACC_BILL_ORD_ID ASC

SELECT 
    COUNT(1) AS CNT
   FROM PAY0016D Extent1
    JOIN SYS0013M Extent2   ON Extent2.CODE_ID = Extent1.ACC_BILL_MODE_ID
    JOIN PAY0047D Extent3   ON Extent3.TASK_ID = Extent1.ACC_BILL_TASK_ID
    JOIN SAL0001D Extent4   ON Extent4.SALES_ORD_ID = Extent1.ACC_BILL_ORD_ID
    JOIN SAL0029D Extent5   ON Extent5.CUST_ID = Extent4.CUST_ID
 WHERE  Extent1.ACC_BILL_TASK_ID = #{taskId}
  
     AND Extent4.SALES_ORD_NO = #{orderNo}
   
  
      AND Extent1.ACC_BILL_REF_NO = #{billNo}
  
  
      AND Extent5.NAME LIKE '%' || #{custName} || '%'
  
  
      AND Extent1.ACC_BILL_GRP_ID = #{group}
  
  
       AND ACC_BILL_MODE_ID = #{billType}
