/* [com.coway.trust.biz.payment.invoiceApi.impl.InvoiceApiMapper.selectInvoiceList] 20190927 - KR JAEMAJEM */ SELECT #{selectInvoiceType} AS SELECT_INVOICE_TYPE , #{salesDt} AS TAX_INVC_REF_DT , B.CODE_ID AS APP_TYPE_ID , B.CODE_NAME AS APP_TYPE_ID_NAME , C.CUST_ID AS CUST_ID , C.NAME AS CUST_ID_NAME , A.SALES_ORD_NO AS SALES_ORD_NO , TO_CHAR(A.SALES_DT, 'DD/MM/YYYY') AS SALES_DT , E.STK_CODE AS STK_CODE , E.STK_DESC AS STK_DESC , C.EMAIL AS EMAIL FROM SAL0001D A INNER JOIN SYS0013M B ON B.CODE_ID = A.APP_TYPE_ID INNER JOIN SAL0029D C ON C.CUST_ID = A.CUST_ID INNER JOIN SAL0002D D ON D.SALES_ORD_ID = A.SALES_ORD_ID INNER JOIN SYS0026M E ON E.STK_ID = D.ITM_STK_ID WHERE A.STUS_CODE_ID NOT IN (8, 10) --SYS0038M : 8(Inactive), 10(Cancelled) AND EXISTS ( SELECT 1 FROM PAY0016D PAY WHERE PAY.ACC_BILL_ORD_ID = A.SALES_ORD_ID AND TO_CHAR(PAY.ACC_BILL_REF_DT, 'YYYYMM') = #{salesDt} )
AND A.SALES_ORD_NO = #{selectKeyword}
AND UPPER(C.NAME) LIKE UPPER(#{selectKeyword})||'%'

