SELECT /* [com.coway.trust.biz.payment.mobileAppTicket.service.impl.MobileAppTicketMapper.selectMobileAppTicketList] 20191016 - KR HAN */ T1.MOB_TICKET_NO MOB_TICKET_NO, T1.SALES_ORD_NO SALES_ORD_NO, T1.TICKET_TYPE_ID TICKET_TYPE_ID, T2.CODE_NAME TICKET_TYPE_NM, T1.TICKET_STUS_ID TICKET_STUS_ID, T3.NAME TICKET_STUS_NM, T1.CRT_USER_BRNCH CRT_USER_BRNCH, T4.CODE CRT_USER_BRNCH_CD, T4.NAME CRT_USER_BRNCH_NM, TO_CHAR(T1.CRT_DT, 'DD-MM-YYYY HH24:MI:SS') CRT_DT, T1.CRT_USER_ID CRT_USER_ID, TO_CHAR(T1.UPD_DT, 'DD-MM-YYYY HH24:MI:SS') UPD_DT, T1.UPD_USER_ID UPD_USER_ID, T10.USER_NAME UPD_USER_NM FROM MOB0001D T1 LEFT JOIN SYS0013M T2 ON T2.CODE_ID = T1.TICKET_TYPE_ID AND T2.CODE_MASTER_ID = 435 LEFT JOIN SYS0038M T3 ON T3.STUS_CODE_ID = T1.TICKET_STUS_ID LEFT JOIN SYS0047M T10 ON T10.USER_ID = T1.CRT_USER_ID INNER JOIN SYS0005M T4 ON T1.CRT_USER_BRNCH = T4.BRNCH_ID
AND TO_CHAR (T1.CRT_DT, 'YYYY-MM-DD') >= TO_CHAR(TO_DATE (#{_reqstStartDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND TO_CHAR (T1.CRT_DT, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE (#{_reqstEndDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND T1.MOB_TICKET_NO = #{ticketNo}
AND T1.TICKET_TYPE_ID = #{ticketType}
AND T1.TICKET_STUS_ID = #{ticketStatus}
AND T4.CODE = #{branchCode}
AND T10.USER_NAME = #{memberCode}
AND T1.SALES_ORD_NO = #{orderNo}
ORDER BY T1.CRT_DT DESC
