mapper.sql:
FROM PAY0296D A --Mobile Request Fund Transfer INNER JOIN MOB0001D B ON B.MOB_TICKET_NO = A.MOB_TICKET_NO INNER JOIN SYS0038M C ON C.STUS_CODE_ID = B.TICKET_STUS_ID INNER JOIN SYS0013M D ON D.CODE_MASTER_ID = 396 AND D.CODE_ID = A.FT_RESN LEFT OUTER JOIN SYS0070M E ON E.CHENAL_TYPE = 'M' AND E.ATCH_FILE_GRP_ID = A.FT_ATTCH_IMG LEFT OUTER JOIN SYS0071D F ON F.ATCH_FILE_ID = E.ATCH_FILE_ID INNER JOIN SYS0005M G --Branch Info master table . ON G.BRNCH_ID = B.CRT_USER_BRNCH INNER JOIN SYS0047M H ON A.CRT_USER_ID = H.USER_ID INNER JOIN SYS0047M I ON A.UPD_USER_ID = I.USER_ID WHERE TO_CHAR(B.CRT_DT, 'YYYYMMDD') BETWEEN TO_CHAR(TO_DATE(#{fromReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{toReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND A.MOB_TICKET_NO = #{mobTicketNo}
AND A.CUR_ORD_NO = #{curOrdNo}
AND B.TICKET_STUS_ID = #{ticketStusId}
AND G.CODE = #{brnchCode}
AND H.HR_CODE = #{memCode}

mapper.select:
/* [com.coway.trust.biz.payment.mobilePayment.impl.RequestFundTransferMapper.selectOutstandingAmount] 20191202 - KR JAEMAJEM */
{
    call  SP_GET_ORD_OUTSTANDING(#{ftReqId}, #{selectData, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=selectOutstandingAmountMap})
}

/* [com.coway.trust.biz.payment.mobilePayment.impl.RequestFundTransferMapper.callSpInstReqFundTrnsfr] 20191223 - KR JAEMAJEM */
{
    call  SP_INST_REQ_FUND_TRNSFR
    (
            #{ftReqId}
        ,   #{userId}
        ,   #{errcode, mode=OUT, jdbcType=VARCHAR, javaType=String, resultMap=callSpInstReqFundTrnsfrMap}
        ,   #{errmsg, mode=OUT, jdbcType=VARCHAR, javaType=String, resultMap=callSpInstReqFundTrnsfrMap}
    )
}
