mapper.sql:
FROM PAY0294D A --Request to Invoice on mobile_Header INNER JOIN PAY0295D B --Request to Invoice on mobile_Detail ON A.REQ_INVC_NO = B.REQ_INVC_NO INNER JOIN SYS0038M C ON C.STUS_CODE_ID = A.REQ_STUS_ID INNER JOIN SYS0013M D ON D.CODE_MASTER_ID = 442 AND D.CODE = A.INVC_TYPE LEFT OUTER JOIN SYS0070M E ON E.CHENAL_TYPE = 'M' AND E.ATCH_FILE_GRP_ID = B.INVC_ITM_PO_IMG LEFT OUTER JOIN SYS0071D F ON F.ATCH_FILE_ID = E.ATCH_FILE_ID INNER JOIN MOB0001D G ON G.MOB_TICKET_NO = A.MOB_TICKET_NO AND G.SALES_ORD_NO = B.INVC_ITM_ORD_NO INNER JOIN SYS0005M H --Branch Info master table . ON H.BRNCH_ID = G.CRT_USER_BRNCH INNER JOIN SYS0047M I ON A.CRT_USER_ID = I.USER_ID INNER JOIN SYS0047M J ON A.UPD_USER_ID = J.USER_ID WHERE A.REQ_DT BETWEEN TO_CHAR(TO_DATE(#{fromReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{toReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND A.MOB_TICKET_NO = #{mobTicketNo}
AND G.SALES_ORD_NO = #{salesOrdNo}
AND G.TICKET_STUS_ID = #{ticketStusId}
AND H.CODE = #{brnchCode}
AND I.HR_CODE = #{memCode}
AND A.INVC_TYPE = #{invcType}
