mapper.sql:
FROM PAY0298D A --Mobile Request Fund Transfer INNER JOIN MOB0001D B ON B.MOB_TICKET_NO = A.MOB_TICKET_NO INNER JOIN SYS0038M C ON C.STUS_CODE_ID = B.TICKET_STUS_ID INNER JOIN SYS0032M D ON D.RESN_TYPE_ID = 5692 AND D.RESN_ID = A.REF_RESN LEFT OUTER JOIN SYS0070M E ON E.CHENAL_TYPE = 'M' AND E.ATCH_FILE_GRP_ID = A.REF_ATTCH_IMG LEFT OUTER JOIN SYS0071D F ON F.ATCH_FILE_ID = E.ATCH_FILE_ID INNER JOIN SYS0005M G --Branch Info master table . ON G.BRNCH_ID = B.CRT_USER_BRNCH INNER JOIN SYS0047M H ON A.CRT_USER_ID = H.USER_ID INNER JOIN SYS0004M I ON I.STUS_CODE_ID = 1 AND I.BANK_ID = A.ISSU_BANK_ID INNER JOIN SYS0047M J ON A.UPD_USER_ID = J.USER_ID WHERE TO_CHAR(B.CRT_DT, 'YYYYMMDD') BETWEEN TO_CHAR(TO_DATE(#{fromReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{toReqDt}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND A.MOB_TICKET_NO = #{mobTicketNo}
AND A.ORD_NO = #{ordNo}
AND B.TICKET_STUS_ID = #{ticketStusId}
AND G.CODE = #{brnchCode}
AND H.HR_CODE = #{memCode}
