mapper.select:
SELECT /* [com.coway.trust.biz.payment.payment.service.impl.MobileTicketApiMapper.selectMobileTicketList] 20191108 - KR HAN */ t1.MOB_TICKET_NO , LISTAGG(t1.SALES_ORD_NO,',') WITHIN GROUP(ORDER BY t1.SALES_ORD_NO) AS SALES_ORD_NO , t1.CUST_NAME , t1.TICKET_STUS_NAME , t1.CRT_DT , t1.TICKET_TYPE_ID , t1.TICKET_TYPE_NAME , t1.CANCEL_YN FROM ( SELECT A.MOB_TICKET_NO MOB_TICKET_NO , A.SALES_ORD_NO SALES_ORD_NO , C.NAME CUST_NAME , Y.NAME TICKET_STUS_NAME , TO_CHAR(A.CRT_DT , 'DD/MM/YYYY') CRT_DT , A.TICKET_TYPE_ID TICKET_TYPE_ID , Z.CODE_NAME TICKET_TYPE_NAME , CASE WHEN (A.TICKET_TYPE_ID = '5674' OR A.TICKET_TYPE_ID = '5673') AND A.TICKET_STUS_ID = 1 THEN 'Y' ELSE 'N' END CANCEL_YN FROM MOB0001D A JOIN SAL0001D B ON A.SALES_ORD_NO = B.SALES_ORD_NO JOIN SAL0029D C ON B.CUST_ID = C.CUST_ID JOIN SYS0038M Y ON A.TICKET_STUS_ID = Y.STUS_CODE_ID JOIN SYS0013M Z ON A.TICKET_TYPE_ID = Z.CODE_ID AND Z.CODE_MASTER_ID = 435
AND A.CRT_USER_ID = #{userId}
AND TO_CHAR (A.CRT_DT, 'YYYY-MM-DD') >= TO_CHAR(TO_DATE (#{fromDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND TO_CHAR (A.CRT_DT, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE (#{toDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND A.TICKET_TYPE_ID = #{ticketType}
) t1 GROUP BY t1.MOB_TICKET_NO, t1.CUST_NAME, t1.TICKET_STUS_NAME, t1.CRT_DT, t1.TICKET_TYPE_ID, t1.TICKET_TYPE_NAME, t1.CANCEL_YN ORDER BY CAST(t1.MOB_TICKET_NO AS INT) DESC

